// 'One small step' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/tt3yRH
//
// A recreation of the first footprint on the Moon, made by Neil Armstrong in 1969.
// Amazing to think it's still there.
//
// Code coming in at under 2.4Kb.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane
// and a bunch of others for sharing their knowledge!

#define R	iResolution
#define N	normalize

float n31(vec3 p) {
	vec3 s = vec3(7, 157, 113), ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, 270) + dot(ip, s);
	h = mix(fract(sin(h) * 43.5453), fract(sin(h + s.x) * 43.5453), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float map(vec3 p) {
	float r, k, t, h,
	      bmp = (n31(p) + n31(p * 2.12) * .5 + n31(p * 4.42) * .25 + n31(p * 8.54) * .125 + n31(p * 16.32) * .062 + n31(p * 32.98) * .031 + n31(p * 63.52) * .0156) * .5 * (.5 + 2. * exp(-pow(length(p.xz - vec2(.5, 2.2)), 2.) * .26)),
	      a = p.y - .27 - bmp,
	      b = (bmp * bmp * .5 - .5) * .12;
	p.xy = -p.xy;
	p.x /= .95 - cos((p.z + 1.2 - sign(p.x)) * .8) * .1;
	vec3 tp = p;
	tp.z = mod(tp.z - .5, .4) - .2;
	t = max(box(tp, vec3(2, .16, .12 + tp.y * .25)), box(p - vec3(0, 0, 1.1), vec3(2, .16, 1.7)));
	tp = p;
	tp.x = abs(p.x) - 1.65;
	tp.z -= 1.1;
	t = min(t, box(tp, vec3(.53 - .12 * tp.z, .16, 1.6)));
	p.z /= cos(p.z * .1);
	vec2 q = p.xz;
	q.x = abs(q.x);
	k = q.x * .12 + q.y;
	if (k < 0.) r = length(q) - 1.2;
	else if (k > 2.48) r = length(q - vec2(0, 2.5)) - 1.5;
	else r = dot(q, vec2(.99, -.12)) - 1.2;

	b -= max(max(r, p.y), -t);
	h = clamp(.5 + .5 * (b - a) / -.8, 0., 1.);
	return mix(b, a, h) + .8 * h * (1. - h);
}

vec3 NM(vec3 p, float t) {
	vec3 n = vec3(0), e;
	for (int i = min(iFrame, 0); i < 4; i++) {
		e = .5773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + .005 * t * e);
	}

	return N(n);
}

float ao(vec3 p, vec3 n, float h) { return map(p + h * n) / h; }

vec3 lights(vec3 p, vec3 rd, float d) {
	vec3 ld = N(vec3(6, 3, -10) - p),
	     n = NM(p, d) + n31(p * 79.0625) * .25 - .25;
	float ao = .1 + .9 * dot(vec3(ao(p, n, .1), ao(p, n, .4), ao(p, n, 2.)), vec3(.2, .3, .5)),
	      l1 = max(0., .1 + .9 * dot(ld, n)),
	      l2 = max(0., .1 + .9 * dot(ld * vec3(-1, 0, -1), n)) * .2,
	      spe = max(0., dot(rd, reflect(ld, n))) * .1,
	      fre = smoothstep(.7, 1., 1. + dot(rd, n)),
	      s = 1.,
	      t = .1;
	for (float i = 0.; i < 30.; i++) {
		float h = map(p + ld * t);
		s = min(s, 15. * h / t);
		t += h;
		if (s < .001) break;
	}

	l1 *= .1 + .9 * clamp(s, 0., 1.);
	return mix(.3, .4, fre) * ((l1 + l2) * ao + spe) * vec3(2, 1.8, 1.7);
}

float d = 0.;
vec3 march(vec3 ro, vec3 rd) {
	vec3 p;
	d = .01;
	for (float i = 0.; i < 96.; i++) {
		p = ro + rd * d;
		float h = map(p);
		if (abs(h) < .0015) break;
		d += h;
	}

	return lights(p, rd, d) * exp(-d * .14);
}

void mainImage(out vec4 fragColor, vec2 fc) {
	float t = mod(iTime * .2, 30.);
	vec2 q = fc / R.xy,
	     uv = (fc - .5 * R.xy) / R.y;
	vec3 c, f, r,
	     ro = vec3(0, .2, -4);
	ro.yz *= rot(-sin(t * .3) * .1 - .6);
	ro.xz *= rot(1.1 + cos(t) * .2);
	f = N(vec3(0, 0, .8) - ro);
	r = N(cross(vec3(0, 1, 0), f));
	c = pow(march(ro, N(f + r * uv.x + cross(f, r) * uv.y)), vec3(.45));
	c *= .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	fragColor = vec4(c, mix(1.2, 0., (d + 1.) / 8.));
}

// Cheap Rotation by las:
// http://www.pouet.net/topic.php?which=7931&page=1
#define R(p, a) p=cos(a)*p+sin(a)*vec2(p.y,-p.x)
vec3 rot(vec3 p,vec3 r){
    R(p.xz, r.y);
    R(p.yx, r.z);
    R(p.zy, r.x);
    return p;
}

//https://www.shadertoy.com/view/ldl3zn
vec3 WarpDomain( const in vec3 vPos )
{
    vec3 vResult = vPos;

    float fUnbend = clamp(atan(vResult.x, vResult.z), 0.0, PI * sin(iTime));
    R(vResult.zx, fUnbend);

    float fDroopBlend = max(-vResult.y, 0.0);
    vResult.y += fDroopBlend * 0.4;
    vResult.z += sin(vResult.x * 1.0  + vResult.y * 2.0 + iTime) * fDroopBlend * 0.05;
    return vResult;
}


vec3 ro = vec3(0), rd = vec3(0);
vec3 col = vec3(0);
float curve(float x){
    return x*x*(3.0-2.0*x);
}
vec4 sdScene(vec3 p)
{
    float d = MAX_DIST;
    vec4 res = vec4(MAX_DIST, MAT_VOID);

	vec3 cp001 = p;
	cp001.xyz += vec3(0., -1., 0.);

    // Bending...
    #if 1
	{
        vec3 q = cp001;
        float r = s2u(sin(iTime))*PI*.5; // rotation radian
        float range = .5+s2u(sin(iTime*.5)); // range of bending

        float sgn = sign(q.x);
        float smoothRange = sms(-sms(0., 1., p.z-.2), range, q.x*sgn) * sgn *.5;

        R(q.xz, r*smoothRange);
        cp001 = q;
    }
    #else
    {
        cp001 = WarpDomain(cp001);
    }
    #endif


	d = sdCapsule(rot(cp001+vec3(0.3713, 0., 0.), vec3(0., 0., 1.5708)), 0.5900, 3.4100);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(0.3713, 0., 0.), vec3(0., 0.6647, 0.)), vec3(0.5295, 1., 1.)), d, 0.1900);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(-0.7288, 0., 0.), vec3(1.0374, 0.6176, 0.4935)), vec3(0.6807, 0.9020, 0.9020)), d, 0.1570);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(1.3413, 0., 0.), vec3(0.8924, 0.5617, 0.2927)), vec3(0.6807, 0.9020, 0.9020)), d, 0.1900);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(2.2848, 0., 0.), vec3(1.0186, 0.6089, 0.4699)), vec3(0.4136, 0.7825, 0.7825)), d, 0.1900);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(-1.7662, 0., 0.), vec3(3.4214, 2.4668, 0.6159)), vec3(0.4972, 1.1038, 1.1038)), d, 0.1390);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(3.1596, 0., 0.), vec3(0.5278, 0.5633, -0.3009)), vec3(0.6807, 1.0448, 1.0448)), d, 0.1900);
	d = fOpUnionSmooth(sdEllipsoid(rot(cp001+vec3(-2.5247, 0., 0.), vec3(0.6248, 0.5416, -0.1472)), vec3(0.6807, 0.9020, 0.9020)), d, 0.1900);
	res = v4OpUnion(vec4(d, MAT_ERNST0_001), res);

	d = sdPlane(p);
	res = v4OpUnion(vec4(d, MAT_ERNST0), res);

	d = sdCappedCylinder(rot(cp001+vec3(-3.3399, 0., 0.), vec3(0., 0., 1.5708)), vec2(0.4595, 0.4595))-0.0100;
	res = v4OpUnionSmooth(vec4(d, MAT_ERNST0_001), res, 0.0280);

	d = sdEllipsoid(cp001+vec3(-4.5001, 0., 0.), vec3(0.6139, 0.6139, 0.6139));
	res = v4OpUnion(vec4(d, MAT_ERNST0_002), res);

    return res;
}

vec4 intersect()
{
    float d = 1.;
    vec3  m = MAT_VOID;

    for (int i = 0; i < ITERATION; i++)
    {
        vec3 p = ro + d * rd;
        vec4 res = sdScene(p);
        m = res.yzw;
        res.x *= .5;
        if (abs(res.x) < MIN_DIST || res.x >= MAX_DIST) break;
        d += res.x;
        if (d >= MAX_DIST) break;
    }

    return vec4(d,m);
}

vec3 normal(vec3 p)
{
    float c=sdScene(p).x;
    float e=MIN_DIST*.1;
    return normalize(vec3(
        sdScene(p+vec3(e,0.,0.)).x-c,
        sdScene(p+vec3(0.,e,0.)).x-c,
        sdScene(p+vec3(0.,0.,e)).x-c)
    );
}

float shadow(vec3 o, vec3 n)
{
    float mint=L0_str;
    float maxt=L0_end;
    float k = L0_sft;
    float res = 1.;
    float t=mint;
    float ph = 1e10;
    for( int i=0; i < ITERATION; i++)
    {
        float h = sdScene(o + L0_dir*t).x;
#if 1
        res = min( res, k*h/t);
#else
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
#endif
        t += h;

        if( res<0.0001 || t>maxt ) break;
    }
    return clamp( res, 0.0, 1.0 );
}


vec3 randomSphereDir(vec2 rnd)
{
    float s = rnd.x*PI*2.;
    float t = rnd.y*2.-1.;
    return vec3(sin(s), cos(s), t) / sqrt(1.0 + t * t);
}
vec3 randomHemisphereDir(vec3 dir, float i)
{
    vec3 v = randomSphereDir( vec2(hash11(i+1.), hash11(i+2.)) );
    return v * sign(dot(v, dir));
}

// "Hemispherical SDF AO" by XT95:
// https://www.shadertoy.com/view/4sdGWN
float ambientOcclusion( in vec3 p, in vec3 n, in float maxDist, in float falloff )
{
    const int nbIte = 12;
    const float nbIteInv = 1./float(nbIte);
    const float rad = 1.-1.*nbIteInv;

    float ao = 0.0;

    for( int i=0; i<nbIte; i++ )
    {
        float l = hash11(float(i))*maxDist;
        vec3 aord = normalize(n+randomHemisphereDir(n, l )*rad)*l;

        ao += (l - max(sdScene( p + aord ).x,0.)) / maxDist * falloff;
    }

    return clamp( 1.-ao*nbIteInv, 0., 1.);
}


float specular(vec3 p, vec3 n, vec3 ld)
{
    float power = 30.;
    vec3 to_eye = normalize(p - ro);
    vec3 reflect_light = normalize(reflect(ld, n));
    return pow(max(dot(to_eye, reflect_light), 0.), power);
}

void render()
{
    vec4 hit = intersect();
    vec3 p = ro + hit.x * rd;
    vec3 base_col = hit.yzw;

    if (hit.x>=MAX_DIST)
    {
        col=FOG_COL;
    }
    else
    {
        vec3 n = normal(p);
        vec3 offset = n * .00001;
        float light1 = saturate(dot(n, L0_dir));
        float light2 = saturate(dot(n, L1_dir));
        float shadow = shadow(p+offset, n);

        float ao=0.;
        ao = ambientOcclusion(p, n, .1, .5);
        ao += ambientOcclusion(p, n, .5, .5);
        ao += ambientOcclusion(p, n, 2., 2.);
        ao += ambientOcclusion(p, n, 4., 2.);
        ao = smoothstep(0., 4., ao);

        float shade = 0.;
        shade = light1;

        vec3 shadeLight1 = vec3(L0_col*light1);
        vec3 shadeLight2 = vec3(L1_col*light2);

        col = shadeLight1;
        col *= shadow;
        col+= shadeLight2*ao;
        col+= AMB_COL*ao*AMB_STRENGTH;
        col*= base_col;

        col = mix(col, col+L0_col, specular(p+offset, n, L0_dir)*shadow*1.);
        col = mix(col, FOG_COL, saturate(pow(distance(ro,p)/MAX_DIST+FOG_START, FOG_POW)));
    }
}

vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

void camera(vec2 uv)
{
    perspectiveCam(uv, ro, rd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = (uv*2.-1.)*iResolution.y/iResolution.x;
    uv.x *= iResolution.x / iResolution.y;

    init();
    camera(uv);
    render();
    col = ACESFilm(col);
    col = pow(col, vec3(.9));

    fragColor = vec4(col, 1);
}

