// ACES from knarkowitz
// cheap noise from nimitz

vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}

#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
float valueNoise(in vec3 p,float pw)
{
    vec3 ip = floor(p);
    vec3 fp = fract(p);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec2 rz = textureLod( iChannel0, (tap+0.5)/256.0, 0.0 ).yx;
	return mix( rz.x, rz.y, fp.z );
}

float T;
float fa,fb,fc,fd;
float fbma(vec3 p){

    vec3 op = p;
    float n = 0.;
    p *= 0.5;
    p.y -= T*0.5;
    fa = valueNoise(p,1.); 

    p.y -= fa*(0.2 + sin(op.z + T*0.2)*0.5);
    p.x += T*.325;
    p.y -= T*0.25;

    fb = valueNoise(p*2.,1.);


    p.x += fb*.2 + T*0.04;
    p.z += fb*.4 + T*0.02;
    fd = valueNoise(p*9.8,1.);

    fc = valueNoise(p*5.2,1.);



    n = fa*1. + fb*.55 + fc*0.244 + fd*0.; /*fa*/;// + valueNoise(op*5.8,1.)*0.0;

    //n*= 1.1;

    //n = smoothstep(0.,1.,n);
    //n = pow(n,1. + fa*2.);



    n*= 0.4 + pow(valueNoise(p*0.7 - vec3(0.3,T*0.15,0),7.),3.)*1.*smoothstep(0.,1.,(op.y + 9.)*0.2) ;

    //n = smoothstep(0.,1.,n);
    //n = pow(n,1. + fa*3.5);

    //n *= pow(valueNoise(op*0.3 + 1.,1.),0.4)*1.;
    n *= 1.;

    return n;
}

float fbmb(vec3 p){

    vec3 op = p;
    float n = 0.;
    p *= 0.5;
    p.xz *= rot(length(p.xz)*0.2 + T*0.2);
    //p.y -= T*0.5;
    fa = valueNoise(p,1.); 

    p.y -= fa*(1. + sin(op.z + T*0.2)*0.5);
    //p.x += T*.25;
    p.y -= T*0.125;

    fb = valueNoise(p*2.,1.);



    n = fa*1. + fb*.55 /*fa*/;// + valueNoise(op*5.8,1.)*0.0;


    n*= 0.4;
    return n;
}


float getParticles(vec3 p){
    p.xz *= rot(T*.23);
    p.y -= T;
    vec4 partP = vec4(p,1.);


    #define pmod(p,a) mod(p - 0.5*a,a) - 0.5*a
    partP.wz *= rot(0.4);
    partP = abs(partP);
    partP.xz *= rot(2.4);
    partP = abs(partP);
    partP.yw *= rot(1.4);
    partP = abs(partP);

    vec4 partPa = pmod(partP, 0.6);

    vec4 partPb = pmod(partP + 1., 0.4);
    vec4 partPc = pmod(partP + 4.24, 0.9);


    float d = smoothstep(0.04,0.,length(partPa) - 0.001)*2.;

    d += smoothstep(0.02,0.,length(partPb) - 0.003)*4.;
    d += smoothstep(0.02,0.,length(partPc) - 0.033)*4.;
    return d;
}
float particles = 0.;
float map( vec3 p ){

    vec3 op = p;

    p.xz = p.xz * rot((p.y*(0.5))*2.);

    float fl = smoothstep(0.,1.,op.y);

    float noiseA = fbma(p*3.);

    float d = smoothstep(1.,0.,length(p.xz) - 0. - fl*0.4 - noiseA*(1. + fl*1.) );


    float noiseB = fbmb(op*3.);
    //d = mix(d,1., smoothstep(0.,1.,p.y - 0.4 + fbm(op*3.)));
    d += smoothstep(0.,1.,p.y - 1.2 + noiseB*1.4);

    p = op;

    p.xz *= rot(T*0.3 + p.y*0.2);

    p.y -= T;

    d += smoothstep(0.,1.,valueNoise(p*1.,1.)*0.144)*smoothstep(0.,1.,op.z + 2.6 );

    d += particles = getParticles(p);
    //return smoothstep(1.,0.4,length(p)-0.2 - d);
    return d*3.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec2 ouv = uv;
    vec3 col = vec3(0);

    float dith = texture(iChannel0,fragCoord/8.).x;
    T = iTime*4. - dith/60.;iFrameRate;

    uv.x += sin(iTime*0.4 + 0.2)*0.3;    
    vec3 ro = vec3(0);

    ro.z -= 5.;

    ro.xz *= rot(sin(T*0.2)*0.1);
    ro.yz *= rot(sin(T*0.25 )*0.1);

    vec3 rd = normalize(vec3(uv,1));

    rd.yz *= rot(sin(T*0.25 )*0.1);
    ro += rd*dith*0.02;
    vec3 p = ro;

    float t = 0.;

    float stSz = 0.05;

    float steps = 110.;

    vec3 accum = vec3(0);
    float totalD = 0.;

    vec3 lDir = normalize(vec3(5.2,0.2,1.));
    vec3 lDirB = normalize(-vec3(2.2,0.2,1.));

    vec3 lCol = vec3(0.9,.48,0.3)*3.;


    // --  sun and bg
    #define pal(a,b,c,d,e) (a + (b)*sin((c)*(d) + (e)))

    float sunW = 0.95;


    float sun = dot(rd,lDir);

    vec3 bg = pal(0.5,vec3(0.7,0.8,0.9)*0. + 0.5,vec3(3,1.4,0.2),.5,4. + sun*2. + 7.4);
    col = bg *0.02;

    col += sin(uv.xyx + T)*0.004;

    // --  marching
    for(float i = 0.; i < steps; i++){

        float d = map(p);

        float difffact = clamp( (d - map(p + lDir*.1))*1.6,0.,1.);
        float difffactb = clamp( (d - map(p - normalize(p)*0.03))*1.,0.,1.);
        float difffactc = clamp( (d - map(p + lDirB*.1))*0.3,0.,1.);

        vec3 diff = mix(vec3(0.3,0.2,0.3)*0.072,vec3(0.5,0.1,0.1) + col*0.5 + lCol*0.5 + vec3(0.3,0.2,0.1)*pow(fb,5.)*13. ,difffact);
        diff = mix(diff,vec3(6.5,0.6,0.4) + col*0.5 + lCol*0.5 ,difffactb);
        diff = mix(diff,vec3(1.5,0.6,0.3)*0.4 + col*0. + vec3(0.4,0.3,0.7)*0.4 ,difffactc);

        //vec3 absorption = mix(vec3(0.2,0.4,0.2),vec3(0.09,0.11,0.09)*4.4, totalD);
        vec3 absorption = mix(vec3(0.2,0.4,0.2)*0.,vec3(0.2,0.4,0.2), totalD);

        vec3 fringe = clamp( 1. - d*2.,0.,1.)*lCol*0.1;

        vec3 oCol = 1.*diff*(mix(absorption,vec3(1.),0.2)) + fringe*(1. + diff*7.);


        d *= stSz;
        accum += d*oCol*(1.-totalD);




        if(totalD > 1.){
            break;
        }
        totalD += d*(1.-totalD);



        p += rd*stSz;
    }


    // -- post

    //col += accum;
    col = mix(col,accum,totalD);

    col += texture(iChannel2,fragCoord.xy/iResolution.xy).xyz*0.6;


    col *= vec3(1.06,0.94,0.92);
    //col = mix(col,smoothstep(0.,1.,col*1.7),0.6);
    col *= 1. - dot(ouv,ouv*0.2)*4.;
    col = mix(col, acesFilm(col*1.8),0.5);

    col = pow(max(col,0.),vec3(0.454545));

    fragColor = vec4(col,1.0);
}

