          vec3 flare = 0.01*(sunCol)*smoothstep(0.02,0.,dfl);
          flare += 0.003*(sunCol*sunCol)*smoothstep(0.04,0.,abs(dfl - dFdx(uv.x)));
          C += flare.xyzz*abs(sin(i*10.));
      }
  } else {

      C = texture(iChannel0,U/iResolution.xy);
  }


}

