// Symbol of polyhedron to render.
vec4 wythoff_symbol = WS_IKE;

//////////////////////////////////////////////////////////////////////

float demo_mode = 2.0;
float color_by_face = 2.0;
float show_triangles = 0.1;
float sphericity = 0.1;

// Demo also scales up/down object
float scale = 2.0;

//////////////////////////////////////////////////////////////////////

const float KEY_C = 77.5/356.0;
const float KEY_D = 78.5/356.0;
const float KEY_R = 92.5/356.0; 
const float KEY_S = 93.5/356.0;
const float KEY_T = 94.5/356.0;

const float KEY_1 = 59.5/356.0;
const float KEY_2 = 60.5/356.0;
