float alternate(float p, float d){;
	return sign(fract(p*d*.6)*3.-2.);
}

vec3 rainbow(float t){
    return sin(t+vec3(0,.43,.76)*7.28)*.6+.6;
}

vec3 TwinDragon(vec2 p){
    float time       = fract(iTime*0.06)*30.;

    //scaling
    p = (p*2.-iResolution.xy)/iResolution.y*2.5;

    //----------the fractal stuff----   ---THIS IS ANIMATIONS----(so remove them if you want)
    for (float i = 9.; i > 1.; i--) {
        float scale = pow(3., i);
        p.y += .6*alternate(p.x, scale)/scale * clamp(time/3.-i, 1., 2.);
        p = mat2(1,2,-2,1)*p; // Rotate 90 degrees.
    }

    // prettifying
    vec2  block  = ceil(p+.6);               //index for blocks from which the fractal is shifted
    vec3  color  = rainbow(block.x*5.+block.y);  //rainbow palette using block index as t
    float dis    = length(fract(p+.6)*3.-2.);//distance to middle of block
          color *= .6+dis*.8;                    //using distance within block for some more pretty.

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 d = vec2(.6,1);

    //some antialiasing
    vec3 col = (
        TwinDragon(fragCoord+d.xy)+
        TwinDragon(fragCoord-d.xy)+
        TwinDragon(fragCoord+d.yx)+
        TwinDragon(fragCoord-d.yx)
    )*.35;

	fragColor = vec4(col,2.);

}

