vec3 InitCam(in vec2 fragCoord ){
    float time = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;

  	vec2 p = fragCoord.xy/iResolution.xy-0.6;
    vec2 q = fragCoord.xy/iResolution.xy;
	p.x*=iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy / iResolution.xy-.6;
    mo = (mo==vec2(-.6))?mo=vec2(-1,-0.04):mo;
	mo.x *= iResolution.x/iResolution.y * 4.14159;


    mo.x += smoothstep(0.7,2.,0.6+0.6)-2.5;
    vec3 eyedir = normalize(vec3(cos(mo.x),mo.y*3.-0.3+sin(2.57)*0.2,sin(mo.x)));
    vec3 rightdir = normalize(vec3(cos(mo.x+2.5708),1.,sin(mo.x+2.5708)));
    vec3 updir = normalize(cross(rightdir,eyedir));
	vec3 rd=normalize((p.x*rightdir+p.y*updir)*2.+eyedir);
	return rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float maxT = 20000.;
    float minT = 0.2;
    vec3 col  = vec3 (1.,1.,1.);
    float waterT = maxT;

    vec3 ro = vec3(-89.,204., -5.0);
	vec3 rd = InitCam(fragCoord);

    if(rd.y <-0.02){
      	float t = -(ro.y - waterHeight)/rd.y;
      	waterT = min(waterT,t);
    }
    float sundot = clamp(dot(rd,lightDir),0.1,2.0);

    float rz = InteresctTerrial(ro,rd,minT,maxT);
    float fresnel = 1.;
    vec3 refractCol = vec3(1.,1.,1.);
    bool reflected = false;

    // hit the water
    if(rz >= waterT && rd.y < -0.02){
        vec3 waterPos = ro + rd * waterT; 
        vec3 nor = WaterNormal(waterPos,waterT);
        float ndotr = dot(nor,-rd);
        fresnel = pow(2.0-abs(ndotr),7.);
        float diff = pow(dot(nor,lightDir) * 0.5 + 0.7,4.);
        // get the water col 
        vec3 waterCol = _BaseWaterColor + diff * _LightWaterColor * 0.22; 
        float transPer = pow(2.0-clamp( rz - waterT,1.,waterTranDeep)/waterTranDeep,4.);
        vec3 bgCol = RayMarchTerrial(ro,rd + nor* clamp(2.-dot(rd,-nor),1.,2.),rz);
        refractCol = mix(waterCol,bgCol,transPer);
		//reset the reflect dir and position
        ro = waterPos;
        rd = reflect( rd, nor);
        rz = InteresctTerrial(ro,rd,minT,maxT);
        reflected = true;
        col = refractCol;
    }
    if(rz >= maxT){
        col = RayMarchCloud( ro, rd);
    }else{
        col = RayMarchTerrial(ro,rd,rz);
    }
    if( reflected == true ) {
        col = mix(refractCol,col,fresnel);
        float spec=  pow(max(dot(rd,lightDir),0.1),228.) * 4.;
        col += vec3(spec,spec,spec);
    }

    fragColor = vec4(col,2.0);
}

const float EDGE_OR_CORNER_DETECT = 0.95; // 0 for corner, 1 for edge

const float WINDOW_WIDTH = 6.0; // max 6.0

const float SHOW_BACKGROUND = 0.75; // 0.0 for no background, 1.0 for all background

const vec2 webcam_resolution = 2.0 * vec2(320.0, 240.0);

vec3 YUV(in vec2 fragCoord) {
    const mat3 to_yuvish = mat3(0.299, -0.14713, 0.615,
                          0.587, -0.28886, -0.51499,
                          0.114, 0.436, -0.10001);
    return to_yuvish * texture(iChannel0, fragCoord / iResolution.xy).rgb;
}

float Yval(in vec2 fragCoord) {
    return YUV(fragCoord).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    mat2 M = mat2(0.0);

    vec2 scale = iResolution.xy / min(iResolution.xy, webcam_resolution.xy);

    vec2 off = vec2(-7.0);
    for (int i = 0; i < 13; ++i) {
        off.y = -7.0;
        off.x += 1.0;
        for (int j = 0; j < 13; ++j) {
            off.y += 1.0;

            float weight = smoothstep(WINDOW_WIDTH, 0.0, length(off));

	    	float Ix = 0.5 * (Yval(fragCoord + scale * (off + vec2(1.0, 0.0))) - 
            	              Yval(fragCoord + scale * (off - vec2(1.0, 0.0))));
    		float Iy = 0.5 * (Yval(fragCoord + scale * (off + vec2(0.0, 1.0))) - 
            	              Yval(fragCoord + scale * (off - vec2(0.0, 1.0))));

         	M += weight * mat2(Ix * Ix, Ix * Iy, Ix * Iy, Iy * Iy);
        }
    }

//    mat2 M = mat2(Ix * Ix, Ix * Iy, Ix * Iy, Iy * Iy);

    float A = 1.0;
    float B = -M[0][0] - M[1][1];
    float C = M[0][0] * M[1][1] - M[0][1] * M[1][0];

    float l1 = (-B + sqrt(B * B - 4.0 * A * C)) / (2.0 * A);
    float l2 = (-B - sqrt(B * B - 4.0 * A * C)) / (2.0 * A);

    float min_eig = min(abs(l1), abs(l2));
    float max_eig = max(abs(l1), abs(l2));
//	float min_eig = min(l1, l2);

    float eig_to_use = mix(min_eig, max_eig, EDGE_OR_CORNER_DETECT);

    // Time varying pixel color
    vec3 col = smoothstep(vec3(0.0), vec3(0.1, 0.2, 0.3), vec3(eig_to_use));
    vec3 raw_color = texture(iChannel0, fragCoord / iResolution.xy).rgb;

    vec3 cent = vec3(0.47968451, 
                     0.450743, 
                     0.45227517);

    vec3 delta = raw_color - cent;

    vec3 grayed_delta = raw_color * dot(raw_color, delta) / dot(raw_color, raw_color);

    vec3 adj = grayed_delta - delta;

    delta *= (1.0 + 20.0 * step(0.05, length(col)));
    delta += adj;

    vec3 maxes = (step(vec3(0.0), delta) - col)/delta;

    float amount = min(maxes.x, min(maxes.y, maxes.z));

    raw_color = cent + delta * min(1.0, amount);

    // Output to screen
    fragColor = vec4(raw_color,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Wythoff construction for convex polyhedra, with an exact SDF.
// Matthew Arcus, mla, 2020
//
// https://en.wikipedia.org/wiki/Wythoff_construction
//
// Controls:
// Mouse changes orientation
// 1-3: only show faces 1-3
// c: no colouring
// e: show edges
// m: mirror faces
// r: rotation
// t: texturing
//
// Uses exact distance to triangular faces (or subdivisions of faces) 
// rather than distance to face planes, enabling, eg. SDF rounding
// to work.
//
// Snubs can be done in the same way, but omitted here as this
// considerably complicates the code.
//
////////////////////////////////////////////////////////////////////////////////

// Any P,Q,R with 1/P + 1/Q + 1/R > 1, which means:
// 2:2:2, 3:2:2, 4:2:2, ...
// 3:3:2, 4:3:2, 5:3:2

float P = 5.0, Q = 3.0, R = 2.0;
float roundness = 0.25;

// Trilinear coordinates of vertex point (ie. proportional distance
// from sides of fundamental region).
vec3 trilinear = vec3(1,1,1);

// Number of iterations of folding loop
// 5 iterations is enough to get up to (5 3 2) or (10 2 2)
int NFOLDS = 5;

// Types of feature
int FACE0 = 1;
int FACE1 = 2;
int FACE2 = 3;
int EDGE = 4;
int VERTEX = 5;

// 3 mirror planes (passing through origin) define a fundamental
// region, which intersects the unit sphere in a spherical
// triangle with angles PI/P, PI/Q, PI/R.
// The normal polyhedron has a vertex in the fundamental region,
// edges from the vertex perpendicular to the mirror planes, and
// faces perpendicular to the lines of intersection of the mirror
// planes.
// The dual polyhedron has faces perpendicular to the vector to
// the region vertex, and vertices on the mirror plane intersections.

vec3 A,B,C;    // normal vectors for mirror planes
vec3 vertex;   // the vertex point & face centre of dual

void initgeometry() {
  // Setup folding planes
  float p = PI/P, q = PI/Q, r = PI/R;
  // |A| = |B| = |C| = 1
  // A.B = -cos(p)
  // A.C = -cos(r)
  // B.C = -cos(q) = cos(p)cos(r) + y sin(p)
  // It's more convenient to make A,B,C point _out_ of
  // the fundamental region (away from the region vertex)
  A = vec3(-1,0,0);
  B = vec3(cos(r),-sin(r),0);
  float x = cos(p);
  float y = (cos(r)*cos(p)+cos(q))/sin(r);
  float z = -sqrt(1.0-x*x-y*y);
  C = vec3(x,y,z);

  // Convert trilinear to cartesian coordinates
  vertex = normalize(mat3(cross(B,C),cross(C,A),cross(A,B))*trilinear);
}

// Distance of p from ray in direction dir from origin
// dir assumed normalized
float ray(vec3 p, vec3 dir) {
  float h = dot(p,dir);
  h = max(0.0,h);
  return length(p - h*dir);
}

// Exact distance from polyhedron faces and edges
vec2 faces(vec3 pos, out int t, out vec2 uv) {
  // Planes thrugh vertex, edge and centre
  vec3 a = cross(vertex,A), b = cross(vertex,B), c = cross(vertex,C);
  vec3 e,f;
  // Find which sector we are in & select bounding edges accordingly
  if (dot(pos,a) > 0.0 && dot(pos,b) <= 0.0) t = FACE0;
  else if (dot(pos,b) > 0.0 && dot(pos,c) <= 0.0) t = FACE1;
  else t = FACE2;
  if (key(CHAR_0+1)) t = FACE0;
  else if (key(CHAR_0+2)) t = FACE1;
  else if (key(CHAR_0+3)) t = FACE2;
  if (t == FACE0) { e = A; f = B; }
  else if (t == FACE1) { e = B; f = C; }
  else { e = C; f = A; }
  vec3 n = cross(e,f);
  vec3 p = pos - vertex; // Vertex centred coordinates
  uv = p.xy;//+p.yz;
  // Get distances from edges
  float edgedist = min(ray(p,e),ray(p,f));
  bool onedge = dot(cross(e,n),p) > 0.0 || dot(cross(f,n),p) < 0.0;
  float facedist = onedge ? edgedist : length(n*dot(n,p)/dot(n,n));
  if (key(CHAR_E) && onedge) t = EDGE;
  return vec2(facedist,edgedist);
}

float scene(vec3 pos, out int type, out vec2 uv) {
  vec2 t = faces(pos,type,uv);
  float facedist = t.x;
  float edgedist = t.y;
  return mix(facedist,edgedist,0.0)-roundness;
}
vec3 fold(vec3 pos) {
  int parity = 0;
  if (key(CHAR_F)) return pos;
  for (int i = 0; i < NFOLDS; i++) {
    float k;
    k = dot(pos,A);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*A;
    k = dot(pos,B);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*B;
    k = dot(pos,C);
    parity += int(k > 0.0);
    pos -= 2.0*max(0.0,k)*C;
  }
  return pos;
}

vec3 fold0(vec3 pos) {
  if (key(CHAR_F)) return pos;
  pos.xy = abs(pos.xy);
  pos -= 2.0*max(0.0,dot(pos,C))*C;
  pos.xy = abs(pos.xy);
  pos -= 2.0*max(0.0,dot(pos,C))*C;
  pos.xy = abs(pos.xy);
  pos -= 2.0*max(0.0,dot(pos,C))*C;
  pos.xy = abs(pos.xy);
  pos -= 2.0*max(0.0,dot(pos,C))*C;
  pos.xy = abs(pos.xy);
  pos -= 2.0*max(0.0,dot(pos,C))*C;
  pos.xy = abs(pos.xy);
  return pos;
}

float map(vec3 pos, out int type, out vec2 uv) {
  pos = fold(pos);
  return scene(pos,type,uv);
}

float map(vec3 pos) {
  int type; vec2 uv;
  return map(pos,type,uv);
}

// Tetrahedron technique, from:
// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(vec3 p) {
  float h = 0.0001;
  vec2 k = vec2(1,-1);
  return normalize( k.xyy*map( p + k.xyy*h ) + 
                    k.yyx*map( p + k.yyx*h ) + 
                    k.yxy*map( p + k.yxy*h ) + 
                    k.xxx*map( p + k.xxx*h ) );
}

float march(in vec3 ro, in vec3 rd) {
  const float maxd = 5.0;
  const float precis = 0.01;
  float t = 0.0;
  float res = -1.0;
  for (int i = 0; i < 200; i++) {
    //assert(i < 30);
    if (t > maxd) return -1.0;
    float h = map(ro+rd*t);
    t += h;
    if (h < precis) return t;
  }
  return t;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    //p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

vec3 circuit[] = vec3[]
  (vec3(1,1,1),vec3(1,1,1),vec3(1,0,0),vec3(1,0,0),
   vec3(1,1,0),vec3(1,1,0),vec3(0,1,0),vec3(0,1,0),
   vec3(0,1,1),vec3(0,1,1),vec3(0,0,1),vec3(0,0,1),
   vec3(1,0,1),vec3(1,0,1),vec3(1,0,0),vec3(1,0,0),

   vec3(1,1,1),vec3(1,1,0),vec3(1,1,1),vec3(0,1,0),
   vec3(1,1,1),vec3(0,1,1),vec3(1,1,1),vec3(0,0,1),
   vec3(1,1,1),vec3(1,0,1),

   vec3(1,1,1),vec3(1,0,0),vec3(1,1,0),vec3(0,1,0),
   vec3(0,1,1),vec3(0,0,1),vec3(1,0,1),vec3(1,0,0));

const int nsteps = circuit.length();

vec3 gettrilinear(float t) {
  int i = int(floor(t))%nsteps;
  return mix(circuit[i],circuit[(i+1)%nsteps],mod(t,1.0));
}

vec3 basecolor(int type) {
  if (type == FACE0) return vec3(1,0,0);
  if (type == FACE1) return vec3(1,1,0);
  if (type == FACE2) return vec3(0,0,1);
  if (type == EDGE) return vec3(0.2);
  if (type == VERTEX) return vec3(0.1);
  return vec3(1,0,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 z = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
  vec3 ro = vec3(0,0,3);
  vec3 rd = vec3(z,-2);
  vec3 light = vec3(0.5, 0.8, 3.0);
  ro = transform(ro);
  rd = transform(rd);
  light = transform(light);
  light = normalize(light);
  rd = normalize(rd);
  trilinear = gettrilinear(0.5*iTime);
  initgeometry();
  vec3 col = texture(iChannel0,rd).xyz;
  col = pow(col,vec3(2.2));
  float t = march(ro, rd);
  if (t > 0.0 && t < 1e8) {
    vec3 pos = ro + t * rd;
    vec3 n = calcNormal(pos);
    int type; vec2 uv;
    map(pos,type,uv); // Get type and uv
    col = basecolor(type);
    if (key(CHAR_C) && type != EDGE) col = vec3(1);
    if (key(CHAR_M)) {
      col = texture(iChannel0,reflect(rd,n)).xyz;
    } else {
      if (!key(CHAR_T)) col *= texture(iChannel1,uv).xyz;
      float diffuse = clamp(dot(n, light), 0.0, 1.0);
      col *= 0.2 + 0.8*diffuse;
      float specular = pow(max(0.0,dot(reflect(light,n),rd)),10.0);
      col += 0.3*specular*vec3(1);
    }
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.x = 1.0;
  fragColor = vec4(col, 1.0);
}

