//-------------------------------------------------
//From https://www.shadertoy.com/view/XtXGRS#
vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float dot2( in vec3 v ) { return dot(v,v); }

// Function by iq: https://www.shadertoy.com/view/ttfGWl
vec3 closestTriangle(vec3 p, vec3 v0, vec3 v1, vec3 v2)
{
    vec3 v10 = v1 - v0; vec3 p0 = p - v0;
    vec3 v21 = v2 - v1; vec3 p1 = p - v1;
    vec3 v02 = v0 - v2; vec3 p2 = p - v2;
    vec3 nor = cross(v10,v02);

    // method 1, in 3D space
    if( dot(cross(v10,nor),p0)<0.0 ) return v0 + v10*clamp( dot(p0,v10)/dot2(v10), 0.0, 1.0 );
    if( dot(cross(v21,nor),p1)<0.0 ) return v1 + v21*clamp( dot(p1,v21)/dot2(v21), 0.0, 1.0 );
    if( dot(cross(v02,nor),p2)<0.0 ) return v2 + v02*clamp( dot(p2,v02)/dot2(v02), 0.0, 1.0 );
    return p - nor*dot(nor,p0)/dot2(nor);
}

float triangleDistance(vec3 p, vec3 v0, vec3 v1, vec3 v2) {
  return distance(p,closestTriangle(p,v0,v1,v2));
}

// Function by iq: https://www.shadertoy.com/view/ttfGWl
// Triangle extends to infinity
vec3 closestTriangle2(vec3 p, vec3 v0, vec3 v1, vec3 v2)
{
    vec3 v10 = v1 - v0; vec3 p0 = p - v0;
    //vec3 v21 = v2 - v1; vec3 p1 = p - v1;
    vec3 v02 = v0 - v2; vec3 p2 = p - v2;
    vec3 nor = cross(v10,v02);

    // method 1, in 3D space
    if( dot(cross(v10,nor),p0)<0.0 ) return v0 + v10*clamp( dot(p0,v10)/dot2(v10), 0.0, 1.0 );
    //if( dot(cross(v21,nor),p1)<0.0 ) return v1 + v21*clamp( dot(p1,v21)/dot2(v21), 0.0, 1.0 );
    if( dot(cross(v02,nor),p2)<0.0 ) return v2 + v02*clamp( dot(p2,v02)/dot2(v02), 0.0, 1.0 );
    return p - nor*dot(nor,p0)/dot2(nor);
}

float triangleDistance2(vec3 p, vec3 v0, vec3 v1, vec3 v2) {
  return length(p-closestTriangle2(p,v0,v1,v2));
}

