    //select polyhedra from (wk2,srk2 ), (w33,sr33), (w43,sr43), (w53,sr53)
    w = metamorph(w43,w53,ff); sr = metamorph(sr43,sr53,ff);
	#endif

    // return texture data
	mat = vec4(w,sr,1.0 );

	// sdf r(w) sphere
    float r = 1.0 + 0.1*sin(atan(log(length(w))*ba_v_distri));
	float d = length(p)-r;
