}


// Get distance field
float map(vec3 rp){
    float d = 999.;

    rp.xy*=rot(iTime*.1);
    rp.z-=iTime*.4;

    rp.yx += vec2(.4, .36);

    // Repeat space
    vec3 b = vec3(vec2(0.78), 2.6);
    vec3 id = floor(rp/vec3(b.x, b.y, 1.));

    //rp.yz *= rot(id.x*0.3);

    //rp.z += cos(rp.x*3. + iTime*10.)*0.02;
    //rp.x += sin(rp.y*3. + iTime*10.)*0.02;

    rp = mod(rp, b)-b*0.5;

    // Mirror space
    rp.xy = -abs(rp.xy);

    // Sphere height
    float z = .18*cos(.8*iTime+(id.x+id.y*4.)*40.);

    vec2 c0 = vec2(.5);
    vec2 c = c0;
    vec2 lc = c;

    // Build the web grid thing
    for(float i = 0.0; i < 19.0; i++){
        c = c0 * rot(i*8.6);
        d = min(d, sdCap(rp, vec3(lc, 0.), vec3(c, 0.), .017));
        lc = c;
    }


    return d;
}

// Standard raymarch normals
vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}

// Trace scene
float march(vec3 rd, vec3 ro){
 	float t = 0., d = 0.;   

    for(int i = 0; i < 55; i++){
    	d = map(ro + rd*t); 	   
        if(abs(d) < .0015 || t > 8.) break;

        t += d * .55;
    }
    return t;
}

// Color pixel
vec3 color(vec3 p, vec3 rd, vec3 n, float t){
    // Light position and direction
    vec3 lp = vec3(0., 22., -13.0);
    vec3 ld = normalize(lp-p);

    // Standard specular
    float spec = pow(max(dot(normalize(reflect(ld, n)), rd), 0.), 16.);

    vec3 col = vec3(0.75, 0.9, 0.95)*spec*.16;

    col = mix(vec3(.333), col, exp(-t*t*t*t*0.0007)); // White fog
	return col;   
}



void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    // Ray direction with inverted fish eye 
    vec3 rd = normalize(vec3(uv, 1.0 - dot(uv, uv) * -.7));
    // Ray origin
    vec3 ro = vec3(0., 0., -1.8);

    rd.yz *= rot(-.6);
    //rd.xz *= rot(.07);

    // Mouse movement
    if(iMouse.z > 0.){
        ro.x += m.x*3.;
        ro.y += m.y*2.3;
        rd.xz *= rot(m.x*.9);
    }

    float t = march(rd, ro); // Dist to scene
    vec3 n = normal(ro + rd*t); // normals of scene
    vec3 col = color(ro + rd*t, rd, n, t); // Get pixel color

    col *= ss(0.9, 0.45, abs(uv.x)); // Outer side shade

    f = vec4(col, 1.0);
}

