// Raymarch scene

//iquilezles.org/articles/distfunctions
float sdCap( vec3 p, vec3 a, vec3 b, float r ){
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

// Easy way to do differnt materials for any object 
float mat = 0.;
float md = 99.;
void newmat(float d, float nm){
    if(d < md){
        md = d;
        mat = nm;
    }
}

// Scene translation
void trns(inout vec2 p){
    p.y += iTime*.4;
    p.x += cos(iTime*.25)*.5;
}

// Get distance field
float map(vec3 rp){
    float d = 999.;

    trns(rp.xy);

    // Repeat space
    vec3 b = vec3(vec2(0.78), 0.);
    vec3 id = floor(rp/vec3(b.x, b.y, 1.));
    rp = mod(rp, b)-b*0.5;

    // Mirror space
    rp.xy = -abs(rp.xy);

    // Sphere height
    float z = .18*cos(.8*iTime+(id.x+id.y*4.)*40.);

    // Dist to spheres 
    d = min(d, length((rp-vec3(.0, .0, z))*vec3(1., 1., 1.5))-.275);
    newmat(d, 0.); // Use mat 0

    vec2 c0 = vec2(.5);
    vec2 c = c0;
    vec2 lc = c;

    // Build the web grid thing
    for(float i = 0.0; i < 19.0; i++){
        c = c0 * rot(i*8.6);
        d = min(d, sdCap(rp, vec3(lc, 0.), vec3(c, 0.), .022));
        lc = c;
    }
    newmat(d, 1.); // Use mat 1

    return d;
}

// Standard raymarch normals
vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}

// Trace scene
float march(vec3 rd, vec3 ro){
 	float t = 0., d = 0.;   

    for(int i = 0; i < 58; i++){
    	d = map(ro + rd*t); 	   
        if(abs(d) < .0015 || t > 11.) break;

        t += d * .55;
    }
    return t;
}

// Color pixel
vec3 color(vec3 p, vec3 rd, vec3 n, float t){
    // Light position and direction
    vec3 lp = vec3(0., 22., -13.0);
    vec3 ld = normalize(lp-p);

    // Standard specular and fresnel
    float spec = pow(max(dot(normalize(reflect(ld, n)), rd), 0.), 16.);
    float fres = smoothstep(-.72, .72, dot(rd, n));

    trns(p.xy);

    vec3 col = vec3(0);

    // Sphere id
    vec3 b = vec3(vec2(0.78), 0.);
    vec3 id = floor(p/vec3(b.x, b.y, 1.));

    float pwr = .23; // specular power

    // Color variation for spheres
    if(mat == 0.){ 
        pwr = .07; 
        col = .014*vec3((.5+.5*cos((id.y+id.x)*324.3 + iTime*.25)));
        col += vec3(0.9, 0.75, 0.7)*spec*pwr;
        col += fres*.12;
    }
    else if(mat == 1.) col += vec3(0.8, 0.9, 0.99)*spec*pwr; // Slightly different color for web thing

    col = mix(vec3(.9), col, step(t, 10.)); // White background

	return col;   
}



void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = vec2(u.xy - 0.5*R.xy)/R.y;

    // Ray direction with inverted fish eye 
    vec3 rd = normalize(vec3(uv, 1.0 - dot(uv, uv) * -0.45));
    // Ray origin
    vec3 ro = vec3(0., 0., -1.8);

    rd.yz *= rot(-.6);
    rd.xz *= rot(.07);

    // Mouse movement
    if(iMouse.z > 0.){
        ro.x += m.x*3.;
        ro.y += m.y*2.3;
        rd.xz *= rot(m.x*.6);
    }

    float t = march(rd, ro); // Dist to scene
    vec3 n = normal(ro + rd*t); // normals of scene
    vec3 col = color(ro + rd*t, rd, n, t); // Get pixel color

    col *= ss(0.99, 0.45, abs(uv.x)); // Outer side shade

    f = vec4(col, 1.0);
}

