// Uncomment for a smooth surface
//#define SMOOTH_SURFACE

#define SIN_25 0.42261826174
#define COS_25 0.90630778703

float mapScene(in vec3 p) {
    const vec2 res = vec2(0.2);
    vec2 uv = 0.25 * p.xz + 0.5;

    vec2 cell = floor(uv / res) * res;
    vec2 local = fract(uv / res);

    #ifdef SMOOTH_SURFACE
    local *= local * (3.0 - 2.0 * local);
    #endif

    float ld = texture(iChannel0, cell).x;
    float rd = texture(iChannel0, cell + vec2(res.x, 0.0)).x;
    float lu = texture(iChannel0, cell + vec2(0.0, res.y)).x;
    float ru = texture(iChannel0, cell + res).x;

    float height = 2.0 * mix(mix(ld, rd, local.x), mix(lu, ru, local.x), local.y);
    float sheet = max(abs(p.y - height) - 0.05, max(abs(p.x), abs(p.z)) - 2.0) * 0.5;

    return sheet;
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)) - mapScene(p - vec3(0.001, 0.0, 0.0)),
                          mapScene(p + vec3(0.0, 0.001, 0.0)) - mapScene(p - vec3(0.0, 0.001, 0.0)),
                          mapScene(p + vec3(0.0, 0.0, 0.001)) - mapScene(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float time = iTime + texture(iChannel0, vec2(1.0)).x;
    float c = cos(time), s = sin(time);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float dt = 0.0;
    for (int iter=0; iter < 200; iter++) {
        vec3 p = ro + rd * dt;

        p.y += 0.5;
        p.yz *= mat2(COS_25, SIN_25, -SIN_25, COS_25);
        p.xz *= mat2(c, s, -s, c);

        float d = mapScene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);

            n.xz *= mat2(c, -s, s, c);
            n.yz *= mat2(COS_25, -SIN_25, SIN_25, COS_25);

            vec2 uv = 0.25 * p.xz + 0.5;
            fragColor.rgb += mix(mix(vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), uv.x), mix(vec3(1.0, 1.0, 0.0), vec3(0.0, 0.0, 1.0), uv.x), uv.y);
            fragColor.rgb *= max(0.3, dot(n, l));

            break;
        }

        if (d > 100.0) {
            break;
        }

        dt += d;
    }
}

