/// Convert from a buffer of normals/depth into something pretty
#define BUFFER_WORLD iChannel0
#define BUFFER_STATE iChannel1



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //vec2 screen_coords = (fragCoord/iResolution.xy);
    //vec4 world = texture(WORLD_BUFFER, screen_coords);
    vec2 uv = (fragCoord/iResolution.xy);

    // sample previous tile
    #ifdef HQ
    vec4 previous = vec4(0.0);
    #else
    vec4 previous = sample_tile(BUFFER_WORLD, iResolution.xy, uv, 1.0);
    #endif

    mat4 camera_transform = quat_to_transform(
        read_data(BUFFER_STATE, ADDR_CAMERA_ORIENTATION),
        read_data(BUFFER_STATE, ADDR_CAMERA_POSITION).xyz
    );


    // Output to screen
    vec4 data = raymarch(
        iResolution.xy,
        uv,
        previous,
        camera_transform
    );

    float dist = data.a;
    vec3 end_position = data.xyz;

    vec3 surface_normal = calc_normal(end_position);

    float lighting = dot(surface_normal, vec3(0.5)) * 0.5 + 0.5;

    float fog = dist * 0.25;//pow(dist, 2.0);

    vec3 col = vec3(0.5, 0.6, 0.9);
    col *= lighting;
    col = mix(col, vec3(1.0, 0.9, 0.8), fog);

    fragColor = vec4(col, 1.0);

    if (iMouse.z > 0.0) {
        //fragColor = sample_tile(iChannel0, iResolution.xy, uv, 1.0);
        fragColor = vec4(texture(BUFFER_WORLD, uv).a) * 0.1;
    }
}

