#define HQ

const ivec2 ADDR_CAMERA_POSITION = ivec2(0,0);
const ivec2 ADDR_CAMERA_ORIENTATION = ivec2(0,1);
const ivec2 ADDR_CAMERA_ANG_VELOCITY = ivec2(0,2);
const ivec2 ADDR_CAMERA_LIN_VELOCITY = ivec2(0,3);

const float LENS = 0.5;

const int KEY_LEFT = 65;
const int KEY_UP   = 82;
const int KEY_RIGHT = 68;
const int KEY_DOWN = 70;
const int KEY_FORWARD = 87;
const int KEY_BACKWARD = 83;

const int KEY_TILT_UP = 38;
const int KEY_TILT_DOWN = 40;
const int KEY_PAN_LEFT = 37;
const int KEY_PAN_RIGHT = 39;
const int KEY_ROLL_LEFT = 81;
const int KEY_ROLL_RIGHT = 69;

const float MAX_TILES = 3.0;

#ifdef HQ
const int steps = 96;
#else
const int steps = 32;
#endif

// Fetch a single pixe from a buffer
vec4 read_data(sampler2D buffer, ivec2 address){
    return texelFetch(buffer, address, 0);
}



// A surface is defined by a vec4. The rgb channels are the normal, the alpha
// is the distance to the surface. This makes physics etc. very easy
float sphere_sdf(vec3 query_point, float sphere_radius) {
    float l = length(query_point);
    float df = l - sphere_radius;

    return df;
}


float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}



float surface_intersect(float surface_1, float surface_2) {
    // Find the volume both surface occupy
    return max(surface_1, surface_2);
}
float surface_difference(float surface_1, float surface_2) {
    // subtract surface_2 from surface_1
    // invert surface 2:
    return surface_intersect(surface_1, -surface_2);
}


// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

// polynomial smooth min (k = 0.1);
float smax( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return max( a, -b ) + h*h*k*(1.0/4.0);
}



float distance_field(vec3 co) {
    float rad = clamp(co.z * 0.05 + 0.45, 0.1, 0.3);
    //float rad = 0.45;
    co = mod(co, vec3(1.0)) - 0.5;
    //co = (co - clamp(round(co), -5.0, 5.0));
    //return sphere_sdf(co, rad);

    return sdRoundBox(co, vec3(rad, rad, 0.3), 0.1);
}


// A simple fractal-like made by iterative transformation
float sample_world(vec3 world_position) {

    float body = 999.0;
    vec3 co = world_position;
    //co.z *= 0.5;

    float scale = 0.2;
    mat4 m = mat4(
		vec4(0.6373087, -0.0796581,  0.7664804, 0.0),
  		vec4(0.2670984,  0.9558195, -0.1227499, 0.0),
  		vec4(-0.7228389,  0.2829553,  0.6304286, 0.0),
        vec4(0.1, 0.6, 0.2, 0.0)
    );
    //mat4 m = mat4(1.0);

    for (int i=0; i<3; i++) {
        co = (m * vec4(co, float(i))).xyz;
        scale *= (3.0);

        float field = distance_field(co * scale) / scale;
     	body = smin(body, field, 0.05);
    }

    return -body;
}

vec3 calc_normal(vec3 sample_point) {
    const float h = 0.01; // replace by an appropriate value
    const vec2 k = vec2(1,-1);

    vec3 normal = normalize(
		k.xyy * sample_world( sample_point + k.xyy*h ) + 
		k.yyx * sample_world( sample_point + k.yyx*h ) + 
		k.yxy * sample_world( sample_point + k.yxy*h ) + 
		k.xxx * sample_world( sample_point + k.xxx*h ) );
    normal = normal.zyx;
    return normal;
}


vec4 raymarch(vec2 resolution, vec2 uv, vec4 start_data, mat4 camera_transform) {
    // Convert to range (-1, 1) and correct aspect ratio
    vec2 screen_coords = (uv - 0.5) * 2.0;
    screen_coords.x *= resolution.x / resolution.y;


    vec3 ray_start_position = camera_transform[3].xyz;

    vec3 ray_direction = normalize(vec3(screen_coords * LENS, 1.0));
    ray_direction = (camera_transform * vec4(ray_direction, 0.0)).xyz;


    float dist = start_data.a * 0.9;
    vec3 sample_point = ray_start_position + dist * ray_direction;

    float results = sample_world(sample_point);

    float tolerance = 0.0;

    for (int i=0; i<steps; i += 1) {
        dist += results;
        sample_point += ray_direction * results;
        results = sample_world(sample_point);

        // TODO: Derive from resolution, camera lens and distance
    	tolerance = LENS / resolution.x * dist;

        if (results < tolerance || dist > 5.0) {
        	break; 
        }
    }



    return vec4(sample_point, dist);
}





// Create a quaternion from axis-angle notation
vec4 quat_from_axis_angle(vec3 axis, float angle) {
    float factor = sin(angle) / 2.0;
    float w = cos(angle) / 2.0;
    return normalize(vec4(axis*factor, w));
}

// Convert a quaternion into a transformation matrix
mat4 quat_to_transform(vec4 quat, vec3 translation) {
    float qx = quat.x;
    float qy = quat.y;
    float qz = quat.z;
    float qw = quat.w;
    float qx2 = qx * qx;
    float qy2 = qy * qy;
    float qz2 = qz * qz;

 	return mat4(
        1.0 - 2.0*qy2 - 2.0*qz2,	2.0*qx*qy - 2.0*qz*qw,	2.0*qx*qz + 2.0*qy*qw, 0.0,
    	2.0*qx*qy + 2.0*qz*qw,	1.0 - 2.0*qx2 - 2.0*qz2,	2.0*qy*qz - 2.0*qx*qw, 0.0,
    	2.0*qx*qz - 2.0*qy*qw,	2.0*qy*qz + 2.0*qx*qw,	1.0 - 2.0*qx2 - 2.0*qy2, 0.0,
        translation, 0.0
    );
}

// Multiply two quaternions
vec4 quat_mul(vec4 a, vec4 b) {
 	return vec4(
        a.w * b.x + a.x * b.w + a.y * b.z - a.z * b.y,
        a.w * b.y - a.x * b.z + a.y * b.w + a.z * b.x,
        a.w * b.z + a.x * b.y - a.y * b.x + a.z * b.w,
        a.w * b.w - a.x * b.x - a.y * b.y - a.z * b.z
    );   
}






vec2 tile_resolution(vec2 screen_resolution, float tile_id_f) {
    float tile_height = 1.0 / pow(2.0, tile_id_f);
    return screen_resolution * tile_height;
}


vec4 sample_tile(sampler2D buffer, vec2 resolution, vec2 uv, float tile_id_f) {
    float tile_height = 1.0 / pow(2.0, tile_id_f);

    uv.x += 1.0;
    vec2 area_uv = uv * tile_height;

    // Compensate for GL.LINEAR sampling - we need to sample the middle of the pixel
    vec2 tile_resolution = resolution * tile_height;
    vec2 inv_resolution = 1.0 / resolution.xy;
    area_uv -= mod(area_uv, inv_resolution) - inv_resolution * 0.5;

    return texture(buffer, area_uv);
}

