// Render the world. 
//
// Outputs:
// - rgb = analytic normal
// - a = depth
//
// Pretty much a single raymarcher

#define BUFFER_STATE iChannel0
#define BUFFER_SELF iChannel1



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifndef HQ
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float tile_id_f = floor(log2(1.0 / uv.x)) + 1.0;
    float tile_height = 1.0 / pow(2.0, tile_id_f);

	// Convert to coordinates for this tile    
    vec2 tile_uv = vec2(uv / tile_height);
   	tile_uv.x -= 1.0;

    if (uv.y > tile_height || tile_id_f > MAX_TILES) {
        fragColor = vec4(0.0);
        return;
    }

    // sample previous tile
    vec4 previous = sample_tile(BUFFER_SELF, iResolution.xy, tile_uv, tile_id_f + 1.0);


    mat4 camera_transform = quat_to_transform(
        read_data(BUFFER_STATE, ADDR_CAMERA_ORIENTATION),
        read_data(BUFFER_STATE, ADDR_CAMERA_POSITION).xyz
    );


    vec4 data = raymarch(
        tile_resolution(iResolution.xy, tile_id_f),
        tile_uv,
        previous,
        camera_transform
    );

