// ##### Fractal #####
float mandelbulb(vec3 pos, int iter) {
	float Power = float(2.0);
	vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
	for (int i = 0; i < iter; i++) {
		r = length(z);
		if (r>Bailout) break;
		// Convert to Polar Coordinates
		float theta = acos(z.z/r);
		float phi = atan(z.y,z.x);
		dr =  pow(r, Power-1.0)*Power*dr+1.0;
		// Scale and Rotate the Point
		float zr = pow(r, Power);
		theta = theta*Power;
		phi = phi*Power;
		// Convert Back to Cartesian Coordinates
		z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
		z+=pos;
	}
	return 0.5*log(r)*r/dr;
}

// Fractal Density
int randexpiter(float iter){
    return int(round((rand()*iter)+iter));
}

vec3 mandelbulbvolume(vec3 raypos){
    vec3 dist = vec3(mandelbulb(raypos, randexpiter(16.0)), mandelbulb(raypos, randexpiter(8.0)), mandelbulb(raypos, randexpiter(4.0)));
    vec3 density = -clamp(pow(dist, vec3(0.25)), 0.0, 0.1)+0.1;
    return density;
}

// ##### RAYMARCHING #####
vec3 directLight(vec3 raypos){
    vec3 LightColor = vec3(16.0);
    vec3 LightDirection = normalize(vec3(1.0));
    float distorigin = 0.0;
    vec3 density = vec3(0.0);
    vec3 raydirmod = LightDirection*STEP_SIZE*rand();
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos+raydirmod;
        density = vec3(mandelbulbvolume(raypos)*DENSITY);
        vec3 absorbance = exp(-density);
        attenuation *= absorbance;
    }
    return LightColor*attenuation;
}

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin = 0.0;
    //vec3 densityadd = vec3(0.0);
    //vec3 density = vec3(0.0);
    vec3 raypos = camerapos;
    vec3 raydirmod = (raydir*rand())*STEP_SIZE;
    vec3 outCol = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos + raydirmod;
        // LoicVDB: don't use this, the density doesn't accumulate, its
        // only purpose is to compute absorbance and transmittance
        //densityadd = vec3(fractal(raypos)*DENSITY);
        //density = vec3(density+densityadd);

        // LoicVDB: just use this for density instead:
        vec3 density = vec3(mandelbulbvolume(raypos)*DENSITY);
        vec3 absorbance = exp(-density*STEP_SIZE);
        vec3 transmittance = 1.0-absorbance;
        vec3 directlighting = attenuation*transmittance;
        if((density.r+density.g+density.b) > 0.0){
            directlighting = directlighting*directLight(raypos);
        }
        outCol += directlighting;
        attenuation *= absorbance;
        distorigin = raypos.z-camerapos.z;
        if(distorigin>MAX_DISTANCE) break;
    }
    return outCol;
}

