int hCheck(vec2 U, vec2 D, out int count) {
    count = 0;
    int  d = num(U+D) - num(U);                                 // value step in dir D, use a ref for candidate series

    vec2 P = D-D;
    for( int i=0; i<6; i++, P+=D )                              // count series  U + kD
        if ( num(U+P+D) - num(U+P) == d ) count++; else break;

    return d;                                                   // return increment, and out: count
}

float check(vec2 U,vec2 D)  {
    int c,  d  =  hCheck(U, D,c ),                              // count upstream
        cr, dr = -hCheck(U,-D,cr),                              // and downstream
