////////////////////////////////////////////////////////////////////////////////
//
// 729 Rhombic Dodecahedra, Matthew Arcus, mla, 2020
//
// Ray tracing parallelograms - how many can we do?
// Quite a few in fact, a fast sphere intersection test helps.
//
// Mouse and up/down moves around
// 'b': blink
// 'f': fog
// 'r': rotation
//
////////////////////////////////////////////////////////////////////////////////

const int N = 4; // (2N+1)^3 polyhedra
float maxdist = 60.0;

// Ray-plane intersection:
// Intersect p+kr with plane generated by e0,e1 at origin
// Solve linear system: p-tr = ue0 + ve1  & return vec3(-t,u,v).
// Uses Cramer's rule, ie. if mat3(A,B,C)*X = P then:
// X = vec3(det(P,B,C),det(A,P,C),det(A,B,P))/det(A,B,C)
// Determinant of mat3(A,B,C) is just the triple product:
// dot(A,cross(B,C)) = dot(B,cross(C,A)) = -dot(C,cross(B,A)) etc.
//
// This is the ray-triangle intersection algorithm of Tomas Mller and Ben Trumbore,
// but it's of wider applicability than just to triangles. (If e0,e1 are sides of a
// triangle, then we are in the triangle iff 0 <= u,v,1-u-v <= 1 but can generally treat
// u and v as (possibly oblique) general coords in the plane).

vec3 intersect(vec3 p, vec3 r, vec3 e0, vec3 e1, vec3 n) {
  // Expects that n = cross(e0,e1);
  vec3 m = cross(r,p);
  return vec3(-dot(p,n),dot(m,e1),-dot(m,e0))/dot(r,n);
}

int hitindex = -1;
vec3 hitnormal = vec3(0);
bool checkparallelogram(vec3 p, vec3 r, vec3 v0, vec3 v1, vec3 v2, int index, inout vec3 amin) {
  p -= v0; v1 -= v0; v2 -= v0;
  vec3 normal = cross(v1,v2);
  if (dot(normal,r) > 0.0) return false; // Back face cull
  vec3 a = intersect(p,r,v1,v2,normal);
  // a = vec3(t,u,v). Require 0 <= t <= amin.x and 0 <= u,v <= 1 for a parallelogram
  bool hit = all(bvec2(all(greaterThanEqual(a,vec3(0))),
                       all(lessThanEqual(a,vec3(amin.x,1,1)))));
  if (!hit) return false;
  hitindex = index;
  hitnormal = normal;
  amin = a;
  return true;
}

void rhombic(vec3 p, vec3 r, vec3 q, int index, inout vec3 amin) {
  p -= q;
  if (key(CHAR_B)) {
      float h = fhash(uint(index)^uint(4.0*iTime));
      if (h < 0.1) return;
  }
  // Octahedron vertices
  vec3 o0 = vec3(0,0,2);
  vec3 o1 = vec3(0,0,-2);
  vec3 o2 = vec3(0,2,0);
  vec3 o3 = vec3(0,-2,0);
  vec3 o4 = vec3(2,0,0);
  vec3 o5 = vec3(-2,0,0);
  // Cube vertices
  vec3 c0 = vec3(1,1,1);
  vec3 c1 = vec3(1,-1,1);
  vec3 c2 = vec3(-1,-1,1);
  vec3 c3 = vec3(-1,1,1);

  vec3 c4 = vec3(1,1,-1);
  vec3 c5 = vec3(1,-1,-1);
  vec3 c6 = vec3(-1,-1,-1);
  vec3 c7 = vec3(-1,1,-1);

  // Rhombic dodecahedron faces
  checkparallelogram(p,r,o0,c1,c0,index,amin);
  checkparallelogram(p,r,o0,c2,c1,index,amin);
  checkparallelogram(p,r,o0,c3,c2,index,amin);
  checkparallelogram(p,r,o0,c0,c3,index,amin);

  checkparallelogram(p,r,o1,c7,c4,index,amin);
  checkparallelogram(p,r,o1,c6,c7,index,amin);
  checkparallelogram(p,r,o1,c5,c6,index,amin);
  checkparallelogram(p,r,o1,c4,c5,index,amin);

  checkparallelogram(p,r,o4,c4,c0,index,amin);
  checkparallelogram(p,r,o4,c1,c5,index,amin);
  checkparallelogram(p,r,o5,c6,c2,index,amin);
  checkparallelogram(p,r,o5,c3,c7,index,amin);
}

// Fast check for sphere intersection
bool checksphere (vec3 p, vec3 r, vec3 centre, float radius2) {
  p -= centre; // centre on sphere
  float pr = dot(p,r);
  if (pr > 0.0) return false;
  float pp = dot(p,p);
  return pr*pr >= pp-radius2;
}

vec3 getcolor(int hitindex) {
  float h = fhash(uint(hitindex));
  return hsv2rgb(vec3(h,1,1));
}

vec3 scene(vec3 p, vec3 r, vec3 light) {
  vec3 background = vec3(1,1,0.75);
  vec3 color = background;
  vec3 amin = vec3(1e8,0,0);
  float radius2 = 4.0; // Bounding sphere radius
  for (int i = -N; i <= N; i++) {
    for (int j = -N; j <= N; j++) {
      for (int k = -N; k <= N; k++) {
        vec3 centre = 4.0*vec3(i,j,k);
        if (checksphere(p,r,centre,radius2)) {
          int index = (i+N)+(2*N+1)*(j+N+(2*N+1)*(k+N));
          //assert(index >= 0);
          //assert(index < (2*N+1)*(2*N+1)*(2*N+1));
          rhombic(p,r,centre,index,amin);
        }
      }
    }
  }
  if (hitindex >= 0) {
    hitnormal = normalize(hitnormal);
    color = getcolor(hitindex);
    //color *= texture(iChannel0,amin.yz).xyz;
    color *= 0.4+0.6*(max(0.0,dot(hitnormal,light)));
    if (!key(CHAR_F)) color = mix(background,color,max(0.0,maxdist-amin.x)/maxdist);
  }
  return color;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) p.zx = rotate(p.zx,iTime * 0.2);
  return p;
}

void mainImage(out vec4 outColor, vec2 fragCoord) {
  vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
  float camera = -40.0;
  camera *= exp(-0.1*float(keycount(KEY_UP)-keycount(KEY_DOWN)));
  vec3 p = vec3(0,0,camera);
  p = transform(p);
  vec3 light = vec3(0,1,-1);
  light = transform(light);
  light = normalize(light);
  vec3 r = vec3(uv,2);
  r = transform(r);
  r = normalize(r);

  vec3 color = scene(p,r,light);
  pow(color,vec3(0.4545));
  if (alert) color.r = 1.0;
  outColor = vec4(color,1);
}

