const float PI = 3.14159;

const vec3 bg = vec3(226.0/255.0);
const vec3 black = vec3(0.0);
const vec3 tri_red = vec3(239.0/255.0, 23.0/255.0, 2.0/255.0);
const vec3 tri_orange = vec3(224.0/255.0, 150.0/255.0, 2.0/255.0);

const vec3 ps_red = vec3(255.0/255.0, 26.0/255.0, 1.0/255.0);
const vec3 ps_yellow = vec3(255.0/255.0, 179.0/255.0, 1.0/255.0);
const vec3 ps_green = vec3(15.0/255.0, 126.0/255.0, 120.0/255.0);
const vec3 ps_blue = vec3(36.0/255.0, 82.0/255.0, 145.0/255.0);


vec3 gradient(float t) {
    vec3 left = mix(tri_red, tri_orange, t*2.0);
    vec3 right = mix(tri_orange, tri_red, t*2.0 - 1.0);
    return mix(left, right, smoothstep(0.49, 0.51, t));
}

float diamond(vec2 uv) {
    float bl = mix(0.0, 1.0, smoothstep(0.997, 1.003, uv.x + uv.y + 0.5));
    float tl = mix(0.0, 1.0, smoothstep(0.997, 1.003, uv.x - uv.y + 1.5));
    float br = mix(0.0, 1.0, smoothstep(0.997, 1.003, - uv.x + uv.y + 1.5));
    float tr = mix(0.0, 1.0, smoothstep(0.997, 1.003, - uv.x - uv.y + 2.5));
    return tl * tr * bl * br;
}

vec3 diamond_gradient(vec2 uv, vec3 bg) {
    return mix(bg, gradient(uv.x), diamond(uv));
}

float triangle(vec2 uv) {
    float bl = mix(0.0, 1.0, smoothstep(0.997, 1.003, uv.x + uv.y + 0.5));
    float tl = mix(0.0, 1.0, smoothstep(0.997, 1.003, uv.x - uv.y + 1.5));
    return mix(bl * tl, 0.0, smoothstep(0.497, 0.503, uv.x));
}

vec3 triangle_gradient(vec2 uv, vec3 bg) {
    return mix(bg, gradient(uv.x), triangle(uv));
}

vec3 trilogo_animation(vec2 uv, float t_) {
    float t = clamp(0.0, 1.0, t_);
    vec3 big = diamond_gradient(uv, bg);

    vec2 uv_half1 = uv + vec2(0.0, 0.0);
    vec2 uv_half2 = -uv + vec2(1.0, 1.0);
    vec2 uv_small1 = 2.0 * uv - vec2(0.58, 0.92);
    vec2 uv_small2 = -2.0 * uv + vec2(1.42, 1.08);

    vec3 small1 = triangle_gradient(mix(uv_half1, uv_small1, t), big);
    vec3 small2 = triangle_gradient(mix(uv_half2, uv_small2, t), small1);
    return small2;
}

float line(vec2 uv, float at, float angle) {
    const float WIDTH = 0.003;
    return mix(0.0, 1.0, smoothstep(at - WIDTH, at + WIDTH, cos(angle) * uv.x + sin(angle) * uv.y));
}

float polycircle(int segments, vec2 uv, vec2 center, float radius) {
    float result = 1.0;
    for (int i = 0; i < 20; ++i) {
        float angle = 2.0 * PI * float(i + 1) / float(segments);
        result *= line(uv - center, -radius, angle);
    }
    return result;
}

float p(vec2 uv) {
    return clamp(0.0, 1.0, 
    (
        line(uv, -0.1, 0.0) * 
        line(uv, -0.17, PI) * 
        line(uv, -0.54, 1.35) *
        line(uv, -0.6, PI + 1.35)
    ) + 
    polycircle(20, uv * vec2(1.1, 1.0), vec2(0.41, 0.222), 0.3) * line(uv, 0.25, 0.0) +
    (
        line(uv, 0.5, 1.35) * 
        line(uv, -0.6, PI + 1.35) *
        line(uv, -0.1, 0.0) *
        line(uv, -0.43, PI)
    )
    );
}

float s(vec2 uv) {
    return clamp(0.0, 1.0,
    ( // prongs
        (
            line(uv, -0.56, 1.9) *
            line(uv, 0.43, 1.9 + PI) *
            line(uv, 0.22, PI + 1.35)
        ) + (
            line(uv, -0.35, 1.9) *
            line(uv, 0.22, 1.9 + PI) *
            line(uv, -0.26, 1.35)
        ) + (
            line(uv, -0.14, 1.9) *
            line(uv, 0.01, 1.9 + PI) *
            line(uv, -0.26, 1.35)
        )
    ) * ( // top/bottom
        line(uv, -0.47, 1.35) *
        line(uv, 0.06, PI + 1.35)
    ) + ( // right curve
        polycircle(15, uv * vec2(0.60, 1.3), vec2(-0.13, -0.34), 0.2) *
        line(uv, 0.01, 1.9 + PI) * (1.0 - clamp(
            line(uv, 0.14, 1.9 + PI) *
            line(uv, -0.22, 1.9) *
            line(uv, -0.31, 1.35) +
            polycircle(15, uv * 4.35 * vec2(0.60, 1.3), vec2(-0.58, -1.5), 0.2),
            0.0, 1.0
        ))
    ) + ( // left curve
        polycircle(15, uv * vec2(0.60, 1.3), vec2(0.23, -0.36), 0.2) *
        line(uv, -0.56, 1.9) * (1.0 - clamp(
            line(uv, 0.35, 1.9 + PI) *
            line(uv, -0.43, 1.9) *
            line(uv, 0.19, PI + 1.35) +
            polycircle(15, uv * 4.2 * vec2(0.60, 1.3), vec2(0.98, -1.5), 0.2),
            0.0, 1.0
        ))
    )
    );
}

vec4 s_color(vec2 uv) {
    float s_amount = s(uv);
    vec3 s_color = mix(mix(ps_yellow, ps_green, line(uv, -0.3, 1.2)), ps_blue, line(uv, -0.15, 1.2));
    return vec4(s_color, 1.0) * s_amount;
}

vec3 ps_logo(vec2 uv) {
    uv.x += 0.15;
    vec4 logo_s_main = s_color(uv);
    vec4 logo_s_shadow = s_color(uv + vec2(0.00, 0.04));
    vec3 logo_s = mix(mix(black, logo_s_shadow.rgb * 0.5, logo_s_shadow.a), logo_s_main.rgb, logo_s_main.a);
    vec3 logo_p = mix(mix(logo_s, ps_red * 0.5, p(uv + vec2(-0.04, -0.02))), ps_red, p(uv));
    return logo_p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 c_uv = 2.0 * uv - vec2(1.0);
    vec2 sq_uv = c_uv * vec2(iResolution.x / iResolution.y, 1.0);

    float t = mod(iTime, 15.0);
    vec3 color = black;
    if (t < 2.0) {
        color = mix(black, bg, smoothstep(0.0, 2.0, t));
    } else if (t < 7.0) {
        color = trilogo_animation(sq_uv * 0.6 + vec2(0.5), t - 2.0);
    } else if (t < 7.2) {
        color = mix(bg, black, smoothstep(7.0, 7.2, t));
    } else if (t < 8.2) {
        color = mix(black, ps_logo(sq_uv), smoothstep(7.2, 8.2, t));
    } else if (t < 13.2) {
        color = ps_logo(sq_uv);
    }

    // fade black to white 2s
    // tri logo appears
    // halves animate 1s
    // hold 4s
    // (fast fade) black and tri logo disappears
    // ps logo fades in 1s
    // hold 5s
    // ps logo cuts out
    // hold black 2s
    // repeat

    // vec3 logo = trilogo_animation(sq_uv * 0.6 + vec2(0.5), iTime);
    vec3 logo_ps = ps_logo(sq_uv);

    // Output to screen
    fragColor = vec4(color, 1.0);
}

