// Implements the Box-Muller transform
// to generate normally distributed random numbers
vec2 nrand2(float sigma, vec2 mean)
{
	vec2 Z = clamp(rand2(), 0.01, 1.0);
    return mean + sigma * sqrt(-2.0 * log(Z.x)) * 
           vec2(cos(TWO_PI * Z.y),sin(TWO_PI * Z.y));
}

vec4 nrand4(float sigma, vec4 mean)
{
	vec4 Z = clamp(rand4(), 0.01, 1.0);
    return mean + sigma * sqrt(-2.0 * log(Z.xxyy)) * 
           vec4(cos(TWO_PI * Z.z),sin(TWO_PI * Z.z),cos(TWO_PI * Z.w),sin(TWO_PI * Z.w));
}

float gpdf(vec3 delta, vec3 d, float R)
{
    float ddot = dot(delta, d);
    float ddot2 = ddot*ddot;
    float sigma2 = R*R;

    //probability distribution function(unbiased)
    return (1.0/TWO_PI)*(ddot2/sigma2 + 1.0)*exp( (ddot2 - dot(delta, delta))/(2.0 * sigma2) );
}

//sample random direction from a point to a 3D normal distribution
vec3 gdir(in vec3 p0, in vec3 N, in vec3 p1, in float R, out float pdf)
{
    //sample random point from the cloud
    vec3 rn = nrand4(R, vec4(p1,0)).xyz;

    //random direction
    vec3 d = normalize(rn - p0);
    //reflect to the positive hemisphere
    d *= sign(dot(N,d));

    vec3 delta = p0 - p1;

    pdf = gpdf(delta, d, R);

    return d;
}

//sample uniform direction in a hemisphere
vec3 udir(vec3 N, out float pdf)
{
    //sample random point from the cloud
    vec3 rn = normalize(nrand4(1.0, vec4(0.0)).xyz);

    pdf = 1.0/TWO_PI;

    //random direction
    return rn * sign(dot(N, rn));
}


