//scene

float sample_light(vec3 r)
{
    vec3 ldir = normalize(vec3(0.,0., 1.0));
    return 500.*step(0.996, dot(r, ldir));
}

//wavy surface
float map(vec3 p)
{
    return 0.7*(4.5-p.z+0.6*cos(length(p.xy)) - 0.6*sin(0.5*distance(p.xy, vec2(30,10)))
                + 0.6*sin(0.8*p.x+10.*mouse.x/R.x) + 0.4*cos(0.3*p.x + 0.6*p.y - 10.*mouse.y/R.y) +
                 + 0.4*cos(-0.6*p.x + 0.1*p.y) + 0.7*cos(-0.3*p.x - 0.5*p.y)
                 + 0.2*cos(2.0*p.x - 0.2*p.y) + 0.1*cos(1.7*p.x - 2.2*p.y));
                 //+ 0.07*cos(4.0*p.x + 3.2*p.y) + 0.03*cos(0.1*p.x - 5.2*p.y));
}

vec4 grad(vec3 p, float dx) 
{
	const vec3 k = vec3(1,-1,0);
	return  (k.xyyx*map(p + k.xyy*dx) +
			 k.yyxx*map(p + k.yyx*dx) +
			 k.yxyx*map(p + k.yxy*dx) +
			 k.xxxx*map(p + k.xxx*dx))/4.;
}


vec3 ray_march(inout vec3 p, in vec3 d)
{
    for(int i = 0; i < 32; i++)
    {
        float DE = map(p);
        p += DE*d;
    }
    return normalize(grad(p, 0.01).xyz);
}

//refraction index wavelength dependence 
float dispersion(float w)
{
    //return 2.8;
    return 8.0 - 8.0*(w - 300.)/500.;
}

float trace(in vec3 p, in vec3 d, float w)
{
    vec3 N = ray_march(p,d);
    vec3 d1 = refract(d, N, dispersion(w));
    return sample_light(d1);
}


