//UPDATE THE APPROXIMATED LIGHT DISTRIBUTION
//n^2 gaussian distributions
//n*n pixel cell

vec4 cell[cell_L];
int local;

void load_distribution(vec2 p, out float wsum, out float sigma, out vec3 mean)
{
    vec4 data = ch1(p);
    wsum = data.w;
    mean = normalize(data.xyz);
    sigma = length(data.xyz);
}

vec4 load_neigh(vec2 p, int d, out float wsum)
{
    vec2 x = vec2(d/cell_S, d%cell_S);
    vec3 mean; float sigma;
    load_distribution(p + x, wsum, sigma, mean);
    return vec4(mean, sigma);
}

void load_cell(vec2 p)
{
    vec2 ci = cell_size*floor(p/cell_size);
    vec2 cd = floor(p) - ci;
    local = int(cd.y*cell_size + cd.x);
    float wsum;
    for(int i = 0; i<cell_L; i++)
        cell[i] = load_neigh(ci, i, wsum);
}

//reject all samples that do not fall into this distribution
float rejection(vec3 d)
{
    //distance rejection
    int id = 0;
    float dmin = 1e10;
    float dlocal = 0.;
    for(int i = 0; i<cell_L; i++)  
    {
        float d = distance(cell[i].xyz, d);
        if(i == local) dlocal = d;
        if(d < dmin) {id = i; dmin = d;}
    }

    return step(1.0,dmin/dlocal);


    //probability rejection
    //(is not stable)

    /*float p0 = 0.;
    float p1 = 0.;
    for(int i = 0; i<4; i++)  
    {
        float p = gpdf(d, cell[i].xyz, cell[i].w);
        if(i == local) p1 += p;
        else p0 += p;
    }
    return smoothstep(0.3, 3.0, 3.*p1/p0);
    */
}

void mainImage( out vec4 c, in vec2 p )
{
    INIT;
    INIT_RNG;
    load_cell(p);

    //light distribution
    float wsum; vec3 mean; float sigma;

    //load
    load_distribution(p, wsum, sigma, mean);

    //neighbor cell blur
    float wsum0, sigma0; vec3 mean0;
    float sigmaB = sigma*wsum; vec3 meanB = mean*wsum; float wsumB = wsum;
    vec2 deltas[4] = vec2[4](vec2(1,0),vec2(-1,0),vec2(0,1),vec2(0,-1));

    for(int i = 0; i < 4; i++)
    {
        load_distribution(p + cell_size*deltas[i], wsum0, sigma0, mean0);
        wsum0 *= spacial_blur;
        sigmaB += sigma0*wsum0; meanB += mean0*wsum0; wsumB += wsum0; 
    }

    sigma = sigmaB/wsumB; mean = meanB/wsumB; 

    //loss 
    wsum *= (1. - temporal_loss);

    vec2 ci = cell_size*floor(p/cell_size);
    //update light distribution using local pixels
    float sigma2 = sigma*sigma*wsum;
    vec3 mean1 = mean*wsum;
    range(i, -1, cell_S) range(j, -1, cell_S)
    {
        vec2 delta = vec2(i, j);

        vec4 cur_sample = ch0(ci + delta);

        float pdf = length(cur_sample.xyz); 
        float s = cur_sample.w;
        vec3 d = normalize(cur_sample.xyz);

        vec3 dx = d - mean;
        float rej = rejection(d);
        float w = rej*s;
        //update sigma
        sigma2 += dot(dx,dx)*w;
        //update mean
        mean1 += d*w;

        //weight sum
        wsum += w;
    }

    mean = normalize(mean1);
    sigma = sqrt(sigma2/wsum);



    if(iFrame < 1 || isnan(sigma) || isinf(sigma) || isnan(mean.x))
    {
       //default light distribution
       sigma = 1.0;
       wsum = 1.;
       float p;
       mean = normalize(vec3(0,0,1) + udir(vec3(0,0,1), p));
    }

    c = vec4(mean*sigma, wsum);
}

