float NoiseSeed;
float randomFloat(){
  NoiseSeed = sin(NoiseSeed) * 84522.13219145687;
  return fract(NoiseSeed);
}

float SCurve (float value, float amount, float correction) {

	float curve = 1.0; 

    if (value < 0.5)
    {

        curve = pow(value, amount) * pow(2.0, amount) * 0.5; 
    }

    else
    { 	
    	curve = 1.0 - pow(1.0 - value, amount) * pow(2.0, amount) * 0.5; 
    }

    return pow(curve, correction);
}



//Chromatic Abberation from: https://www.shadertoy.com/view/XlKczz
vec3 chromaticAbberation(sampler2D tex, vec2 uv, float amount)
{
    float aberrationAmount = amount/10.0;
   	vec2 distFromCenter = uv - 0.5;

    // stronger aberration near the edges by raising to power 3
    vec2 aberrated = aberrationAmount * pow(distFromCenter, vec2(3.0, 3.0));

    vec3 color = vec3(0.0);

    for (int i = 1; i <= 8; i++)
    {
        float weight = 1.0 / pow(2.0, float(i));
        color.r += texture(tex, uv - float(i) * aberrated).r * weight;
        color.b += texture(tex, uv + float(i) * aberrated).b * weight;
    }

    color.g = texture(tex, uv).g * 0.9961; // 0.9961 = weight(1)+weight(2)+...+weight(8);

    return color;
}




//film grain from: https://www.shadertoy.com/view/wl2SDt
vec3 filmGrain()
{
    return vec3(0.9 + randomFloat()*0.15);
}




//Sigmoid Contrast from: https://www.shadertoy.com/view/MlXGRf
vec3 contrast(vec3 color)
{
    return saturate(vec3(
        SCurve(color.r, 3.0, 1.0), 
        SCurve(color.g, 4.0, 0.7), 
        SCurve(color.b, 2.6, 0.6)
    ));
}




//anamorphic-ish flares from: https://www.shadertoy.com/view/MlsfRl
vec3 flares(sampler2D tex, vec2 uv, float threshold, float intensity, float stretch, float brightness)
{
    threshold = 1.0 - threshold;

    vec3 hdr = texture(tex, uv).rgb;
    hdr = vec3(floor(threshold+pow(hdr.r, 1.0)));

    float d = intensity; //200.;
    float c = intensity*stretch; //100.;
    mat2 rot = rotate(30.0);

    //horizontal
    for (float i=c; i>-1.0; i--)
    {
        float texL = texture(tex, uv+vec2(i/d, 0.0) * rot).r;
        float texR = texture(tex, uv-vec2(i/d, 0.0) * rot).r;
        hdr += floor(threshold+pow(max(texL,texR), 4.0))*(1.0-i/c);
    }


    hdr *= vec3(0.678,0.804,1.000); //tint

	return hdr*brightness;
}



//mip map bloom: https://www.shadertoy.com/view/Ms2Xz3
const float Threshold = 0.5;
const float Intensity = 2.0;
const float BlurSize = 2.0;

vec3 mipMapBloom(vec3 Screen, in sampler2D Tex, in vec2 Coord, in float MipBias)
{
	vec2 TexelSize = MipBias/iChannelResolution[0].xy;

    vec3  Color = texture(Tex, Coord, MipBias).rgb;
    Color += texture(Tex, Coord + vec2(TexelSize.x,0.0), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,0.0), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(0.0,TexelSize.y), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(0.0,-TexelSize.y), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(TexelSize.x,TexelSize.y), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,TexelSize.y), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(TexelSize.x,-TexelSize.y), MipBias).rgb;    	
    Color += texture(Tex, Coord + vec2(-TexelSize.x,-TexelSize.y), MipBias).rgb;    

    Color = Color/9.0;
    vec3 Highlight = clamp(Color-Threshold,0.0,1.0)*1.0/(1.0-Threshold);
    return saturate(1.0-(1.0-Screen)*(1.0-Highlight*Intensity));
}




//from: https://www.shadertoy.com/view/4l2GWm
vec3 quincunxAA(sampler2D tex, vec2 fragCoord, float blur)
{
	vec3 pixelColor;
	pixelColor =  texture(tex, (fragCoord + vec2( 0.0, 0.0)) / RES).rgb / 2.0;
	pixelColor += texture(tex, (fragCoord + vec2( blur, blur)) / RES).rgb / 8.0;
	pixelColor += texture(tex, (fragCoord + vec2( blur,-blur)) / RES).rgb / 8.0;
	pixelColor += texture(tex, (fragCoord + vec2(-blur,-blur)) / RES).rgb / 8.0;
	pixelColor += texture(tex, (fragCoord + vec2(-blur, blur)) / RES).rgb / 8.0;
	return pixelColor;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/RES;
    #ifdef MARGIN
    if(uv.y < marginSize || uv.y > 1.0-marginSize){ fragColor = vec4(0.0); return; }
    #endif

    vec3 color = quincunxAA(iChannel0, fragCoord, 2.0);


    color = chromaticAbberation(iChannel0, uv, 0.8);
    color *= filmGrain();
    color = mipMapBloom(color, iChannel0, uv, BlurSize);
    color += contrast(flares(iChannel0, uv, 0.9, 200.0, 0.8, 0.04));



    fragColor = vec4(color, 1.0);

}

