        vec3 p = eye + dir * depth.w;

        vec4 dist = scene(p);
        if (dist.w < EPSILON * length(p)) {
			return vec4(saturate(dist.xyz), depth.w);
        }
        depth.w += dist.w;
        if (depth.w > MAX_DIST) {
            return MAX_VEC;
        }
    }
    return MAX_VEC;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    #ifdef MARGIN
    if(uv.y < (marginSize-0.02) || uv.y > 1.0-(marginSize-0.02)){ fragColor = vec4(0.0); return; }
    #endif

    //vec3 eye = rotateCamera(200.0, iMouse.xy/iResolution.xy, iMouse); //uncomment this

    vec3 eye = vec3(10.0, -8.0, 0.0);
    vec3 lookAt = vec3(-2.0, -2.0, 100.0);

    mat4 viewToWorld = viewMatrix(eye, lookAt, vec3(0.0, 1.0, 0.0));
    vec3 dir = rayDirection(25.0, iResolution.xy, fragCoord);

    vec3 worldDir = (viewToWorld * vec4(dir, 0.0)).xyz;

    vec3 col = vec3(0);

    vec4 dist = raymarch(eye, worldDir);
    col = vec3(dist.xyz + (dist.w/MAX_DIST)*vec3(0.996,0.820,1.000));


    // Output to screen
    fragColor = vec4(saturate(col),1.0);
}

