{ if (d1.x<d2.x) return d1; else return d2; }

vec2 opS2( vec2 d1, vec2 d2 )
{ if  (-d1.x<d2.x) return d1; else  return d2; }

vec2 opI2( vec2 d1, vec2 d2 ) 
{  if (d1.x>d2.x)	return d1; else return d2;  }

vec2 opS(vec2 d1, vec2 d2) 
{ return vec2(max(-d2.x, d1.x), 2.0); }

vec2 opRep2D( in vec2 p, in vec2 c )
{ vec2 q = mod(p+0.5*c,c)-0.5*c; return  q; }

float sdCirc(vec2 p, float r)
{ return length(p)-r; }

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0); }


vec2 BloqueSimple(vec2 p)
{ 
    vec2 resp=vec2(9999.9,-1.0);
    float b1= sdBox(p-vec2(0.0), vec2(0.4,0.2) );
    float b1v= sdBox(p-vec2(0.0,0.19), vec2(0.4,0.02) );
    float b1h= sdBox(p-vec2(-0.38,0.0), vec2(0.02,0.2) );
    b1=opS(b1v, b1);
    b1=opS(b1h, b1);
    resp=opU2(vec2(b1, 38.0), resp);
    resp=opU2(vec2(b1v, 1.0), resp);
    resp=opU2(vec2(b1h, 1.0), resp);
    return resp;
}

vec2 nubes(vec2 p)
{   vec2 resp=vec2(9999.9,-1.0);
    float c1= sdCirc(p-vec2(0.0,0.05), 0.105);
    float c2= sdCirc(p-vec2(0.15,0.02), 0.065);
    float c3= sdCirc(p-vec2(-0.15,0.02), 0.065);
    float b1=sdBox(p-vec2(0.0,-0.05), vec2(0.24,0.045 ) );
    float ctodo;
    ctodo=opU(c1,c2);
    ctodo=opU(c3,ctodo);
    ctodo=opS(b1, ctodo);
    resp=opU2(vec2(ctodo,1.0 ),resp);
	return resp;

}


vec2 PastoTrapezoid(vec2 p)
{   vec2 resp=vec2(9999.9,-1.0);
     p=rotate2d(p-vec2(0.0,-0.73), radians(180.0));
    float sdt1= sdTrapezoid( p, 0.01, 0.15, 0.15 );
    resp=opU2(vec2(sdt1,12.0 ),resp);
	return resp;
}


vec2 LLuvia(vec2 p)
{   vec2 resp=vec2(9999.9,-1.0);
    p=rotate2d(p-vec2(0.0,-0.73), radians(140.0));
    vec2 c =vec2(0.5,0.5 );
    vec2 pp=opRep2D(p-vec2(0.0,iTime), c);
    float sb1= sdBox( pp,vec2(0.005,0.03) );
    resp=opU2(vec2(sb1,0.0 ),resp);
	return resp;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.*fragCoord-iResolution.xy)/iResolution.y;
    //p*= 4.0;
    p.x+=iTime;
	vec3 col = vec3(0.280,0.320,1.0);

    vec2 pp=p*4.0;
    vec2 pp1=p*0.5;
    vec2 pp4=p*0.35;

    vec2 c=vec2(0.83,11.0);
    vec2 p1= opRep2D(pp,c  );
    vec2 c2=vec2(0.83,10.0);
    vec2 p2= opRep2D(pp1,c2  );
    vec2 c3=vec2(0.83,15.0);
    vec2 p3= opRep2D(pp1-vec2(0.2,0.0),c2  );
    vec2 c4=vec2(0.83,15.0);
    vec2 p4= opRep2D(pp4-vec2(-0.3,0.2),c2  );
    vec2 c5=vec2(0.3,11.0);
    vec2 p5= opRep2D(p,c5  );

    float ypos=-3.8;

    vec2 b2=BloqueSimple(p1-vec2(0.0,ypos));
