//--- Rikka 2
// by Catzpaw 2020

//Buffer A:animation

//SETUP
#define OCT 5
#define ITER 170
#define EPS 7e-3
#define NEAR 0.1
#define FAR 13.0

float time;

//SNIPPETS
#define ZERO min(0,iFrame)
float hash(float v){return fract(sin(v*22.9)*67.);}
mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,s,-s,c);}

//SDF
vec2 hexFold(vec2 p){return abs(abs(abs(p)*mat2(.866,.5,-.5,.866))*mat2(.5,-.866,.866,.5));}
float sdHex(vec3 p){p=abs(p);return max(p.z-.02,max((p.x*.5+p.y*.866),p.x)-.015);}

//SCENE
float map(vec3 p){
	float h=hash(floor(p.x)+floor(p.y)*133.3+floor(p.z)*166.6),o=FAR,s=1.+h;
	p=fract(p)-.5;
    p.y+=h*.4-.2;
    p.xz*=rot(time*(h+.8));
    p.yz*=rot(time+h*5.);
    h=hash(h);p.x+=h*.15;
    float l=dot(p,p);
    if(l>.1)return l*2.;
 	for(int i=ZERO;i<OCT;i++){
		p.xy=hexFold(p.xy);
		p.xy*=mat2(.866,-.5,.5,.866);
		p.x*=(s-h);
		h=hash(h);p.y-=h*.065-.015;p.y*=.8;
        p.z*=1.2;
		h=hash(h);p*=1.+h*.3;
		o=min(o,sdHex(p));
        h=hash(h);s=1.+h*2.;
    }
	return o;
}

//RAYMARCHING
float trace(vec3 ro,vec3 rd,out vec3 n){
	float t=NEAR,d;
    float eps;
	vec3 p;
	vec2 e=vec2(.1,-.1);
	for(int i=ZERO;i<ITER;i++){
        float l=log(t+1.);
        eps=EPS*l;
		p=ro+rd*t;
		d=map(p);
		if(d<eps||t>FAR)break;
		t+=step(d,.3)*d*.2+d*l*.16;
	}
	n=normalize(e.xyy*map(p+e.xyy*eps)+e.yyx*map(p+e.yyx*eps)+e.yxy*map(p+e.yxy*eps)+e.xxx*map(p+e.xxx*eps));
	return min(t,FAR);
}

//BACKGROUND
vec3 tx0(vec3 rd){float v=abs(dot(rd,vec3(.35,-.5,.35)));return vec3(v);}
//vec3 tx0(vec3 rd){return texture(iChannel0,rd).rgb*.4;}

//MAIN
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    time=iTime+117.7;
	vec2 uv=(fragCoord.xy-.5*iResolution.xy)/iResolution.y;
	vec3 n,ro=vec3(-.9,time*.4,-.9),rd=normalize(vec3(uv*.7,1.));
    rd.yz*=rot(.45);
    rd.xz*=rot(.78);
    float d=trace(ro,rd,n);
	float v=1.-d*(1./FAR),s=pow(1.-abs(dot(n,normalize(-rd))),5.);
    v=v>.01?v:0.;
    vec3 c=vec3(.0,.1,.2)+tx0(rd)*.5;
    c+=v>.01?(tx0(n)+s)*v:vec3(0);

	fragColor=vec4(c,d/FAR);
}

