//FM INSTRUMENTS (freq,time)
float fmbass(float f,float x){f*=6.283;return fbdist(sin(f*2.03)*.5+sin(f*2.+sin(f+sin(f*7.51)*env_d(x,.07))*env_d(x,.2)),1.,.02)*env_d(x,.5);}
float fmharpsi(float f,float x){f*=6.283;return (sin(f+sin(f*5.+sin(f*9.))))*env_d(x,.7);}
//float fmvibe(float f,float x){f*=6.283;return (sin(f+sin(f*9.)))*env_d(x,.9);}
float fmep(float f,float x){f*=6.283;return (sin(f+sin(f*15.)*env_d(x,.5)*.6))*env_d(x,.7);}
//float fmmarimba(float f,float x){f*=6.283;return (sin(f+sin(f*3.99+sin(f*5.3)*env_d(x,.08))*env_d(x,.3)))*env_d(x,.4);}
//float fmguitar(float f,float x){f*=6.283;return fbdist(sin(f+sin(f*3.01+sin(f*3.49)*env_d(x,.4))*env_d(x,.7)*.6)*1.3*env_d(x,.9),1.,1.);}
float fmpad(float f,float x){f*=6.283;return sin(f+sin(f*1.01))*(.9+sin(x*60.)*.1);}

//PERCUSSION INSTRUMENTS (time)
float bd(float x){return sin(x*freq(84./(1.+x*.7)))*env_d(x,.5);}
float sd(float x){return (osc_nesnoise(x,11)*.7*env_d(x,.2)+sin(x*freq(81.5/(1.+x*.05)))*env_d(x,.25));}
//float hh(float x,float d){return osc_nesnoise(x,3)*env_d(x,d);}

//================================================================================
// SEQUENCER
//--------------------------------------------------------------------------------
//BIT OPERATION
int imod(int x,int y){return int(x-y*(x/y));}
int get12(uint b,int l){return int(b>>l*4)&4095;}
int get8(uint b,int l){return int(b>>l*4)&255;}
int get4(uint b,int l){return int(b>>l*4)&15;}
int get2(uint b,int l){return int(b>>l*2)&3;}
int get3(int b,int p){return int(b>>p)&7;}

//GET NOTE FROM SCALE
int degree=0;uint scale=0u;	//internal use
int scaleNote(int y){int n=y>6?12:0;return y<-50?-99:key+int((scale>>((y+56)%7)*4)&15u)+n;}

//GET PATTERN FROM SEQUENCE
float mp=0.; //note length multiplier(from speed)
int   to=0;  //top note for auto inversion
uint  pt=0u; //pattern now playing
int   st=0;  //step now playing
const vec4 spd=vec4(16.,32.,4.,8.);
void getPattern(float t,uint sq){
    bool di=int(mod(t*bpm*16./steps,16.))<get4(sq,6);
    to=di?get4(sq,5):get4(sq,2);
    pt=di?pat[get8(sq,3)]:pat[get8(sq,0)];
    mp=spd[di?get2(sq,15):get2(sq,14)];
    st=int(mod(t*bpm*mp/steps,16.));
}

//GET CHORD SEQUENCE
uint scop(uint sc,int p,int op){p*=4;int n=int(sc&(15u<<p));sc^=uint(n);n>>=p;n+=op;n=n<0?n+12:n;return sc|uint((n&15)<<p);}
uint scset(uint sc,int p,int b){p*=4;int n=int(sc&(15u<<p));sc^=uint(n);return sc|uint(b<<p);}
ivec4 seven=ivec4(7,1,5,6);
void scaleChord(float t,float l,uint p){
    int st=int(mod(t*bpm*4./steps,4.)),d=get2(p,15),sc=st<d?0:get2(p,14),ch=st<d?get8(p,4):get8(p,2);
    d+=get2(p,13);sc=st<d?sc:get2(p,12);ch=st<d?ch:get8(p,0);
    scale=scl[sc];
    degree=get3(ch,0);degree-=degree>0?1:0;
    int c35=get3(ch,3);
    scale=scop(scale,(degree+2)&7,(c35&2)>0?(c35&1)*2-1:0);
    scale=scop(scale,(degree+4)&7,(c35&4)>0?(c35&1)*2-1:0);
    int c7=(seven[get2(uint(ch),3)]+degree)%7;
    scale=scset(scale,(degree+6)&7,int(scl[sc]>>c7*4)&15);
}

//GET NOTE FROM PATTERN
int notegen(int s,int s1,int s2){
    return fract(sin(float(s+s1)+float(s1*20)*gateSeed)*50.)<(float(get4(pt,6))*.0625)?-99:
        imod(int(abs(sin(float(s+s1+s*s2)*noteSeed)*float(s2))),get4(pt,7)+1);
}
vec2 getNote(float t,float l){
    int s1=get12(pt,3),s2=get12(pt,0),n=notegen(st,s1,s2);float x=mod(t,l/mp);
    if((st>=1)&&(n<-50)){n=notegen(st-1,s1,s2);x+=l/mp;}
    n+=n<-50?0:degree+7;n-=n>16-to?7:0;
    return (n<-50)?vec2(x,-99):vec2(x,scaleNote(n));
}
float getChord(int n){
    n+=degree+7;n-=n>16-to?7:0;
    return float(scaleNote(n));
}
vec2 getNoteByChord(float t,float l){
    int s1=get12(pt,3),s2=get12(pt,0),n=notegen(st,s1,s2);n-=n&1;float x=mod(t,l/mp);
    if((st>=1)&&(n<-50)){n=notegen(st-1,s1,s2);x+=l/mp;n-=n&1;}
    n+=n<-50?0:degree+7;n-=n>16-to?7:0;
    return (n<-50)?vec2(x,-99):vec2(x,scaleNote(n));
}
vec2 getGate(float t,float l){
    int n=get2(pt,st);float x=mod(t,l/mp);
    if((st>=1)&&(n==0)){n=get2(pt,st-1);x+=l/mp;}
    return vec2(x,n);
}

//================================================================================
// RENDERER
//--------------------------------------------------------------------------------
// track(t,l,s)
//   t...time
//   l...measure length (steps/bpm)
//   s...step now playing
//
//   getPattern(t,patternSequence)...get pattern data now playing
//   getGate(t,l)...get noteTime and noteNum from percussive pattern
//   getNote(t,l)...get noteTime and noteNum from melodic pattern
//   getNoteByChord(t,l)...noteNum will restricted by chord
//   getChord(n)...get chord noteNum 0:root 2:3rd 4:5th 6:7th
//
// render(t,l,inout o,e)
//   o...audio output
//   e...false:render is called by delay effect
//
//   scaleChord(t,l,chordSequence)...set scale & chord for following tracks
//
//--------------------------------------------------------------------------------
//TRACK 1 snaredrum,closehh,openhh
vec2 track1(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq1[imod(s,len1)]);
    vec2 n=getGate(t,l);
    o+=(n.y==1.)?sd(n.x)*vec2(.48,.62):vec2(0);
    o+=(n.y==2.)?neshh(n.x,.08)*vec2(.15,.24):vec2(0);
    o+=(n.y==3.)?neshh(n.x,.2)*vec2(.15,.24):vec2(0);
    return o;
}
//TRACK 2 bassdrum,lowconga,highconga
vec2 track2(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq2[imod(s,len2)]);
    vec2 n=getGate(t,l);
    o+=(n.y==1.)?bd(n.x)*vec2(.4,.55):vec2(0);
    o+=(n.y==2.)?neslc(n.x)*vec2(.05,.15):vec2(0);
    o+=(n.y==3.)?neshc(n.x)*vec2(.05,.15):vec2(0);
    return o;
}
//TRACK 3 bass
vec2 track3(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq3[imod(s,len3)]);
    vec2 n=getNoteByChord(t,l);
    o+=(n.y>-50.)?(fmbass(freq(n.y+11.95)*n.x,n.x)+fmbass(freq(n.y+12.05)*n.x,n.x))*vec2(.28,.31):vec2(0);
    return o;
}
//TRACK 4 chord
vec2 track4(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq4[imod(s,len4)]);
    vec2 n=getNote(t,l);
    o+=(n.y>-50.)?fmpad(freq(n.y+36.03)*n.x,n.x)*vec2(.29,.21):vec2(0);
    o+=(n.y>-50.)?fmpad(freq(getChord(2)+48.02)*n.x,n.x)*vec2(.30,.20):vec2(0);
    o+=(n.y>-50.)?fmpad(freq(getChord(4)+36.01)*n.x,n.x)*vec2(.31,.19):vec2(0);
    o+=(n.y>-50.)?fmpad(freq(getChord(6)+48.01)*n.x,n.x)*vec2(.32,.18):vec2(0);
    return o;
}
//TRACK 5 accompaniment
vec2 track5(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq5[imod(s,len5)]);
    vec2 n=getNote(t,l);
    o+=(n.y>-50.)?fmharpsi(freq(n.y+72.)*n.x,n.x)*vec2(.08,.05):vec2(0);
    return o;
}
//TRACK 6 melody
vec2 track6(float t,float l,int s){
    vec2 o=vec2(0);getPattern(t,seq6[imod(s,len6)]);
    vec2 n=getNote(t,l);
    o+=(n.y>-50.)?fmep(freq(n.y+36.03)*n.x,n.x)*.4:0.;
    return o;
}
void render(float t,float l,inout vec2 o,bool e){
    int s=int(t/l); //sequence number now playing
    scaleChord(t,l,chordseq[imod(s,chordlen)]); //set scale & chord
    o+=e?track1(t,l,s):track1(t,l,s)*.5; //TRACK 1 snaredrum,closehh,openhh
    o+=e?track2(t,l,s):vec2(0); //TRACK 2 bassdrum,lowconga,highconga
    o+=e?track3(t,l,s):vec2(0); //TRACK 3 bass
    o+=  track4(t,l,s);         //TRACK 4 chord
    o+=  track5(t,l,s);         //TRACK 5 accompaniment
    o+=  track6(t,l,s);         //TRACK 6 melody
}

//================================================================================
// MASTER
//--------------------------------------------------------------------------------
#define MASTER_DRIVE    0.35
#define MASTER_PRESSURE 1.05
#define DELAY_REPEAT    12
#define DELAY_WET       0.4
#define DELAY_FEEDBACK  0.84
#define DELAY_TIME      0.11
#define DELAY_STEREO    0.08
vec2 mainSound(in int Samp,float time){
    float l=steps/bpm,vol=1.,d=DELAY_WET,r=DELAY_TIME;
    vec2 o=vec2(0);
    if(time>sec-fade){vol=(sec-min(time,sec))/fade;}
    time-=start*60./bpm;
    if(time<0.)return o;
    render(time,l,o,true);
    float ff=0.,p=0.;
    for(int i=0;i<DELAY_REPEAT;i++){
        time-=r;
        if(time<0.)continue;
        vec2 a=vec2(0);
        render(time,l,a,false);
        d*=DELAY_FEEDBACK;
        o+=a*d*vec2(1.+p*ff,1.-p*ff);
        ff=ff<0.?1.:-1.;
        p+=DELAY_STEREO;
    }
    return amp(o,vec3(MASTER_DRIVE,MASTER_PRESSURE,vol));
}

