//---------------------------------------------------------------

vec4 sdBox( vec3 p, vec3 b ) // distance and normal
{
    vec3 d = abs(p) - b;
    float x = min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
    vec3  n = step(d.yzx,d.xyz)*step(d.zxy,d.xyz)*sign(p);
    return vec4( x, n );
}

mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);

}

vec4 fbmd( in vec3 x )
{
    const float scale  = 1.5;

    float a = .5;
    float b = .5;
	float f = 2.0;
    vec3  d = vec3(0.0);
    for( int i=0; i<14; i++ )
    {
        vec3 r = f*x*scale;
        // i don't know how to get good sine fbms without rotations
        r.xy *= rot(float(f*f));
        r.yz *= rot(float(f*f*f));
        vec4 n = noised(r);
        // this undoes(?!) the rotation for the derivatives
        n.zw = rot(float(f*f*f)) * n.zw;
        n.yz = rot(float(f*f)) * n.yz;

        a += b*n.x;           // accumulate values		
        d += b*n.yzw*f*scale; // accumulate derivatives
        b *= .66;             // amplitude decrease
        f *= 1.4;             // frequency increase
    }

	return vec4( a, d );
}

vec4 map( in vec3 p )
{
	vec4 d1 = fbmd( p );
    d1.x -= 0.37;
	d1.x *= 0.7;
    d1.yzw = normalize(d1.yzw);

    // clip to box
    vec4 d2 = sdBox( p, vec3(1.5) );
    return (d1.x>d2.x) ? d1 : d2;
}

// ray-box intersection in box space
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );
	if( tN > tF || tF < 0.0) return vec2(-1.0);
	return vec2( tN, tF );
}

// raymarch
vec4 interesect( in vec3 ro, in vec3 rd )
{
	vec4 res = vec4(-1.0);

    // bounding volume    
    vec2 dis = iBox( ro, rd, vec3(1.5) ) ;
    if( dis.y<0.0 ) return res;

    // raymarch
    float tmax = dis.y;
    float t = dis.x;
	for( int i=0; i<128; i++ )
	{
        vec3 pos = ro + t*rd;
		vec4 hnor = map( pos );
        res = vec4(t,hnor.yzw);

		if( hnor.x<0.001 ) break;
		t += hnor.x * .6;
        if( t>tmax ) break;
	}

	if( t>tmax ) res = vec4(-1.0);
	return res;
}

// compute normal numerically
#ifdef SHOW_NUMERICAL_NORMALS
vec3 calcNormal( in vec3 pos )
{
	vec2 eps = vec2( 0.0001, 0.0 );
	vec3 nor = vec3( map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	                 map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	                 map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}
#endif

// fibonazzi points in s aphsre, more info:
// http://lgdv.cs.fau.de/uploads/publications/spherical_fibonacci_mapping_opt.pdf
vec3 forwardSF( float i, float n) 
{
    const float PI  = 3.141592653589793238;
    const float PHI = 1.618033988749894848;
    float phi = 2.0*PI*fract(i/PHI);
    float zi = 1.0 - (2.0*i+1.0)/n;
    float sinTheta = sqrt( 1.0 - zi*zi);
    return vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, zi);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
    for( int i=0; i<32; i++ )
    {
        vec3 ap = forwardSF( float(i), 32.0 );
        float h = hash(float(i));
		ap *= sign( dot(ap,nor) ) * h*0.25;
        ao += clamp( map( pos + nor*0.001 + ap ).x*3.0, 0.0, 1.0 );
    }
	ao /= 32.0;

    return clamp( ao*5.0, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

	// camera anim
    float an = 0.1*iTime;
	vec3 ro = 3.0*vec3( cos(an), 0.8, sin(an) );
	vec3 ta = vec3( 0.0 );

    // camera matrix	
	vec3  cw = normalize( ta-ro );
	vec3  cu = normalize( cross(cw,vec3(0.0,1.0,0.0)) );
	vec3  cv = normalize( cross(cu,cw) );
	vec3  rd = normalize( p.x*cu + p.y*cv + 1.7*cw );

	// render
	vec3 col = vec3(0.0);
    vec4 tnor = interesect( ro, rd );
	float t = tnor.x;

    if( t>0.0 )
	{
		vec3 pos = ro + t*rd;
        #ifndef SHOW_NUMERICAL_NORMALS
        vec3 nor = tnor.yzw; // no need to call calcNormal( pos );
        #else
        vec3 nor = calcNormal( pos );
        #endif
        float occ = calcAO( pos, nor );
        float fre = clamp( 1.0+dot(rd,nor), 0.0, 1.0 );
        float fro = clamp( dot(nor,-rd), 0.0, 1.0 );
        col = mix( vec3(0.05,0.2,0.3), vec3(1.0,0.95,0.85), 0.5+0.5*nor.y );
        //col = 0.5+0.5*nor;
        col += 10.0*pow(fro,12.0)*(0.04+0.96*pow(fre,5.0));
        col *= pow(vec3(occ),vec3(1.0,1.1,1.1) );
	}

    col = sqrt(col);

    fragColor=vec4(col,1.0);
}

#define SIN_15 0.2588190451
#define COS_15 0.96592582628

float shape(in vec3 p) {
    return max(mod(length(p) - 2.0, 1.0) - 0.5, length(p) - (0.6 + 0.4 * sin(iTime)) * 5.0) - 0.01;
}

float scene(in vec3 p) {
    vec3 res = vec3(1.0, 1.0, 1.0);

    vec3 cell = floor(p / res) * res;
    vec3 local = fract(p / res);

    float ldb = shape(cell);                           // Left, Down, Back
    float rdb = shape(cell + vec3(res.x, 0.0, 0.0));   // Right, Down, Back
    float ldf = shape(cell + vec3(0.0, 0.0, res.z));   // Left, Down, Front
    float rdf = shape(cell + vec3(res.x, 0.0, res.z)); // Right, Down, Front
    float lub = shape(cell + vec3(0.0, res.y, 0.0));   // Left, Up, Back
    float rub = shape(cell + vec3(res.xy, 0.0));       // Right, Up, Back
    float luf = shape(cell + vec3(0.0, res.yz));       // Left, Up, Front
    float ruf = shape(cell + res);                     // Right, Up, Front

    return mix(mix(mix(ldb, rdb, local.x),
                   mix(ldf, rdf, local.x),
                   local.z),

               mix(mix(lub, rub, local.x),
                   mix(luf, ruf, local.x),
                   local.z),

               local.y);
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(scene(p + vec3(0.001, 0.0, 0.0)) - scene(p - vec3(0.001, 0.0, 0.0)),
                          scene(p + vec3(0.0, 0.001, 0.0)) - scene(p - vec3(0.0, 0.001, 0.0)),
                          scene(p + vec3(0.0, 0.0, 0.001)) - scene(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 8.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    float dt = 0.0;

    for (int iter=0; iter < 250; iter++) {
        vec3 p = ro + rd * dt;
        vec3 u = p;

        p.yz *= mat2(COS_15, SIN_15, -SIN_15, COS_15);
        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        float d = scene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);

            n.yz *= mat2(COS_15, -SIN_15, SIN_15, COS_15);
            n.xz *= mat2(c, -s, s, c);

            fragColor.rgb += 0.65 - 0.5 * dot(n, normalize(u));
            //fragColor.rgb += max(0.0, pow(dot(n, normalize(u)), 1.0)); // Hacky ambient occlusion (only works for certain shapes)
            break;
        }

        if (d > 100.0) {
            break;
        }

        dt += d;
    }
}

#define S(x,y,z) smoothstep(x,y,z)
#define debug(x) fragColor = vec4(vec3(x), 1.0); return
#define debug2(x) fragColor = vec4(vec2(x), 0.0, 1.0); return
#define debug3(x) fragColor = vec4(x, 1.0); return

const float PI = 3.14159;
const float TAU = PI * 2.0;
const float AA = 0.005;

float circle(vec2 uv, float r) {
    float d = length(uv);
    return S(r+AA, r-AA, d);
}

mat2 rot2D(float a) {
    float sa = sin(a), ca = cos(a);
    return mat2(ca, -sa, sa, ca);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 coords = uv * 2.0 - 1.0;
    coords.x *= iResolution.x / iResolution.y;
    coords = rot2D(-iTime* 2.) * coords;

    float numberOfDots = 8.0; 
    float angle = (atan(coords.y, coords.x) + PI) / TAU * numberOfDots;
    float rid = floor(angle);
    float baseAngle = rid / numberOfDots * TAU - PI;
    vec2 ruv = rot2D(baseAngle) * coords;

    //debug(rid / numberOfDots); // Debug radial ids
    //debug2(ruv);               // Debug radial uvs

    float sizeId = (numberOfDots - rid - 1.);
    float circleBaseRadius = 0.03;
    float circleDynRadius = sizeId * 0.01;
    float circleRadius = circleBaseRadius + circleDynRadius;
    float circleDistance = 0.4 - circleDynRadius;

    vec3 col = vec3(circle(ruv - vec2(circleDistance, circleRadius), circleRadius));

    fragColor = vec4(col, 1.0);
}

float mapEnvironment(in vec2 p, in int ballID) {
    float distToEnv = mapEnvironmentNoBalls(p);
    for (int id=0; id < NUMBER_OF_BALLS; id++) {
        if (id != ballID) {
            vec2 ballPos = getBallPos(id);
            distToEnv = min(distToEnv, length(p - ballPos) - BALL_RADIUS);
        }
    }

    return distToEnv;
}

float normalDistribution(in float x, in float sigma, in float mu) {
    const float SQRT_TWO_PI = 2.50662827463;
    float q = (x - mu) / sigma;
    return exp(-0.5 * q * q) / (sigma * SQRT_TWO_PI);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float env = mapEnvironmentNoBalls(uv);
    fragColor.rgb += vec3(0.6, 0.3, 0.0) * smoothstep(unit, 0.0, env);

    if (env > 0.0) {
        for (int id=0; id < NUMBER_OF_BALLS; id++) {
            vec2 ballPos = getBallPos(id);
            float ball = length(uv - ballPos) - BALL_RADIUS;
            fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 0.8, 0.6), smoothstep(unit, 0.0, ball));
            fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(ball) + 0.2 * unit));
        }
    }

    if (iMouse.z > 0.0) {
        fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, abs(uv.y + 0.4 - 0.75 * normalDistribution(3.0 * uv.x, 1.0, 0.0))));
    }
}

