vec4 mapEnvironment(in vec2 p, in int ballID) {
    vec2 ballPos = getBallPos(ballID);

    float distToEnv = mapEnvironmentNoBalls(p);
    vec2 colliderVel = vec2(0.0, 0.0);
    float colliderType = 0.0;

    for (int id=0; id < NUMBER_OF_BALLS; id++) {
        if (id != ballID) {
            vec2 colliderPos = getBallPos(id);
            distToEnv = min(distToEnv, length(p - colliderPos) - BALL_RADIUS);

            if (length(ballPos - colliderPos) < 2.0 * BALL_RADIUS) {
                colliderVel = getBallVel(id);
                colliderType = 1.0;
            }
        }
    }

    return vec4(distToEnv, colliderVel, colliderType);
}

vec2 getNormal(in vec2 p, in int id) {
    return normalize(vec2(mapEnvironment(p + vec2(0.001, 0.0), id).x - mapEnvironment(p - vec2(0.001, 0.0), id).x,
                          mapEnvironment(p + vec2(0.0, 0.001), id).x - mapEnvironment(p - vec2(0.0, 0.001), id).x));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 address = ivec2(fragCoord);
    if (address.x < NUMBER_OF_BALLS && address.y == 0) {
        fragColor = vec4(generatePosition(fragCoord.x), 0.0, 1.0);
    }

    if (address.x < NUMBER_OF_BALLS && address.y == 1) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

    if (iFrame > 0) {
        if (address.x < NUMBER_OF_BALLS) {
            vec2 ballPos = getBallPos(address.x);
            vec2 ballVel = getBallVel(address.x);

            ballVel.y -= GRAVITY;
            ballPos += ballVel;

            vec4 envData = mapEnvironment(ballPos, address.x);
            if (envData.x < BALL_RADIUS) {
                vec2 hitNormal = getNormal(ballPos, address.x);
                ballPos += hitNormal * (BALL_RADIUS - envData.x); // Thanks kastorp :)

                if (envData.w > 0.0) {
                    vec2 perpNormal = hitNormal.yx * vec2(-1.0, 1.0);
                    vec2 ballVel2 = envData.yz;
                    ballVel = dot(ballVel2, hitNormal) * hitNormal + dot(ballVel, perpNormal) * perpNormal;
                }

                else {
                    ballVel = reflect(ballVel, hitNormal);
                }

                ballVel *= FRICTION;
            }

            if (address.y == 0) {
                fragColor = vec4(ballPos, 0.0, 1.0);
            }

            if (address.y == 1) {
                fragColor = vec4(ballVel, 0.0, 1.0);
            }
        }
    }
}

