#define FOREST_DEPTH 150.

float tri( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.58,0.9660254038,0.5773502692,1.7320508076);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float tre( in vec2 p)
{
    return tri(vec2(p.x/p.y, p.y - clamp(round(p.y),-5.,-1.)));
}

float tres (in vec2 p)
{
    return tre(p*vec2(18.,7.)-vec2(0.,2.));
}

float treePos(in vec2 p, in float i) {
    return abs(sin(round(p.x+1.5)))*1./i-0.04*i+0.9;
}

float trees (in vec2 p, in float i)
{
    return tres(
        vec2(fract(p.x)-0.5,p.y +treePos(p,i)));
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 colour(in float t) {
    return palette(t, vec3(0.3,0.7,0.5),vec3(2.0,1.0,0.0),  vec3(0.4,0.5,0.5), vec3(0.50,0.20,0.25));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2. -1.;

    vec3 col = vec3(0.,-uv.y,.3)/2.;

    float s = sdHexagram(uv*vec2(48.,22.)-vec2(0.,16.),1.);

    col = vec3(max(col,1. - pow(max(s/4.,0.),0.3)));


    for (float i = 1.; i < FOREST_DEPTH ; ++i) {

        float d = trees((uv+vec2(sin(i*2.),0.))*(i+15.)/16.,i);

        if (d < 0.) {
            col = colour(i/90.)*(0.2+vec3(10.,10.,5.)/i) + 1. - 1.1*length(uv-vec2(0.,0.4));
            break;
        }
    }

    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846

// 2D rotation with pivot point (.5,.5)
vec2 rotate2D(vec2 st, float angle){
    st -= 0.5;
    st =  mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    st += 0.5;
    return st;
}
// a square with given size as side 
float square(vec2 st, vec2 side){
    vec2 border = vec2(0.5)-side*0.5;
    vec2 pq = smoothstep(border,border+.01,st);
    pq *= smoothstep(border,border+.01,vec2(1.0)-st);
    return pq.x*pq.y;
}
// pseudo-random function, returns value between [0.,1.]
float rand (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(31.7667,14.9876)))
                 * 833443.123456);
}
//bilinear value noise function
float bilinearNoise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners of a 2D square
    float f00 = rand(i);
    float f10 = rand(i + vec2(1.0, 0.0));
    float f01 = rand(i + vec2(0.0, 1.0));
    float f11 = rand(i + vec2(1.0, 1.0));

    vec2 u = smoothstep(0.,1.,(1.-f));
	return u.x*u.y*f00+(1.-u.x)*u.y*f10+
    u.x*(1.-u.y)*f01+(1.-u.x)*(1.-u.y)*f11;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uv = fragCoord.xy/iResolution.xy;
    uv-=.5;
    vec3 color = vec3(0.0);
    float scale=6.-4.*iMouse.x/iResolution.x;
    // Divide the space into cells
	vec2 ixy=.5+floor(scale*uv);
    uv = fract(scale*uv);
    // Using a 2x2 matrix to rotate with given angle
    uv = rotate2D(uv,.5*PI*sin((ixy.x+ixy.y)*iTime));
 	float nr=bilinearNoise(uv);
    // Draw a noisy square
    color =vec3(1.*nr)+vec3(square(uv,vec2(0.7)));
    color*=vec3(1.,.5+.5*sin(40.*nr+ixy.x*iTime), .5+.5*sin(40.*nr+ixy.y*iTime));

    fragColor = vec4(color,1.0);
}

const float TAU = 6.2831853071;
const float N = 10.;

float distBand(float R1, float R2, float r) {
    return (R2-R1)*0.5 - abs(r - (R1+R2)*0.5);
}

float crest(float a, float r) {
    return 50. * distBand(a*0.5, a, r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    uv -= vec2(iResolution.x/iResolution.y, 1.) * 0.5;
    uv *= 0.5;
    float mask = 0.;
    // Main spirals
    float a = fract(0.5 - atan(uv.y, uv.x) / TAU + iTime*0.1);
    float r = length(uv);
    for (float i = 0.; i < N; ++i) {
        mask += max(crest(fract(a + i/N), r), 0.);
    }

    vec3 col = mix(vec3(1, 45, 64), vec3(166, 228, 255), mask) / 255.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy; 

    float l = exp2(round(log2(R.x))); // normalizing by a power of 2.
    U.x -= (R.x-l)/2.; if (abs(U.x-l/2.)>l/2.) { O = vec4(.5); return; }

    int  p = int(U), q = int(l), n = int(sqrt(l));

    // encodes x as p/q, p & q coprimes  search and divive by common divisors

    while( p%2 == 0 && q%2 == 0 ) p/=2, q/=2, n/=2; // treat 2^max separately
                                                    // to allow += 2 in main loop
    for( int i=3; i < n; i+=2 )
      if( p%i == 0 && q%i == 0 )     // i divide p and q : reduce both by i^max
          p/=i, q/=i, n/=i, i-=2;    // -=2 emulates while

    O = vec4( 1./float(q) < .5*U.y/R.y );  
}

const float TAU = 6.2831853071;
const float N = 10.;

float cnoise(vec2 P);

float distBand(float R1, float R2, float r) {
    return (R2-R1)*0.5 - abs(r - (R1+R2)*0.5);
}

float crest(float a, float r) {
    return 5. * distBand(a*0.5, a, r);
}

vec2 rotate(float a, vec2 uv) {
    float c = cos(a);
    float s = sin(a);
    return vec2(
        c * uv.x - s * uv.y,
        s * uv.x + c * uv.y
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv *= 0.5;
    uv = rotate(iTime*0.7, uv);
    uv += 0.005*cnoise(uv*200.);
    //uv = rotate(iTime*0.25, uv);
    float mask = 0.;
    // Main spirals
    float a = fract(0.5 - atan(uv.y, uv.x) / TAU);
    float r = 1.5*length(uv)-0.23;
    for (float i = 0.; i < N; ++i) {
        mask += max(crest(fract(a + i/N), r), 0.);
    }

    // Output to screen
    vec3 col = mix(vec3(1, 45, 64)*0.3, vec3(166, 228, 255)*3., mask) / 255.;
    fragColor = vec4(col,1.0);
}

//	Classic Perlin 2D Noise 
//	by Stefan Gustavson
// Thanks to https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec2 fade(vec2 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}

vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}

float cnoise(vec2 P){
  vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
  vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
  Pi = mod(Pi, 289.0); // To avoid truncation effects in permutation
  vec4 ix = Pi.xzxz;
  vec4 iy = Pi.yyww;
  vec4 fx = Pf.xzxz;
  vec4 fy = Pf.yyww;
  vec4 i = permute(permute(ix) + iy);
  vec4 gx = 2.0 * fract(i * 0.0243902439) - 1.0; // 1/41 = 0.024...
  vec4 gy = abs(gx) - 0.5;
  vec4 tx = floor(gx + 0.5);
  gx = gx - tx;
  vec2 g00 = vec2(gx.x,gy.x);
  vec2 g10 = vec2(gx.y,gy.y);
  vec2 g01 = vec2(gx.z,gy.z);
  vec2 g11 = vec2(gx.w,gy.w);
  vec4 norm = 1.79284291400159 - 0.85373472095314 * 
    vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
  g00 *= norm.x;
  g01 *= norm.y;
  g10 *= norm.z;
  g11 *= norm.w;
  float n00 = dot(g00, vec2(fx.x, fy.x));
  float n10 = dot(g10, vec2(fx.y, fy.y));
  float n01 = dot(g01, vec2(fx.z, fy.z));
  float n11 = dot(g11, vec2(fx.w, fy.w));
  vec2 fade_xy = fade(Pf.xy);
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
  float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
  return 2.3 * n_xy;
}

float bdBar(in vec2 p, in vec4 r, float s) {
    vec4 v4 = vec4(r.xy, 1. - (r.xy + r.zw));
    vec4 f = smoothstep(v4 - s, v4 + s, vec4(p, 1. - p));
    return f.x * f.y * f.z * f.w;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    const float st = 1. / 32.;
    vec3 col = vec3(0.);

    for (int i = 0; i < 32; i++) {
        float freq = clamp(pow(texture(iChannel0, vec2(float(i) / 32., 0.)).x, 1.5), 0., 1.);
        float cst = float(i) * st;
        float bar = bdBar(uv + .5, vec4(cst, 0., .01, freq), .002);
        col += vec3(freq, cst, 0.) * bar;
    }

    col = pow(col, vec3(.454545));

    fragColor = vec4(col, 1.);
}

