vec3 getNormal(vec3 p){
	vec2 t = vec2(0.,0.0001);
	return normalize(map(p) - vec3(
    	map(p - t.yxx),
    	map(p - t.xyx),
    	map(p - t.xxy)
    ));
}


#define EPS 0.0001

float march(inout vec3 p){
	float d = 10e5;

    for(float i = 0.; i < 200.; i++){
        float m = map(p);
        d = min(d,m);
        if(d < EPS){
        	break;
        }
        p += rd*(m + 0.00);
    }


    return d;
}


float marchaa(vec2 uv){
    float col = 0.;
    float aa = 1.;

    for(float i =0.; i < aa*aa + min(float(iFrame),0.); i++){
        vec2 luv = uv + vec2(mod(i,aa),floor(i/aa))/aa/iResolution.xy;
        vec3 p = ro;

        float m = march( p);
        if (m < EPS){

            vec3 n = getNormal(p);

            vec3 l = normalize(vec3(1.,2.,-1.));

            float diff = max(dot(n,l),0.);


        	col += 1.*diff;
        } 

    }

    col /= aa*aa;
    col = screenprint(uv,col,0.008,0.9);
    col = 1. - col; // < remove me
    return col;
}

float logoGen(vec2 uv, float seed, float sc, float its){
    vec3 col = vec3(0);

    uv *= sc;
    vec2 id = floor(uv);
    vec2 fruv = fract(uv) - 0.5;

    if(length(id.x) > 0. || length(id.y) > 0.)
        return 10e4;

    id += seed;
    float gap = 0.109;

    float h1 = .9;

	float d = 10e4;


    float r = r22(id).x; // todo - change to r21
    float rI= floor(mod(r*14225.12 , 10.));

    fruv *= rot(0.25*pi*rI);

    for (float i = 0.; i < its; i ++) {

    	fruv *= rot(0.25*pi*id.y);
        fruv.x = abs(fruv.x);
        fruv.y = abs(fruv.y);

        vec2 rr = r22(id + i*seed);

        rr *= 10.;



        if (rr.x < 2.) {
        	fruv.x -= 0.5;
        } else if (rr.x < 4.) {
        	fruv.x -= 0.25;
        } else if (rr.x < 6.){
        	fruv.y -= 0.5;
            fruv *= rot(4.*0.125*pi);
        } else if (rr.x < 8.) {
        	fruv.x -= 0.5;
        } else {
        	//fruv.x -= 0.125;
            fruv.y -= 0.325;
        }

    }


    d = min(d, sdBox(fruv + vec2(0., h1 + gap), vec2(0.04, h1)));

    d = min(d, sdBox(fruv + vec2(h1 + gap, 0.), vec2(h1, 0.04)));

    d = min(d, sdBox( (fruv  + vec2(0.057) )*rot(-0.25*pi), vec2(h1*0.25, 0.04)));

	return d/sc;
}

float text(vec2 p, float[8] chars, float spacing, float s) {
	p *= s;
    p.x *= 1. - spacing;
    vec2 id = floor(p*16.);
    p = mod(p,1./16.);
    p.x = p.x/(1. - spacing) + 0.1375;
    float char = chars[int(id.x)];
    float t;
    if(abs(id.y) < 1. && id.x >= 0. && id.x < 8. && mod(char, 1.) == 0.){
        vec4 letter = texture(iChannel3,p + vec2(mod(char,16.)/16.,floor(char/16.)/16. ));
        t = letter.w - 0.5;
        t /= s;
    } else {
        t = 10e5;
    }
    return t;

}


float barcode(vec2 uv, float md,vec2 s){

    float d = 10e5;

    vec2 ouv = uv;

    vec2 offs = vec2( sin(uv.x*2.2 + pow(abs(cos(uv.x*.2)),8.)*1.)*.1, 0.);
    offs.x -= sin(ouv.x*0.2 )*0.2;


    uv += offs;

    //uv.y -= sin(ouv.x*2. - cos(ouv.y*2. - ouv.x*0.4 + sin(ouv.x*4.))*0.9)*0.5;

    float dfb = length(vec2(dFdx(uv.x),dFdy(uv.y)));

    vec2 id = floor(uv/md);
    uv = pmod(uv,md );


    d = min(d, length(uv.x) + 0.001 -md*1.1*pow(r11(id.x),2.) );


    d = max(d,max(abs(ouv.x) - s.x,abs(ouv.y) - s.y));
    d = min(d,abs(max(abs(ouv.x) - s.x,abs(ouv.y) - s.y)) - 0.001);


    df = dfb*(1. - exp(-smoothstep(0.,1.,length(offs)*5.))*0.7 );


    return d;
}

float sineDistField( vec2 uv, float scale )
{
    vec2 ouv = uv;


    float d = 10e5;

    ouv.y += iTime*0.1;
    vec2 offs = vec2( sin(uv.x*4.2 + sin(iTime) + 3.)*.1, 0.);
    offs.x -= sin(ouv.x*2. - cos(ouv.y*8. - ouv.x*2.4 + sin(ouv.x*7.))*0.2)*0.2;
    //offs.y -= sin(ouv.y*2.)*.2;

    ouv.y -= iTime*0.1;

    uv += offs;

    uv *= scale;

    //uv.y -= sin(ouv.x*2. - cos(ouv.y*2. - ouv.x*0.4 + sin(ouv.x*4.))*0.9)*0.5;

    float dfb = length(vec2(dFdx(uv.x),dFdy(uv.y)));

    uv = pmod(uv,0.02 );


    d = min(d, length(uv) - 0.00);
    d = min(d, length(uv.x) - 0.00);

    vec2 s = vec2(0.35,0.27);
    //d = max(d,max(abs(ouv.x) - s,abs(ouv.y) - s));
    ouv += vec2(0.,-0.12);
    d = max(d,sdBox(ouv,vec2(s)));
    d = min(d,abs(sdBox(ouv,vec2(s))) - 0.002);

    df = dfb*(1. - exp(-smoothstep(0.,1.,length(offs)*4.))*0.75 )/scale;
    d -= 0.004;
    return d;
}

float asemic( vec2 uv )
{
    vec2 ouv = uv;

    float df = dFdx(uv.x);

    float d = 10e5;

    uv.x += 0.2;
    float w = 0.05;

    vec2 A = vec2(-0.2,-0.2);
    vec2 B = vec2(0);
    vec2 C = vec2(0,-0.4); 
    vec2 D = vec2(0.4,-0.4); 
    vec2 E = vec2(1.,-0.4); 

    float sm = 0.35;

    vec2 F = vec2(0,-0.3);

    vec2 G = vec2(0.2,0.);
    vec2 H = vec2(0.2,-0.2);
    vec2 L = vec2(0.2,-0.4);


    d = min(d,sdOrientedBox(uv,A,vec2(0. + w*0.33,0.),w*0.52, sm));
    d = min(d,sdOrientedBox(uv,B + vec2(0.,0. + w*0.175),C,w, sm));
    d = min(d,sdOrientedBox(uv,C,D,w, 0.5));
    d = min(d,sdOrientedBox(uv - vec2(0.,w*0.5),D,E,0., sm));


    uv -= vec2(0.22,0.);


    d = min(d,sdOrientedBox(uv,A,vec2(0. + w*0.33,0.),w*0.52, sm));

    d = min(d,sdOrientedBox(uv,B + vec2(0.,0. + w*0.175),F,w, sm));

    uv -= vec2(0.22,0.);

    d = min(d,sdOrientedBox(uv,A*0.6,vec2(0. + w*0.33,0.),w*0.52, sm));

    d = min(d,sdOrientedBox(uv,B + vec2(0.,0. + w*0.175),F,w, sm));
    d = min(d,sdOrientedBox(uv,B + vec2(0,-0.2),G,w, sm*0.05));

    d = min(d,sdOrientedBox(uv + vec2(0.02,0.275),B + vec2(0,-0.),G,w, sm*0.));


    d = min(d,sdOrientedBox(uv + 0.05,C,L,w, sm) - 0.03);
    d = min(d,sdOrientedBox(uv ,C,L,w, sm) - 0.03);


    uv -= vec2(0.35,0.);

    d = min(d,sdOrientedBox(uv,A*0.6,vec2(0. + w*0.33,0.),w*0.52, sm));

    d = min(d,sdOrientedBox(uv + vec2(0.02,0.),B + vec2(0,-0.),G,w, sm*1.65));

    d = min(d,sdOrientedBox(uv + vec2(0.01,-0.014),B + vec2(0,-0.175),G,w, sm*0.5));
    d = min(d,sdOrientedBox(uv + vec2(0.01,-0.014) + vec2(0.1),B + vec2(0,-0.175)*1.,G,w, sm*0.5));


    d = min(d,sdOrientedBox(uv + vec2(0.13,0.275),B,G,w, 0.));



    d -= 0.001;

	return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    uv *= 1. - smoothstep(1.,0.,dot(uv,uv)*0.2)*0.4;

    uv *= 1.7;

    guv = uv;
    luv = uv;

    frame = floor(float(iFrame)*SPEED);
    seed = r11(frame)*200.;
    df = dFdx(uv.x);
    dfo = df;

    uv += texture(iChannel1,uv).xy*0.008+ texture(iChannel2,uv*0.05).xy*0.003+ texture(iChannel2,uv*0.45).xy*0.0;

    vec3 col = vec3(0.994);
    float d = 10e5;


    vec2 roll = r12(frame + 1.);


    //d = sineDistField(uv,2.)*0.5;
    vec2 off = vec2(0);


    ro.xy = vec2(sin(iTime),cos(iTime))*0.14;

    ro.z = iTime;

    lookAt = vec3(0,0,ro.z + 1.);

    rd = getRd(ro,lookAt,uv - off - vec2(-0.3,-0.09), .8);

    rd.xy *= rot(0.7);

    d = slice( uv - off, -0.8, 0.8, 21., false) - .004;



    //col = colour( max(d - 0.1,abs(uv.x + off.x) - 0.12), col, vec3(0,0.6,0.2), 1,0,6.,.05);


    //df = 0.01;
    df *= 142.;
    col = colour( d, col, vec3(0.4,0.001,0.004)*1. + sin(uv.yxx)*0.4, 1,0,9.,.001);


    df = dfo;



    col -= vec3(0.9,0.7,0.9)*smoothstep(df*(1. ),0.,abs(uv.x + R.x/R.y*0.5 - 0.01) - 0.004)*( 0.1 + 0. );



    col = 1. - col;

    float scanlines = 70.;
    col *= 1. - 0.5*float(mod(fragCoord.y,iResolution.y/scanlines)/(iResolution.y/scanlines) > 0.5);


    fragColor = vec4(col,1.0);
}



