vec3 colour( float db, vec3 ca, vec3 cb, int mode, int stroke, float strokeparam, float strokeparamb){
	vec2 roll;

    float ss = mod(floor(frame/period ),6.);
    roll = r12(frame);

    vec4 n = valueNoise(guv,1.); 
    vec4 nb = valueNoise(guv*0.5 + 0.2,2.); 

    float deb;

    if (stroke == 0) {
		deb = smoothstep(df + texture(iChannel1,guv*0.5).x*0.53*n.z*strokeparamb,0.,db );
    } else if (stroke == 1){
        // airbrush/chalk
    	deb = smoothstep(df*strokeparam*0.01 + texture(iChannel0,guv*5.).x*0.004*strokeparam,0.,db );
    } else if (stroke == 2){
        // abstract
    	deb = smoothstep(df*strokeparam + texture(iChannel0,guv*5.).x*0.1*strokeparam,0.,db );
    } else if (stroke == 3){
        // paint

        db -= texture(iChannel1,guv*(0.3 - n.y*0.2)).x*0.07 + texture(iChannel1,guv*(1. - n.z*0.4)).y*0.03*n.y;

    	deb = smoothstep(df + texture(iChannel1,guv*1.).x*0.03*n.z,0.,db );
    } else if (stroke == 4){
        // paint 2
        vec2 dir = normalize(roll*2. - 1.);
        float pw = roll.x;
        db -= texture(iChannel1,guv*(0.3 - n.y*0.2)).x*0.24*pow(n.w,0.5) + texture(iChannel1,guv*(1. - n.z*0.4)).y*0.03*n.y;

        vec2 ruv = luv + texture(iChannel2,guv*0.02).xy*0.12;

        // splatter
        db += texture(iChannel1,ruv*0.2).x * 0.2*pow(nb.x,5.)*strokeparamb;
        db -= texture(iChannel2,ruv*0.3).x * (0.35*pow(n.z,3.) + 0.35*pow(nb.z,3.) )*strokeparamb;
        /*
        db -= texture(iChannel1,vec2((rot(atan(dir.y,dir.x))*ruv).x*0.2)).x * 0.1 *
            smoothstep(1.,0.,dot(ruv,dir*rot(0.5*pi))*3.);
        */
    	deb = smoothstep(df + texture(iChannel1,guv*1.).x*0.03*n.z,0.,db );
    } else if (stroke == 5){
        // paint 2
        vec2 dir = normalize(roll*2. - 1.);
        float pw = roll.x;
        db -= texture(iChannel1,guv*(0.3 - n.y*0.2)).x*0.07 + texture(iChannel1,guv*(1. - n.z*0.4)).y*0.03*n.y;

        vec2 ruv = luv + texture(iChannel2,guv*0.02).xy*0.08; // bending

        float dt = dot(ruv,dir*rot(0.5*pi));

        db -= texture(iChannel1,vec2((rot(atan(dir.y,dir.x))*ruv).x*0.2)).x * 0.2 *
            smoothstep(1.,0.,dt*3.);

        db = mix(db,n.x,pow(n.y,4.));
    	//db = mix(db,-texture(iChannel2,guv*0.04).t,pow(texture(iChannel2,guv).y,6.)*1.);

        deb = smoothstep(df + texture(iChannel1,guv*0.4).x*(0.29 + smoothstep(0.,1.,dt)*66.)*n.z,0.,db ); // smoothing
    }




    vec3 ob = mix(vec3(0),cb,deb);
    vec3 co = vec3(0);

    vec4 ta = texture(iChannel2,guv);
    vec4 tb = texture(iChannel1,guv);

    vec4 t = mix(ta,tb,0.6);


    if( stroke == 90){
        deb = mix(-0.,1.,clamp(db,0.,1.));
    }


    if(stroke == 90){
    	cb = mix( cb, 1. - cb, smoothstep(1.,0.,strokeparam*(t.x + ta.x - nb.x*0.4 + n.y*0.2)));
    } else {
        cb*=1. - smoothstep(1.,0.,abs(db)*(30.01) + t.z*1.53)*.9;	
    }


    float odeb = max(deb - pow(smoothstep(0.,1.,t.y*0.77), 4.25), 0.);


    if(mode == 0){
        // 0 replace
        if ( stroke == 3 || stroke == 4 ){
        	co = mix( ca, cb, max(deb - pow(smoothstep(0.,1.,t.y*0.77 - 0.1*smoothstep(1.,0.,abs(db)*14.11*(1. + pow(nb.x,3.)*5.))   ), .25*strokeparam)*1.1, 0.));
        } else if( stroke == 5 ){
        	co = mix( ca, cb, max(deb - pow(smoothstep(0.,1.,pow(t.y,0.4)*0.57), 0.75*strokeparam)*1., 0.));
        } else {
        	co = mix( ca, cb, max(deb - pow(smoothstep(0.,1.,t.y*0.77), strokeparam), 0.));
        }
    } else if(mode == 1){
        // 1 multiply
        co = mix(ca, ca*cb, odeb);
    } else if(mode == 2){
        // 2 overlay
        if (length(ca) < 0.5){
    		co = mix(ca, 2.*ca*cb, odeb);    	
        } else {
    		co = mix(ca, 1. - 2.*(1. - ca)*(1. - cb), odeb);
        }
    } else if(mode == 3){
        // 3 darken
          	co = mix(ca, min(ca,cb)*length(ca /* cb */), odeb);
    } else if(mode == 4){	
        // 4 burn
    	co = mix(ca, clamp(1.0 - (1.0 - ca) / cb,0.,1.), odeb);
    } else if(mode == 5){	
        // 5 linear burn
    	co = mix(ca, ca + cb - 1., odeb);
    } else if(mode == 6){
        // 6 color dodge
    	co = mix(ca, length(ca) > .5 ? 1.0 - 2.0 * (1.0 - ca) * (1.0 - cb) : 2.0 * ca * cb, odeb);
    } else if(mode == 7){
    	// 7 exclusion 
    	co = mix(ca, ca + cb - 2.0 * ca * cb, odeb);
    } else if(mode == 8){
    	// 8 subtract 
    	co = mix(ca, ca - cb, odeb);
    } else if(mode == 9){
    	// 9 linear light
    	co = mix(ca, length(cb) > .5 ? ca + 2.0 * (cb - .5) : ca + 2.0 * cb - 1.0, odeb);
    } else if(mode == 10){
    	// 10 screen
    	co = mix(ca, 1.0 - (1.0 - ca) * (1.0 - cb), odeb);
    } 

    return co;
}



float slice(vec2 uv, float rangeStart, float rangeEnd, float sliceCnt, bool rotational){

    float d = 10e5;
    rd = getRd(ro,lookAt,uv, .9);

    if(rotational){

        vec3 lro = ro;


        vec3 normal = vec3(0,0.,1);
        for(float i = rangeStart; i < rangeEnd; i += (rangeEnd - rangeStart)/sliceCnt){

            normal.xz *= rot(tau/sliceCnt + 0. );
			float plane = intersectPlane(lro,rd,normal).x;

            vec3 p = lro + rd*plane;

            vec2 ldf = vec2(dFdx(plane),dFdy(plane));

            float m = map(p);

            if(m <= d){

                //df = max(ldf.y,ldf.x)*1.;

                df = length(ldf);

                d = min(d,m);

            }

        }


    } else {
        for(float i = rangeStart; i < rangeEnd; i += (rangeEnd - rangeStart)/sliceCnt){
            vec3 lro = ro + vec3(0,i,0);


            float plane = intersectPlane(lro,rd,vec3(0,-1,0)).x;
            vec3 p = lro + rd*plane;

            vec2 ldf = vec2(dFdx(plane),dFdy(plane));

            float m = map(vec3(p.x,p.y - i*1.5,p.z));
            if(m <= d){

                //df = max(ldf.y,ldf.x)*1.;
                float amt = smoothstep(1.,0.,abs(p.y - lro.y)*0.2);

                amt = pow(amt, 0.04);
                d = mix(d,10000000.,amt);

                df = length(ldf)*0.5;

                df = mix(df,0.,amt);


                d = min(d,m);


            }

        }

    }



    return d;
}


float dotwork(vec2 uv, float w, float densitya, float densityb){
	float d = 10e5;


    float den = mix(densitya,densityb,pow(smoothstep(densitya,densityb,(uv.x)/w/2. +0.5), 2.7));

    uv -= r12( seed*2.5 + frame)*24.;

    for(float i = 0.; i < 925.; i++){
    	vec2 r = r12( seed*2.5 + i*2.4 + frame);

        float s = 2. + r.x*4.;
        //float s = 4.;
        vec2 luv = uv*s;
        vec2 fruv = fract(luv) - 0.5;
        vec2 id = floor(luv );
        vec2 rp = r22( id + seed)*2. - 1.;

        float density = mix(densitya,densityb,smoothstep(densitya,densityb,id.x*w*s + w*0.5));

        if(r.y < mix(density,den,1.)){
        	d = min(d,length(fruv - rp*(0.4 - 0.3*smoothstep(2.,4.,s)))/s);
        }

    }

    return d - 0.002;
}


float screenprint(vec2 p,float lum, float cellSize, float strength){
	float t = 0.;
    vec2 q = p ;

    p *= rot(0.125*pi);


    q /= cellSize/pi;
    // some distortion
    p -= length(sin(q))*normalize(p)*cellSize/6.;


    p = pmod(p,cellSize);
    p -= length(sin(q))*normalize(p)*cellSize/6.*0.6;

    float lsz = 0.;

    float n = valueNoise(q*410.,1.).x*5.;

    n = pow(n,2.)*0.07;
    lsz = pow(smoothstep(0.,1.,lum*(0.45 +n)),strength)*cellSize*0.6;

    float col = smoothstep(0.002,0.,length(p) - lsz + 0.001);
    //col = mix(col,smoothstep(0.01,0.,sin(q.x)*sin(q.y)* lsz*1. ),0.1);

    return col;
}


float jaggy(vec2 uv, float densitya, float densityb, float w){
	float d = 10e5;

    d = length(uv.y);

    uv.x += 0.5;

    float den = mix(densitya,densityb,pow(smoothstep(densitya,densityb,(uv.x)/w/2. +1.), 1.));

    //uv -= r12( seed*2.5 + frame)*24.;

    for(float i = 0.; i < 7.; i++){
    	vec2 r = r12( seed*2.5 + i*2.4 + frame);

        float s = 2. + r.x*6.;
        //float s = 4.;
        float luv = uv.x*s;
        float fruv = fract(luv) - 0.5;
        float id = floor(luv );
        float rp = r11( id + seed)*2. - 1.;

        float density = mix(densitya,densityb,smoothstep(densitya,densityb,id*w*s + w*0.5));


        //d = min(d,length(fruv - rp*(0.4 - 0.3*smoothstep(2.,4.,s)))/s);

        if(r.y < mix(density,den,1.) + sin(r.y*53513.)*0.0){
        	d = max(d, -(abs(fruv) - max(rp,0.5)*0.2)/s);
        }

    }
    seed++;


    return d;

}

float bspline( in vec2 coords )
{
    float id = floor(mod(frame/period,2.));
    seg = floor(frame/period/2. );

    vec2 roll = r12(seg);

    coords *= rot(-pi*roll.x*0.1);

    vec2 oc = coords;

    float t = coords.x*float(cnt)*1. + 1.0;
    float[cnt] cp;

    float res = 0.;

    float amp = 0.03;

    vec4 na = valueNoise(vec2(t*4. + seed,0),1.3);
    vec4 nb = valueNoise(vec2(t*19. + seed,0),1.3);

    amp += (na.x*0.7 - nb.x*0.01)*pow(roll.y,4.);

    t += na.x*0.5;

    for(float i = 0.; i < float(cnt); i++){
    	int idx = int(i);

        cp[idx] = r11(i + seed)*amp;


        res += cp[idx] * N_i_3(t, i);
    }

    //res -= smoothstep(0.,1.,t*0.12)*0.5;

    res -= max(smoothstep(0.,1.,guv.x*0.4 + 0.1),0.);

    res += max(smoothstep(0.,1.,-guv.x*0.27),0.);

    //res -= clamp(0.,1.,guv.x);
    //res -= smoothstep(0.,1.,guv.x*1.42)*0.5;

    //res += sin(t + cos(t*2.)*0.01)*0.07;

    //res += sin(t + cos(t*2.)*0.01)*0.07;


    float v = res - coords.y;

    float slope = dFdx(v) / dFdx(coords.x);
    float d = abs(v)/length(vec2(slope, -1.0)); 

    d = max(d,-coords.x - 0.2);
    d = max(d,coords.x - 0.7);

    return d;
}



