// Definition of basic numeric type & multiplication.
// Addition, subtraction & multiplication by a scalar
// are the same as for float or vec2.
#if !defined COMPLEXSTEP
#define T float
T cmul(T z, T w) { return z*w; }
#else
#define T vec2
T cmul(T z, T w) {
#if defined DUAL
  return vec2(z.x*w.x,z.x*w.y+z.y*w.x); // Dual numbers
#else
  return vec2(z.x*w.x-z.y*w.y,z.x*w.y+z.y*w.x); // Complex numbers
#endif
}
#endif

T cmul(T z, T w, T v) {
  return cmul(z,cmul(w,v));
}
T cmul(T z, T w, T u, T v) {
  return cmul(cmul(z,w),cmul(u,v));
}

// I probably lifted this from Knighty's Fragmentarium shader.
T sarti12(T x, T y, T z, T w){
  T x2 = cmul(x,x), y2 = cmul(y,y), z2 = cmul(z,z), w2 = cmul(w,w);
  T x4 = cmul(x2,x2), y4 = cmul(y2,y2), z4 = cmul(z2,z2), w4 = cmul(w2,w2);
  T l1 = x4+y4+z4+w4;
  T l2 = cmul(x2,y2)+cmul(z2,w2);
  T l3 = cmul(x2,z2)+cmul(y2,w2);
  T l4 = cmul(y2,z2)+cmul(x2,w2);
  T l5 = cmul(cmul(x,y),cmul(z,w));
  T s10 = cmul(l1,cmul(l2,l3)+cmul(l2,l4)+cmul(l3,l4));
  T s11 = cmul(cmul(l1,l1),l2+l3+l4);
  T s12=cmul(l1,cmul(l2,l2)+cmul(l3,l3)+cmul(l4,l4));
  T s51=cmul(cmul(l5,l5),l2+l3+l4);
  T s234=cmul(l2,l2,l2)+cmul(l3,l3,l3)+cmul(l4,l4,l4);
  T s23p=cmul(l2,l2+l3,l3), s23m=cmul(l2,l2-l3,l3); 
  T s34p=cmul(l3,l3+l4,l4), s34m=cmul(l3,l3-l4,l4); 
  T s42p=cmul(l4,l4+l2,l2), s42m=cmul(l4,l4-l2,l2);
  T Q12=x2+y2+z2+w2; Q12=cmul(Q12,Q12,Q12); Q12=cmul(Q12,Q12);
  T S12=33.0*sqrt(5.0)*(s23m+s34m+s42m)+19.0*(s23p+s34p+s42p)+10.0*s234-14.0*s10+2.0*s11-6.0*s12-352.0*s51+336.0*cmul(l5,l5,l1)+48.0*cmul(l2,l3,l4);
  return 22.0*Q12-243.0*S12;
}

// Another surface most excellent
// The Kummer Surface. https://mathworld.wolfram.com/KummerSurface.html
float mu2 = 1.0;

T kummer(T x, T y, T z, T w) {
  float K = 1.414213562;
  T p = w-z-K*x;
  T q = w-z+K*x;
  T r = w+z+K*y;
  T s = w+z-K*y;
  T t = cmul(x,x)+cmul(y,y)+cmul(z,z)-mu2*cmul(w,w);
  float lambda = (3.0*mu2-1.0)/(3.0-mu2);
  return cmul(t,t) - lambda*cmul(cmul(p,q),cmul(r,s));
}

T surface(T x, T y, T z, T w){
  //return kummer(x,y,z,w);
  return sarti12(x,y,z,w);
}

#if defined COMPLEXSTEP
vec4 complexstep(vec3 p) {
  float h = 1e-20; // Note the tiny step size!
  float x = p.x, y = p.y, z = p.z, w = 1.0;
  vec2 tx = surface(vec2(x,h),vec2(y,0),vec2(z,0),vec2(1,0));
  vec2 ty = surface(vec2(x,0),vec2(y,h),vec2(z,0),vec2(1,0));
  vec2 tz = surface(vec2(x,0),vec2(y,0),vec2(z,h),vec2(1,0));
  return vec4(vec3(tx.y,ty.y,tz.y)/h,tx.x);
}
#else
float surface(vec3 p) {
  return surface(p.x,p.y,p.z,1.0);
}

vec3 grad(vec3 p) {
  float eps = 1e-3;
  vec2 e = vec2(0,eps);
  return vec3(surface(p+e.yxx)-surface(p-e.yxx),
              surface(p+e.xyx)-surface(p-e.xyx),
              surface(p+e.xxy)-surface(p-e.xxy))/(2.0*eps);
}
#endif

bool doinvert = false;
float eval(vec3 p, out vec3 n, out int type) {
  float k = 1.0;
  if (doinvert) {
    k = 1.0/dot(p,p);
    p *= k;
  }
#if !defined COMPLEXSTEP
  n = grad(p);
  float d = surface(p)/length(n); // Scaled distance
#else
  vec4 t = complexstep(p);
  float d = t.w/length(t.xyz);
  n = t.xyz;
#endif
  type = 0;
  if (doclipradius && !doinvert) {
    float d1 = length(p)-clipradius;
    if (d1 > d) { type = 1; n = p; d = d1; }
  }
  return d/k;
}

float eval(vec3 p) { int type; vec3 n; return eval(p,n,type); }

float march(vec3 ro, vec3 rd) {
  float t = 0.1;
  for(int i = 0; i < maxsteps; i++) {
    //assert(i < 50);
    vec3 p = ro + t*rd;
    float d = eval(p);
    //d = min(d,max(0.5,0.1*t));
    //d *= 0.8;
    //d /= (1.0+0.2*d);
    float k = doinvert ? 0.5 : 1.0;
    t += k*d;
    if (t < 0.0 || t > maxdist || abs(d) < t*precis) break;
  }
  return t;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    p.yz = rotate(p.yz,-theta);
    p.zx = rotate(p.zx,phi);
  }
  if (dorotate) {
    p.xz = -rotate(p.xz,iTime*0.15);
  }
  return p;
}

// softshadow from iq.
float softshadow(vec3 ro, vec3 rd, float mint, float tmax, float w) {
  float t = mint;
  float res = 1.0;
  for (int i = 0; i < 200; i++) {
    float h = eval(ro + t*rd);
    res = min(res, h/(w*t));
    t += clamp(h, 0.01*t, max(0.5,0.1*t));
    if (res < -1.0 || t > tmax) break;
  }
  res = max(res,-1.0); // clamp to [-1,1]
  res = 0.25*(1.0+res)*(1.0+res)*(2.0-res); // smoothstep
  return res;
}

vec2 octamap(vec3 p) {
  // Like a cube map but to an octahedron.
  // Each triangular face uses half the texture.
  int parity = 0; //int(p.z < 0.0);
  p = abs(p);
  p /= dot(p,vec3(1)); // Plane x+y+z = 1
  return parity == 0 ? p.xy : 1.0-p.yx; // Flip in x+y = 1
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  dorotate = !key(CHAR_R);
  doclipradius = key(CHAR_C);
  doinvert = key(CHAR_I);
  float dscale = exp(0.1*float(keycount(KEY_RIGHT)-keycount(KEY_LEFT)));
  maxdist *= dscale;
  clipradius *= dscale;
  float camera = 5.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  maxdist = max(maxdist,2.0*camera); // Ensure maxdist is sensible.
  vec3 ro = vec3(0,0,-camera);
  ro = transform(ro);
  vec3 light = vec3(1,2,-3);
  if (!key(CHAR_L)) light = transform(light);
  vec3 aacol = vec3(0);
  mu2 = 1.0/3.0+2.5-2.5*cos(0.2*iTime); // For Kummer surface
  float time = iTime; // For inlining suppression
  for (float i = 0.0; i < max(-time,AA); i++) {
    for (float j = 0.0; j < max(-time,AA); j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 rd = vec3(uv,2);
      rd = transform(rd);
      rd = normalize(rd);
      int type = 0;
      float t = march(ro,rd);
      vec3 bgcol = vec3(0.01);
      vec3 col = bgcol;
      if (t > 0.0 && t < maxdist) {
        vec3 p = ro+t*rd;
        // Sharp edge between clip region and surface means
        // sometimes we get the wrong normal.
        vec3 n; int type;
        eval(p,n,type); // Re-evaluate for normal and type
        if (type == 1 && dot(p,rd) <= 0.0) n = p;
        else if (type == 2 && p.x < 0.0) n = vec3(-1,0,0); //Huh?
        n = normalize(n);
        col = vec3(1);
        //col = texture(iChannel0,octamap(n)).rgb;
        //col *= dot(vec3(1),texture(iChannel0,octamap(n)).rgb);
        if (type > 0) col = vec3(0.1);
        float ambient = 0.1;
        vec3 lightdir = normalize(light); // Light at infinity
        float diffuse = max(0.0,dot(n,lightdir));
        if (key(CHAR_S)) diffuse *= softshadow(p,lightdir,0.01,4.0*max(1.0,length(p)),0.1);
        col *= ambient+diffuse;
        float fog = key(CHAR_F) ? 0.0 : t/maxdist;
        col = mix(col,bgcol,fog);
      }
      aacol += col;
    }
  }
  aacol /= float(AA*AA);
  aacol = pow(aacol,vec3(0.4545)); // gamma correction
  if (check) aacol.r = 1.0;
  fragColor = vec4(aacol,1);
}

