  col+=rdr2(uv*r2d(.1))*.5;
  col *= .5+1.-sat(lenny(uv*.25));

  float val = texture(iChannel2, fragCoord/iResolution.xy).x;

  col *= sat(iTime-3.);
  //col = mix(col, vec3(1.), val*.5);


