// column major
mat3 kSobelHorizontalR = mat3(1, 2, 1,
                             0, 0, 0,
                             -1, -2, -1);
mat3 kSobelVerticalD = mat3(-1, 0, 1,
                           -2, 0, 2,
                           -1, 0, 1);
mat3 kSobelHorizontalL = mat3(-1, -2, -1,
                             0, 0, 0,
                             1, 2, 1);
mat3 kSobelVerticalU = mat3(1, 0, -1,
                           2, 0, -2,
                           1, 0, -1);

vec4 greyScale( vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Read pixel from webcam
    vec4 px = texture(iChannel0, uv);

    // Weighted greyscale
    float grey = (px.r * 0.2126) + (px.g * 0.7152) + (px.b * 0.0722);
    return vec4(grey,grey,grey,1.0);
}

float magPX( vec2 fragCoord ) { 
  return greyScale(fragCoord).r;
}

// Sample a 3x3 matrix of pixels, centered on fragCoord
mat3 sample3x3(vec2 fragCoord) {
  // Sample the image (column-major)
  mat3 pixels = mat3(
    magPX(fragCoord + vec2(-1,1)), magPX(fragCoord + vec2(-1,0)), magPX(fragCoord + vec2(-1,-1)),
    magPX(fragCoord + vec2(0,1)), magPX(fragCoord + vec2(0,0)), magPX(fragCoord + vec2(0,-1)),
    magPX(fragCoord + vec2(1,1)), magPX(fragCoord + vec2(1,0)), magPX(fragCoord + vec2(1,-1))
  );
  return pixels;
}

// Apply a 3x3 kernel to a pixel matrix
mat3 kernel3x3( mat3 pixels, mat3 kernel ) {
  // Multiply samples by matrix, componentwise (* is matrix mult)
  return matrixCompMult(pixels, kernel);
}

// Sum a pixel matrix
float sum3x3(mat3 pixels) {
  // Sum and return
  return pixels[0][0] + pixels[0][1] + pixels[0][2] +
         pixels[1][0] + pixels[1][1] + pixels[1][2] +
         pixels[2][0] + pixels[2][1] + pixels[2][2];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Sample (greyscale)
    mat3 px = sample3x3(fragCoord);

    // Edge detect
    mat3 edgeHR = kernel3x3(px, kSobelHorizontalR);
    mat3 edgeVD = kernel3x3(px, kSobelVerticalD);
    mat3 edgeHL = kernel3x3(px, kSobelHorizontalL);
    mat3 edgeVU = kernel3x3(px, kSobelVerticalU);

    // Sum pixels
    float i = length(sum3x3(edgeHR)) + length(sum3x3(edgeVD)) + length(sum3x3(edgeHL) + length(sum3x3(edgeVU)));

    // Discard some noise
    if( i < 0.5 ) i = 0.0;

    // Output to screen
    fragColor = vec4(i,i,i,1.0);
}

// Note: this code is unoptimal on purpose, zero optimizations, and demonstration only!

void sharedGetUvAndInterp(in vec2 uv, out ivec2 uvi, out vec2 interp) {
    uv *= iChannelResolution[0].xy;
    uv -= 0.5;
    interp = fract(uv);
    uvi = ivec2(floor(uv));
}


#define PI 3.1415926

float sinc(in float x) {
	if (x == 0.0)
        return 1.0;
    return sin(PI*x) / (PI*x);
}



vec3 sampleBilinear(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);
    vec4 weights = vec4((1.0-interp.x) * (1.0-interp.y),
                        interp.x * (1.0-interp.y),
                        (1.0-interp.x) * interp.y,
                        interp.x * interp.y);
    return  texelFetch(iChannel0, uvi + ivec2(0,0),0).xyz * weights.x
          + texelFetch(iChannel0, uvi + ivec2(1,0),0).xyz * weights.y
          + texelFetch(iChannel0, uvi + ivec2(0,1),0).xyz * weights.z
          + texelFetch(iChannel0, uvi + ivec2(1,1),0).xyz * weights.w;
}

vec3 sampleSinc(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);

    vec3 col = vec3(0.0);
    float weights = 0.0;
    for (int y = -2; y <= 2; ++y) {
        for (int x = -2; x <= 2; ++x) {
            float effective_x = interp.x - float(x);
            float effective_y = interp.y - float(y);
            float window = 1.0;//sinc(effective_x / 4.0) * sinc(effective_y / 4.0);
            float w = sinc(effective_x) * sinc(effective_y) * window;
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz * w;
            weights += w;
        }
    }
    return col / weights;
}

vec4 getBsplineWeights(in float x) {
  	// float w0 = 1.0/6.0 * (1.0-x) * (1.0-x) * (1.0-x);
  	float w1 = 2.0/3.0 - 0.5 * x*x * (2.0-x);
  	float w2 = 2.0/3.0 - 0.5 * (1.0-x)*(1.0-x) * (2.0-(1.0-x));
  	float w3 = 1.0/6.0 * x*x*x;
	return vec4(1.0 - w3 - w1 - w2, w1, w2, w3);
}

vec3 sampleBSpline(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    sharedGetUvAndInterp(uv, uvi, interp);

    vec4 bspline_weights_x = getBsplineWeights(interp.x);
    vec4 bspline_weights_y = getBsplineWeights(interp.y);
    vec3 col = vec3(0.0);
    for (int y = -1; y <= 2; ++y) {
        for (int x = -1; x <= 2; ++x) {
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz * bspline_weights_x[x+1] * bspline_weights_y[y+1];
        }
    }
    return col;
}

vec3 getBiquadraticWeights(in float x) {
    return vec3(0.5 - x + 0.5 * x * x, x - x * x + 0.5, 0.5 * x * x);
}

vec3 sampleBiquadratic(in vec2 uv) {
    ivec2 uvi; vec2 interp;
    uv *= iChannelResolution[0].xy;
    uv -= 0.5;
    interp = uv - round(uv);
    uvi = ivec2(round(uv));

    vec3 biquadratic_weights_x = getBiquadraticWeights(interp.x+0.5);
    vec3 biquadratic_weights_y = getBiquadraticWeights(interp.y+0.5);
    vec3 col = vec3(0.0);

    for (int y = -1; y <= 1; ++y) {
        for (int x = -1; x <= 1; ++x) {
            col += texelFetch(iChannel0, uvi + ivec2(x,y),0).xyz 
                * biquadratic_weights_x[x+1] * biquadratic_weights_y[y+1];
        }
    }
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 origUV = fragCoord/iResolution.xy;
    vec2 uv = origUV / 5.0;
    uv += vec2(0.5, 0.48);
    uv *= iChannelResolution[0].xy;
    if (fract(0.15 * iTime) < 0.8)
    	uv = round(uv);
    uv /= iChannelResolution[0].xy;
    uv += vec2(sin(iTime),cos(iTime)) / 150.0 * vec2(iResolution.y/iResolution.x, 1.0);
    vec2 phase = fract(uv * iChannelResolution[0].xy - vec2(0.5));

    vec3 col = vec3(0.0);

    if (origUV.x > origUV.y) {
        if (origUV.x > 1.0 - origUV.y) {
        	col = sampleSinc(uv);
        } else {
            col = sampleBSpline(uv);
        }
    } else {
        if (origUV.x > 1.0 - origUV.y) {
        	col = sampleBilinear(uv);
        } else {
            col = sampleBiquadratic(uv);
        }
    }
    if (abs(origUV.x - origUV.y) < 0.005 || abs(origUV.x - (1.0-origUV.y)) < 0.005)
        col = vec3(1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

