/*
"SDF to Mesh" by oneshade

After watching The Art of Code's (BIgWings) video about value noise,
I started playing around with it and designed a 3D variant. I found
that if I removed the smooth remapping of local coordinates I got a
cool mesh effect. It reminds me of nimitz's "Triangulator" shader
(https://www.shadertoy.com/view/lllGRr) except the facets aren't always
triangles and it works on more than just heightmaps. I don't think
the facets are always perfectly planar though (take a cube for example).

Also reminds me a lot of marching cubes.
*/

// Mesh resolution:
#define res vec3(1.0, 1.0, 1.0)

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) 0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0))

#define SIN_15 0.2588190451
#define COS_15 0.96592582628

float mapScene(in vec3 p) {
    return length(p) - 2.0;
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)) - mapScene(p - vec3(0.001, 0.0, 0.0)),
                          mapScene(p + vec3(0.0, 0.001, 0.0)) - mapScene(p - vec3(0.0, 0.001, 0.0)),
                          mapScene(p + vec3(0.0, 0.0, 0.001)) - mapScene(p - vec3(0.0, 0.0, 0.001))));
}

float mapSceneMesh(in vec3 p) {
    vec3 cell = floor(p / res) * res;
    vec3 local = fract(p / res);

    float ldb = mapScene(cell);                           // Left, Down, Back
    float rdb = mapScene(cell + vec3(res.x, 0.0, 0.0));   // Right, Down, Back
    float ldf = mapScene(cell + vec3(0.0, 0.0, res.z));   // Left, Down, Front
    float rdf = mapScene(cell + vec3(res.x, 0.0, res.z)); // Right, Down, Front
    float lub = mapScene(cell + vec3(0.0, res.y, 0.0));   // Left, Up, Back
    float rub = mapScene(cell + vec3(res.xy, 0.0));       // Right, Up, Back
    float luf = mapScene(cell + vec3(0.0, res.yz));       // Left, Up, Front
    float ruf = mapScene(cell + res);                     // Right, Up, Front

    return mix(mix(mix(ldb, rdb, local.x),
                   mix(ldf, rdf, local.x),
                   local.z),

               mix(mix(lub, rub, local.x),
                   mix(luf, ruf, local.x),
                   local.z),

               local.y);
}

vec3 getNormalMesh(in vec3 p) {
    return normalize(vec3(mapSceneMesh(p + vec3(0.001, 0.0, 0.0)) - mapSceneMesh(p - vec3(0.001, 0.0, 0.0)),
                          mapSceneMesh(p + vec3(0.0, 0.001, 0.0)) - mapSceneMesh(p - vec3(0.0, 0.001, 0.0)),
                          mapSceneMesh(p + vec3(0.0, 0.0, 0.001)) - mapSceneMesh(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    float dt = 0.0;

    bool mesh = mod(floor(iTime), 2.0) == 1.0;

    for (int iter=0; iter < 250; iter++) {
        vec3 p = ro + rd * dt;

        p.yz *= mat2(COS_15, SIN_15, -SIN_15, COS_15);
        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        float d = mapScene(p);
        if (mesh) {
            d = mapSceneMesh(p);
        }

        if (d < 0.001) {
            vec3 n = getNormal(p);
            if (mesh) {
                n = getNormalMesh(p);
            }

            vec3 l = vec3(-0.58, 0.58, 0.58);

            n.yz *= mat2(COS_15, -SIN_15, SIN_15, COS_15);
            n.xz *= mat2(c, -s, s, c);

            fragColor.rgb = hue2rgb(0.5 * p.y);
            fragColor.rgb = mix(fragColor.rgb, vec3(0.0), smoothstep(0.05, 0.0, abs(fract(p.x) - 0.05)) + smoothstep(0.05, 0.0, abs(fract(p.z) - 0.05)));
            fragColor.rgb *= max(0.0, dot(n, l));
            break;
        }

        if (d > 100.0) {
            break;
        }

        dt += d;
    }
}

