/*
Use the mouse to rotate the camera and the arrow keys to move it.

Common: contains all of the settings and helper functions for texture lookups
Buffer A: contains the code for updating the camera position and yaw/pitch rotations
Image: contains the raymarching code
*/

// Set to true for a slight framerate boost:
const bool OPTIMIZE_RAYMARCHING = false;

float Shape(in vec3 p) {
	return sin(p.x) - sin(p.y) + sin(p.z);
}

float MeshShape(in vec3 p) {
    vec3 cell = floor(p);
    vec3 local = fract(p);

    float ldb = Shape(cell);                       // Left, Down, Back
    float rdb = Shape(cell + vec3(1.0, 0.0, 0.0)); // Right, Down, Back
    float ldf = Shape(cell + vec3(0.0, 0.0, 1.0)); // Left, Down, Front
    float rdf = Shape(cell + vec3(1.0, 0.0, 1.0)); // Right, Down, Front
    float lub = Shape(cell + vec3(0.0, 1.0, 0.0)); // Left, Up, Back
    float rub = Shape(cell + vec3(1.0, 1.0, 0.0)); // Right, Up, Back
    float luf = Shape(cell + vec3(0.0, 1.0, 1.0)); // Left, Up, Front
    float ruf = Shape(cell + vec3(1.0, 1.0, 1.0)); // Right, Up, Front

    return mix(mix(mix(ldb, rdb, local.x),
                   mix(ldf, rdf, local.x),
                   local.z),

               mix(mix(lub, rub, local.x),
                   mix(luf, ruf, local.x),
                   local.z),

               local.y);
}

float FractalMeshShape(in vec3 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += MeshShape(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

float mapScene(in vec3 p) {
    return (FractalMeshShape(p, 2.0, 4.0) - 0.3) * 0.3;
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + GRADIENT_STEP.xyy) - mapScene(p - GRADIENT_STEP.xyy),
                          mapScene(p + GRADIENT_STEP.yxy) - mapScene(p - GRADIENT_STEP.yxy),
                          mapScene(p + GRADIENT_STEP.yyx) - mapScene(p - GRADIENT_STEP.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 rayOrigin = getViewPosition();
    mat3 camera = getCamera(getViewDirection());
    vec3 rayDirection = normalize(FOCAL_LENGTH * camera[2] + uv.x * camera[0] + uv.y * camera[1]);

    fragColor = vec4(0.25, 0.25, 1.0, 1.0);
    float distanceTraveled = 0.0;
    for (int iteration=0; iteration < MAX_ITERATIONS; iteration++) {
        vec3 position = rayOrigin + rayDirection * distanceTraveled;
        float sceneDistance = mapScene(position);
        float multiplier = OPTIMIZE_RAYMARCHING ? 1.0 + distanceTraveled : 1.0;
        if (sceneDistance < MIN_HIT_DISTANCE * multiplier) {
            vec3 normal = getNormal(position);
            vec3 light = vec3(-0.58, 0.58, 0.58);
            float h = 0.5 + 0.5 * normal.y;
            vec3 color = mix(vec3(0.6, 0.3, 0.0), vec3(0.0, 1.0, 0.0), h * h * h);
            fragColor.rgb = color * max(0.0, dot(normal, light));
            fragColor.rgb += 0.3 * color;
            break;
        }

        if (distanceTraveled > MAX_TRACE_DISTANCE) {
            break;
        }

        distanceTraveled += sceneDistance;
    }
}

