/*
The camera rotation is stored as yaw/pitch rotations. Yaw is spin and pitch is tilt.
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(1.0, 0.0, 0.0, 1.0);

    if (iFrame > 0) {
        ivec2 address = ivec2(fragCoord);
        if (address == VIEW_POSITION_ADDR) {
            fragColor = vec4(getViewPosition(), 1.0);
            mat3 camera = getCamera(getViewDirection());

            if (keyUpPressed()) {
                fragColor.xyz += camera[2] * STEP_SIZE;
            }

            if (keyDownPressed()) {
                fragColor.xyz -= camera[2] * STEP_SIZE;
            }

            if (keyRightPressed()) {
                fragColor.xyz += camera[0] * STEP_SIZE;
            }

            if (keyLeftPressed()) {
                fragColor.xyz -= camera[0] * STEP_SIZE;
            }
        }

        if (address == VIEW_DIRECTION_ADDR) {
            vec2 viewDirection = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y * TWO_PI * ROTATION_SPEED;
            fragColor = vec4(viewDirection, 0.0, 1.0);
        }
    }
}

