#define MAX_STEPS 100
#define MAX_DIST 100.f
#define SURF_DIST .01f

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b - a;
	vec3 ap = p - a;

	float t = dot(ab, ap) / dot(ab, ab);
	t = clamp(t, 0., 1.);

	vec3 c = a + t*ab;
	return length(p-c) - r;
}

float sdTorus(vec3 p, vec2 r) {
	float x = length(p.xz) - r.x;
	return length(vec2(x, p.y)) - r.y;
}

float dBox(vec3 p, vec3 hsize) {
	return length(max(abs(p) - hsize, 0.f));
}

float smin( float a, float b, float k )
{
	float h = max(k-abs(a-b),0.0);
	return min(a, b) - h*h*0.25/k;
}

float smax( float a, float b, float k )
{
	float h = max(k-abs(a-b),0.0);
	return max(a, b) + h*h*0.25/k;
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCappedCylinder( vec3 p, float r, float h )
{
  vec2 d = abs(vec2(abs(length(p.xz)),p.y)) - vec2(h,r);
  d.x = abs(d.x) - 0.01;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdCappedTorus(vec3 p, vec2 sc, float ra, float rb)
{
//  p.x = abs(p.x);
  p.z = -p.z;
  float k = (sc.y*p.z>sc.x*p.y || p.z < 0.) ? dot(p.zy,sc) : length(p.zy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float GetDist(vec3 p) {
    //p = p//* vec3(0.8, 1.2, 0.8);
    vec3 c = p - vec3(0., 1.0, 0.);

	//float d = sdCappedCylinder(c, 0.3, 0.3);

    float bl1 = sdEllipsoid(c-vec3(0.2, 0., 0.), vec3(0.25, 0.35, 0.25));
    float bl2 = sdEllipsoid(c-vec3(-0.2, 0., 0.), vec3(0.25, 0.35, 0.25));
    float bls = smin(bl1, bl2, 0.2);

    float r = 1.3 + sin(iTime)/4.;

    float pn = sdCappedTorus(c-vec3(0., 0.15 - r, 0.), vec2(.8, 0.6), r, 0.2);

    float d = smin(bls, pn, 0.1);

	return d;
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	vec2 res = vec2(0.0,-1.0);

    float tmin = 0.5;
    float tmax = 20.0;

#if 1
	// raytrace bounding plane
	float tp = (3.5-ro.y)/rd.y;
	if( tp>0.0 ) tmax = min( tmax, tp );
#endif

	// raymarch scene
	float t = tmin;
	for( int i=0; i<256 && t<tmax; i++ )
	{
		vec2 h = vec2(GetDist(ro+rd*t), 0.);
		if(abs(h.x)<(0.0005*t))
		{
			res = vec2(t,h.y);
			break;
		}
		t += h.x;
	}

	return res;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
	vec2 e = vec2(0.01, 0);

	vec3 n = d - vec3(
		GetDist(p - e.xyy),
		GetDist(p - e.yxy),
		GetDist(p - e.yyx));

	return normalize(n);
}

float rand(float n)
{
	return fract(sin(n)*43758.5453);
}

float rand2d(vec2 v)
{
	vec2 K1 = vec2(23.14069263277926,2.665144142690225);
	return fract( cos( dot(v,K1) ) * 12345.6789 );
}

float rand3d(vec3 v)
{
	vec3 K1 = vec3(23.14069263277926, 2.665144142690225, 7.5739974548463465);
	return fract( cos( dot(v,K1) ) * 12345.6789 );
}

mat3 setCamera(in vec3 ro, in vec3 lookat)
{
	vec3 cw = normalize(lookat-ro);
	vec3 cp = vec3(sin(0.), cos(0.),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 resolution = iResolution.xy;
	vec2 uv = (fragCoord.xy-.5*resolution) / resolution.y;

	vec3 sky_col = vec3(0.3, 0.0, 0.0);
	vec3 col = sky_col;

	float light_coef = 1.;
	vec3 light_color = vec3(1., 0.8, 0.8);

	col = clamp((1.-length(uv-vec2(0., -0.5)) - 0.1*rand2d(uv+iTime)) * light_coef, 0., 1.) * light_color;

    vec2 mo = iMouse.xy/iResolution.xy;

    vec3 lookat = vec3(0., 1., 0.);
    vec3 ro = lookat + vec3( 2.5*cos(0.1*iTime + 7.0*mo.x), 0.8 + 1.0*mo.y, 2.5*sin(0.1*iTime + 7.0*mo.x) );
//	vec3 ro = lookat + vec3(-3.0f, 1.f, -2.0f);
    mat3 cam_mat = setCamera(ro, lookat);


	vec3 rd = cam_mat * normalize(vec3(uv.x+0., uv.y, 1));

	vec2 d = RayMarch(ro, rd);

	vec3 p = ro + rd * d.x;

	if (d.y > -0.5) {
		// moving light
		vec3 lightPos = vec3(-1.2, 1.1, -0.9);
		vec3 light_dir = lightPos - p;
		vec3 l = normalize(light_dir);
		vec3 normal = GetNormal(p);

		vec3 sky_l_dir = vec3(0, 1, 0);

		// light coef
		float main_light = clamp(dot(normal, l) - rand3d(p/*+iTime*/)*0.05, 0., 1.) * 0.5;

		col = vec3(0.4, 0.4, 0.4);

		// lightning
		vec3 lin = vec3(0.);
		lin += 2. * (0.1+light_coef * main_light) * light_color;

		col = col * lin;

		// gama
		col = pow(col, vec3(1./2.2));
	}


	// Output to screen
	fragColor = vec4(col,1.0);
}

