vec2 rotate2d(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

mat2 rot2D(float a)
{
    float c=cos(a);
    float s=sin(a);
    return mat2(c,-s,s,c);
