void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
	float zoom = 1.5;
	vec3 rayDirection = normalize( vec3( (fragCoord-iResolution.xy*.5)/iResolution.y, zoom ) );
    vec3 cameraTarget = vec3(1,0,1.5)*iTime + vec3(0,-1,0);
    vec3 cameraPosition = vec3(0,8,-12);
    float a = iTime/5.;
    cameraPosition.xz = cameraPosition.xz*cos(a) + cameraPosition.zx*sin(a)*vec2(1,-1);
    cameraPosition += cameraTarget;

    vec3 cameraForward = normalize( cameraTarget - cameraPosition );
    vec3 upAxis = vec3(0,1,0);
    vec3 cameraRight = normalize( cross( upAxis, cameraForward ) );
    vec3 cameraUp = cross( cameraForward, cameraRight );

    // rotate ray to camera orientation
    rayDirection = rayDirection.x*cameraRight + rayDirection.y*cameraUp + rayDirection.z*cameraForward;

    vec3 planeNormal = vec3(0,1,0);
    float planeOffset = 0.; // from world origin, in direction of plane normal

    // precompute some things
    float rayDotPlaneNormal = dot(rayDirection,planeNormal);
    float invRayDotPlaneNormal = 1. / rayDotPlaneNormal;
    float cameraDotPlaneNormal = dot(cameraPosition,planeNormal);
    float distanceToPlaneAlongNormal = planeOffset - cameraDotPlaneNormal; // note this is negative, so maybe shouldn't be called "distance"

	vec2 invTextureScale = .04*vec2(1.,iResolution.x/iResolution.y); // correct the aspect ratio

	// only support viewing the plane from in-front (even though it has depth it will break down when viewed edge-on)
    if ( rayDotPlaneNormal >= 0. || cameraDotPlaneNormal < planeOffset )
    {
    	fragColour = vec4(1);
        return;
    }

    // draw the displacement map as a series of slices through the texture, not a solid object
    float displacementHeight = 2.;
    int numSlices = 256;
    float sliceStep = displacementHeight / float(numSlices);

    vec2 textureCoordinate = vec2(0);
    float sliceProportion = 0.;
    float distanceToIntersection = 0.;
    float height = 0.;
    for ( int slice = 0; slice < numSlices; slice++ )
    {
    	sliceProportion = float(slice)/float(numSlices); // how far we are through the full stack of slices, in [0,1)

    	// how far along the ray do we have to travel to hit the plane
    	distanceToIntersection = distanceToPlaneAlongNormal * invRayDotPlaneNormal;

        vec3 intersectionPosition = cameraPosition + rayDirection*distanceToIntersection;

        textureCoordinate = intersectionPosition.xz*invTextureScale;

        // on firefox on my home PC it works without this, but I guess repeat texture mode doesn't working with frame buffers reliably
		textureCoordinate = fract(textureCoordinate);

		// point sample - might be better to have 2 versions of the texture with different filter modes rather than all this maths
        //fragColour = texelFetch(iChannel0,ivec2(textureCoordinate*iResolution.xy)%ivec2(iResolution.xy),0);
        fragColour = texture(iChannel1,textureCoordinate);
        height = FoldHeight(fragColour.a,textureCoordinate);

        // stop marching once we sample something at or above the march height
        if ( 1.-height < sliceProportion ) break;

        distanceToPlaneAlongNormal -= sliceStep; // subtract because distance is negative, easy to explain with a diagram
    }

    // sorry for the cryptic maths here
    vec2 d = vec2(1,0)/iResolution.xy;
    vec3 normal = cross(
    				vec3(0,
    				(
                    	FoldHeight( texture(iChannel0,textureCoordinate+d.yx).a, textureCoordinate+d.yx )
    					-FoldHeight( texture(iChannel0,textureCoordinate-d.yx).a, textureCoordinate-d.yx )
                     ) * displacementHeight
                    ,d.x / invTextureScale.x)
                    ,
    				vec3(d.x / invTextureScale.x,
                    (
                        FoldHeight( texture(iChannel0,textureCoordinate+d.xy).a, textureCoordinate+d.xy )
                        -FoldHeight( texture(iChannel0,textureCoordinate-d.xy).a, textureCoordinate-d.xy )
                     ) * displacementHeight
                    ,0)
                   );
// can get better normals for walls with less bevel if use Channel1 and smaller d, but that will break on smooth curves

	normal = normalize(normal);

	vec3 reflectDirection = reflect(rayDirection,normal);
    vec3 reflectionColour = texture(iChannel2,reflectDirection).rgb;
    vec3 reflectionBlurred = textureLod(iChannel2,reflectDirection,3.).rgb;

    // darken reflections below the horizon so it looks like the plane's reflecting itself
    reflectionColour *= mix( vec3(.3), reflectionColour, smoothstep(.0,.1,reflectDirection.y) );
    reflectionBlurred *= mix( vec3(.3), reflectionColour, smoothstep(.0,.1,reflectDirection.y) );

    // fake - desaturate metallic reflections
    reflectionBlurred = mix( reflectionBlurred.ggg, reflectionBlurred, .1 );


    float metallicity = step(.7,fract(fragColour.a*11.));

	// lighting
    vec3 lighting = vec3(.1,.2,.3) + vec3(2.5,2.2,2)*1.5 * max(0.,dot(normal,normalize(vec3(2,1,3))));
    fragColour.rgb *= mix( lighting, reflectionBlurred, metallicity );

    // reflection
    float fresnel = .02+.98*pow(min(1.+dot(rayDirection,normal),1.),5.);
    fragColour.rgb = mix( fragColour.rgb, reflectionColour, fresnel );

	// darken deeper parts
    fragColour = mix( fragColour, vec4(0), sliceProportion ); 

    // atmospheric fog
    fragColour = mix( vec4(1.), fragColour, exp2( -distanceToIntersection * vec4(.1,.125,.2,1)/20. ) );

    // stretch contrast
    fragColour = clamp(fragColour*1.-.04,0.,1.);

//	fragColour.rgb = .5+.5*normal/(abs(normal)+.1);//
    fragColour.rgb = pow(fragColour.rgb,vec3(1./2.2));

/*
// debug greeble buffer
    fragColour = texelFetch( iChannel0, ivec2(fragCoord + iTime*vec2(80,30))%ivec2(iResolution.xy), 0 );
//    fragColour = step(vec4(abs(fract(iTime*.1)*2.-1.)),fragColour);
*/
}

