       vec4 a = A(v);
       float l = length(Q.xy-a.xy);
       float s = 
       smoothstep(4.,3.,l)*
       smoothstep(10.,20.,length(v-U));
       if (l>0.)  {
           f += 10.*s*(Q.xy-a.xy)/(l);
            if (length(a.zw-Q.zw)>0.)
           f += .5*s*(a.zw-Q.zw)*dot(a.zw-Q.zw,a.xy-Q.xy)/length(a.xw-Q.zw)/l;
       }
   }
   Q.zw += 1e-2*f;
   Q.w -= 3e-4;
   Q.xy += Q.zw*sqrt(.5/(1.+dot(Q.zw,Q.zw)));
   if (length(Q.zw)>1.) Q.zw = normalize(Q.zw);
   if (iMouse.z>0.){ float d = dot((Q.xy-iMouse.zw),Q.xy-iMouse.zw);
       Q.zw += 50.*(Q.xy-iMouse.zw)/(1e-9+sqrt(d)*d);}
   if (Q.x < 1.) Q = vec4(1.,Q.y,-Q.z,Q.w);
   if (R.x-Q.x < 1.) Q = vec4(R.x-1.,Q.y,-Q.z,Q.w);
   if (Q.y < 1.) Q = vec4(Q.x,1.,Q.z,-Q.w);
   if (R.y-Q.y < 1.) Q = vec4(Q.x,R.y-1.,Q.z,-Q.w);


   if (iFrame < 1) 
       Q = vec4(U,0,.6);

   vec2 u = mod(U,vec2(M,M))/M*3.5-1.75;
   vec2 z = ceil(U/M)*M;
   z = sin(vec2(1,123)*z.yx);
   for (int i = 0; i < 10; i++) {
       u = vec2(u.x*u.x-u.y*u.y,2.*u.x*u.y)+vec2(1,.2)*z;
       if (length(u)>4.) break;
   }
   if (length(u)>4.) Q = vec4(-R,0,0);

}

