vec2 rotate(in vec2 p, in float t)
{
	return p * cos(-t) + vec2(p.y, -p.x) * sin(-t);
}

float map(in vec3 p)
{
    return DE(p);//DE() is defined in "Common"
}

vec3 calcNormal(in vec3 p)
{
	const vec2 e = vec2(0.0001, 0.0);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
		map(p + e.yyx) - map(p - e.yyx)));
}

float march(in vec3 ro, in vec3 rd)
{
	const float maxd = 10.0;
	const float precis = 0.0001;//cusps look better with high precision
    float h = precis * 2.0;
    float t = 1.0;
	float res = -1.0;
    for(int i = 0; i < 256; i++)
    {
        if(h < precis*t || t > maxd) break;
	    h = map(ro + rd * t);
        t += h;
    }
    if(t < maxd) res = t;
    return res;
}

vec3 transform(in vec3 p)
{
    vec4 mouse = UI(35);//R.y;
    //mouse.xy = clamp(mouse.xy, vec2(-180.,-90.), vec2(180.,90.));

    p.zx = rotate(p.zx, iTime * 0. - (mouse.x)*PI/360.);
    p.yz = rotate(p.yz, iTime * 0. + (mouse.y)*PI/360.);
    return p;
}

