def Dervish(x,y,z):
   z=-z
   c=math.sqrt(5-math.sqrt(5))/2                            #constant
   r=(1+3*math.sqrt(5))/4
   q=(1-c*z)*(x**2+y**2-1+r*z**2)**2;                       #not a constant
   a=-(8/5)*(1+1/math.sqrt(5))*math.sqrt(5-math.sqrt(5))    
   Pi=math.pi
