void mainImage( out vec4 O, vec2 u ) // --- simulates trajectories
{
    vec2 M = iMouse.xy; 
    if ( M != vec2(0) ) M = ( 2.*iMouse.xy - R ) / R.y;
    M += .002* cos(12.*iTime+vec2(0,11));
    if (u==vec2(.5,N+.5)) O.xy = M;
    if ( u.y > N ) return;                    // simulates only N lines

    if (u.x==.5) { 
        O = vec4( -R.x/R.y,                   // init position
                   u.y/N *2.-1., // + 2e-4 * sin(12.*iTime),
                   1,0);                      // init dir
        return;
    }
                                              // --simulates physics
    O = T(u-vec2(1,0));  // previous state.  O.xy = pos, O.zw = dir (normalized)
    // Physics adapted from set111's 3D black hole https://www.shadertoy.com/view/tsBXW3
    vec2 D = O.xy - M;
    float r = length(D);                      // distance to BH
    float d  = min( .92,// * abs(O.y /O.w) ,  // conservative distance to disk (y==0)   
                   min( r*.5,                 // limit step size far from to BH 
                        r*.1 + .05*r*r/Size   // limit step size close to BH  winner for r<5
                  )   );
    float F = Size * .625 /(r*r);             // bending force
    O.zw = normalize( O.zw - d * F* D/r );    // bend ray towards BH
    O.xy += d * O.zw;                
}

