        float F = -Size * .625 /(r*r);             // bending force
        O.zw =  normalize(O.zw - d * F* O.xy/r ); // bend ray towards BH
        O.xy += d * O.zw;                
    }
    else if (u.x==1.5)                       // save prev position for drawing
        O = T(vec2(.5,u.y));
    else {                                   // draw rays (new segment)
        float d = 1e5, i_, i=0., v;
        for (; i < R.y; i += 5. ) {
            v = line( U, T(vec2(1,i)).xy, T(vec2(0,i)).xy);
            if ( v < d ) d = v, i_ = i;
        }
        O += S( sqrt(d) -0./R.y ) * hue(i_/R.y);
    }   
