void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (storeData(fragCoord, SCREEN_COLOR_ADDR)) {
        fragColor = vec4(0.0, 0.0, 1.0, 1.0); // Initial value
    }

    if (iFrame > 0) { // After initialization
        if (storeData(fragCoord, SCREEN_COLOR_ADDR)) {
            fragColor = fetchData(iChannel0, SCREEN_COLOR_ADDR); // Load value from previous frame

            // Update:
            if (iMouse.z > 0.0) {
                // When the mouse is pressed fade into red
                fragColor.b -= 0.05;
                fragColor.r += 0.05;
                fragColor = clamp(fragColor, 0.0, 1.0); // Clamp to prevent color from changing to slowly the opposite way
            }

            else {
                // Otherwise fade back to blue
                fragColor.b += 0.05;
                fragColor.r -= 0.05;
                fragColor = clamp(fragColor, 0.0, 1.0); // Clamp to prevent color from changing to slowly the opposite way
            }
        }
    }
}

