// 3d ball physics
// heavyly relying on this shader by hubbe:
// https://www.shadertoy.com/view/XdtSWn



#define maxballs 10
#define radius 0.3



// Return a "random" number.
float rand(vec2 co)
{
    float a = 12.9898;
    float b = 78.233;
    float c = 43758.5453;
    float dt= dot(co.xy ,vec2(a,b));
    float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=fragCoord.xy/iResolution.xy;   
    vec4 pos = vec4(0);
    vec3 spd = vec3(0);

    if ((fract(iTime/3.43))<=0.005)
    {
        pos.x= 2.*rand(-uv+iTime)-1.;
    	pos.y= .5*rand( uv+iTime)-1.1;
        pos.z= 2.*rand(-uv.yx+iTime)-1.; 
        spd.x=.06*rand( uv+iTime);
        spd.y=.06*rand(-uv+iTime);
        spd.z=.06*rand( uv.yx+iTime);
        pos.w = 0.1;
        if (rand( uv+iTime)>0.6) pos.w=1.0;
    }    

    else 
    {

    	pos = texture(iChannel0, vec2(float(uv.x),0.)); // get position + type
        spd = texture(iChannel0, vec2(float(uv.x),1.)).xyz; // get speed  

//		calculate touching balls :-)        
    	for (int i = 0; i<maxballs;i++)
    	{
    		vec4 posother = texture(iChannel0, vec2((float(i)+0.5)/iResolution.x,0.));
            vec3 spdother = texture(iChannel0, vec2((float(i)+0.5)/iResolution.x,1.)).xyz;

            if(pos.xyz==posother.xyz) continue;
        	float dista = length(posother.xyz - pos.xyz);
        	if (dista < radius * 2.0) 
        	{
            pos.xyz= pos.xyz + (posother.xyz-pos.xyz) * radius * 2.001 / dista;  
            pos.w=posother.w;
            spd-=0.2*spdother;
            }
        }

    pos.xyz -= spd.xyz;
    spd.xyz *= 1.;
    spd.y -= 0.003;

    // If we reach the bottom, invert the speed in the y direction.
    if (pos.y >= 0.7) { spd.y = abs(spd.y * .7); }
    // if (pos.y <= -0.7) { spd.y = -abs(spd.y * .7); }

    // If we reach left/right wall, invert the speed in the x direction.         
    if (pos.x >= 1.5) { spd.x = abs(spd.x * .7); }  
    if (pos.x <= -1.5) { spd.x = -abs(spd.x * .7); }  

    // If we reach the front/back invert the speed in the z direction.
    if (pos.z >= 4.) { spd.z = abs(spd.z * .7); }
    if (pos.z <= -2.) { spd.z = -abs(spd.z * .7); }

    }

    if (fragCoord.y == 0.5) fragColor = vec4(pos); // write position
    else fragColor = vec4(spd,1.);  // write speed

}

