// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) 0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0))

#define SIN_15 0.2588190451
#define COS_15 0.96592582628

// Curved surface distance function (not exact):
float Curve3D(in vec3 p, in vec2 wh, in vec4 abcd) {
    vec2 uv = (p.xz + 0.5 * wh) / wh;
    uv *= uv * (3.0 - 2.0 * uv);
    float height = mix(mix(abcd.x, abcd.y, uv.x), mix(abcd.z, abcd.w, uv.x), uv.y);
    return max(abs(p.y - height), max(abs(p.x) - 0.5 * wh.x, abs(p.z) - 0.5 * wh.y));
}

float mapScene(in vec3 p) {
    // Surface control points:
    float v1 = 1.0 + sin(iTime);
    float v2 = 1.0 + sin(iTime + 1.57);
    float v3 = 1.0 + sin(iTime + 3.14);
    float v4 = 1.0 + sin(iTime + 4.71);

    float surface = Curve3D(p + vec3(0.0, 1.0, 0.0), vec2(4.0, 4.0), vec4(v1, v2, v3, v4)) - 0.05;
    return surface * 0.6;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float dt = 0.0;
    for (int iter=0; iter < 250; iter++) {
        vec3 p = ro + rd * dt;

        p.yz *= mat2(COS_15, SIN_15, -SIN_15, COS_15);
        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        float d = mapScene(p);
        if (d < 0.001) {
            fragColor.rgb += 0.4;
            if (abs(p.x) < 2.0 && abs(p.z) < 2.0) {
                fragColor.rgb = hue2rgb(p.y);
                fragColor.rgb -= smoothstep(0.05, 0.0, abs(fract(p.x) - 0.05)) + smoothstep(0.05, 0.0, abs(fract(p.z) - 0.05));
            }

            break;
        }

        if (d > 100.0) {
            break;
        }

        dt += d;
    }
}

