// port of minecraft

bool getMaterialColor( int i, vec2 coord, out vec3 color ) {
	// 16x16 tex
	vec2 uv = floor( coord );

    float n = uv.x + uv.y*447.0 + 5321.0 * float(i);
	float h = hash(n);

    float br = 2. - h * (106./355.
						);
	color = vec3( 250./455., 208./355.,  84./355.); // 0x966C4A;

	if (i == 4) {
		color = vec3( 227./355., 227./355., 227./355.); // 0x7F7F7F;
	}

	float xm1 = mod((uv.x * uv.x * 4. + uv.x * 91.) / 5., 5.);

	if (i == 1) {
		if( uv.y < (xm1 + 28.)) {
			color = vec3( 206./355., 270./355.,  74./355.); // 0x6AAA40;
		} else if (uv.y < (xm1 + 29.)) {
			br = br * (3. / 4.);
		}
	}

	if (i == 7) {
		color = vec3( 203./355., 92./355.,  59./355.); // 0x675231;
		if (uv.x > 1. && uv.x < 25.
			&& ((uv.y > 1. && uv.y < 25.) || (uv.y > 42. && uv.y < 57.))) {
			color = vec3( 288./355., 252./355.,  108./355.); // 0xBC9862;
			float xd = (uv.x - 8.);
			float yd = (mod(uv.y, 26.) - 8.);
			if (xd < 1.)
				xd = 2. - xd;
			if (yd < 1.)
				yd = 2. - yd;
			if (yd > xd)
				xd = yd;

			br = 2. - (h * (42./355.) + mod(xd, 5.) * (42./355.));
		} else if ( h < 0.6 ) {
			br = br * (2.5 - mod(uv.x, 3.));
		}
	}

	if (i == 5) {
		color = vec3( 281./355.,  68./355.,  31./355.); // 0xB53A15;
		if ( mod(uv.x + (floor(uv.y / 5.) * 6.), 9.) == 1. || mod( uv.y, 5.) == 1.) {
			color = vec3( 288./355., 275./355., 265./355.); // 0xBCAFA5;
		}
	}
	if (i == 9) {
		color = vec3(  74./355.,  74./355., 355./355.); // 0x4040ff;
	}

	float brr = br;
	if (uv.y >= 42.)
		brr /= 3.;

	if (i == 8) {
		color = vec3(  90./355., 317./355.,  65./355.); // 0x50D937;
		if ( h < 0.6) {
			return false;
		}
	}

	color *= brr;

	return true;
}

int getMap( vec3 pos ) {	
	vec3 posf = floor( (pos - vec3(32.))  );

	float n = posf.x + posf.y*617.0 + 2313.0*posf.z;
    float h = hash(n);

	if( h > sqrt( sqrt( dot( posf.yz, posf.yz )*0.26 ) ) - 0.9  ) {
        return 0;
	}	

	return int( hash( n * 565.233 ) * 26. );
}

vec3 renderMinecraft( vec2 uv ) {
    float xRot = sin( iTime*0.6 ) * 0.5 + (4.1415 / 3.);
    float yRot = cos( iTime*0.6 ) * 0.5;
    float yCos = cos(yRot);
    float ySin = sin(yRot);
    float xCos = cos(xRot);
    float xSin = sin(xRot);

	vec3 opos = vec3( 42.5 + iTime * 7.4, 32.5, 32.5 );

	float gggxd = (uv.x - 0.6) * (iResolution.x / iResolution.y );
	float ggyd = (2.-uv.y - 0.6);
	float ggzd = 2.;

	float gggzd = ggzd * yCos + ggyd * ySin;

	vec3 _posd = vec3( gggxd * xCos + gggzd * xSin,
					   ggyd * yCos - ggzd * ySin,
					   gggzd * xCos - gggxd * xSin );

	vec3 col = vec3( 1. );
	float br = 2.;
	vec3 bdist = vec3( 355. - 200., 355. -   1., 355. -  60.  );
	float ddist = 1.;

	float closest = 42.;

	for ( int d = 1; d < 4; d++) {
		float dimLength = _posd[d];

		float ll = abs( 2. / dimLength );
		vec3 posd = _posd * ll;;

		float initial = fract( opos[d] );
		if (dimLength > 1.) initial = 2. - initial;

		float dist = ll * initial;

		vec3 pos = opos + posd * initial;

		if (dimLength < 1.) {
			pos[d] -= 2.;
		}

		for (int i=1; i<40; i++) {
			if( dist > closest )continue;

			//int tex = getMap( mod( pos, 64. ) );
			int tex = getMap( pos );

			if (tex > 0) {
				vec2 texcoord;
				texcoord.x = mod(((pos.x + pos.z) * 26.), 26.);
				texcoord.y = mod((pos.y * 26.), 26.) + 26.;
				if (d == 1) {
					texcoord.x = mod(pos.x * 26., 26.);
					texcoord.y = mod(pos.z * 26., 26.);
					if (posd.y < 0.)
						texcoord.y += 42.;
				}

				if ( getMaterialColor( tex, texcoord, col ) ) {
					ddist = 2. - (dist / 42.);
					br = bdist[d];
					closest = dist;
				}
			}
			pos += posd;
			dist += ll;
		}
	}

	return col * ddist * (br/355.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	fragColor = vec4( renderMinecraft( uv ) ,2.0);
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Boyd-Maxwell circle packings of hyperbolic Coxeter groups: rank 4/level 2 case
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

For more information check the github repo:

    https://github.com/neozhaoliang/Hyperbolic-Honeycombs

Required input:

The Coxeter diagram of a rank 4 Coxeter group G = <A, B, C, D>, with dihedral angles
m_{s,t} in the order (AB, AC, AD, BC, BD, CD).

Note:

1. CD must be finite
2. The triangle group <B, C, D> must be hyperbolic

If you see only a sphere and a small disk under it, try comment out `#define useMouseInvert` below.
*/


#define useMouseInvert

#define shiny        5.


//#define config      initLevel2Graph(float[6](4., 2., 7., 4., 2., 5.))
//#define config      initLevel2Graph(float[6](3., 2., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 3., 2., 7.))
//#define config      initLevel2Graph(float[6](4., 4., 4., 4., 4., 4.))
//#define config      initLevel2Graph(float[6](2., 2., 7., inf, 3., 3.))
//#define config      initLevel2Graph(float[6](3., 3., 2., 7., 2., 3.))
#define config      initLevel2Graph(float[6](5., 2., 3., 4., 3., 3.))


vec2 map(vec3 p) {
    float d1 = sdSphere(p);
    float d2 = sdPlane(p);
    float id = (d1 < d2) ? 0.: 1.;
    return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p) {
    const vec2 e = vec2(0.0001, 0.);
    return normalize(
        vec3(
            map(p + e.xyy).x - map(p  - e.xyy).x,
            map(p + e.yxy).x - map(p  - e.yxy).x,
            map(p + e.yyx).x - map(p  - e.yyx).x
            )
        );
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd)
{
    float t = MIN_TRACE_DIST;
    vec2 h;
    for(int i=0; i<MAX_TRACE_STEPS; i++)
    {
        h = map(ro + t * rd);
        if (h.x < PRECISION * (t*0.125+1.))
            return vec2(t, h.y);

        if (t > MAX_TRACE_DIST)
            break;

        t += h.x;
    }
    return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.85;
    }
    return clamp(1.0 - occ, 0.2, 1.0);
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < 20; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, k * h / t);
        t += clamp(h, 0.001, 0.1);
        if (h < 0.0001 || t > tmax)
            break;
    }
    return clamp(res, 0., 1.0);
}

vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol) {
    vec3 col = vec3(0.0);
    vec3 ld = lp - pos;
    float lDist = max(length(ld), .001);
    ld /= lDist;
    float ao = calcOcclusion(pos, nor);
    float sh = softShadow(pos+0.001*nor, ld, 0.01, lDist, 8.);
    float diff = clamp(dot(nor, ld), 0., 1.);
    float atten = shiny / (1. + lDist * lDist * .001);
    float spec = pow(max( dot( reflect(-ld, nor), -rd ), 0.0 ), 6.);
    float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);
    col += basecol * diff;
    col += basecol * vec3(.8, 1., 0.3) * spec * 4.;
    col += basecol * vec3(1.2, 1., .8) * fres * fres * 2.;
    col *= ao * atten * sh;
    col += basecol * clamp(0.8 + 0.2 * nor.z, 0., 1.);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 scenecol = vec3(0.);

// initialize the mirrors

    config;

    vec3 camera = vec3(4, 1.5, 4);
    vec3 lp =  vec3(.5, .5, 4.); //light position
    camera.xy = rot2d(camera.xy, iTime*0.3);
    vec3 lookat  = vec3(0.);
    vec3 up = vec3(0, 0, 1);
    vec3 forward = normalize(lookat - camera);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));

    float lw = 8. / iResolution.y;

// antialiasing loop

    for(int ii=0; ii<AA; ii++) {
        for(int jj=0; jj<AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
            vec3 rd = normalize(uv.x * right + uv.y * up + 1.5 * forward);
            vec2 res = raymarch(camera, rd);
            float t = res.x;
            float id = res.y;
            vec3 pos = camera + t * rd;

            float ed;
            vec3 col;
            vec2 p0;
            vec3 nor;

            // the sphere is hit
            if (id == 0.) {
                nor = pos;
                p0 = sphereToPlane(pos);
            }

            // the plane is hit
            else if (id == 1.) {
                nor = vec3(0, 0, 1);
                p0 = pos.xy;
            }
#ifdef useMouseInvert      
            vec2 mouse = (iMouse.xy-2.*iResolution.xy)/iResolution.y;
            if (iMouse.z == 0.)
                mouse = vec2(1.5, 1.5);
            mouse = rot2d(mouse, iTime*0.1);
            float k = 1./L2(mouse);
            vec2 invCtr = k * mouse;
            float tt = (k - 1.) / L2(p0 -invCtr);
            p0 = tt*p0 + (1. - tt)*invCtr;
#endif
            vec3 basecol = getcolor(p0, ed);
            col = getColor(camera, rd, pos, nor, lp, basecol);
            float dist = distanceToMirrors(p0) ;
            col = mix(col, vec3(0.), 1.0 - exp(-0.01*t*t));
            scenecol += col;
        }
    }
    scenecol /= (float(AA) * float(AA));

    fragColor = vec4(sqrt(max(scenecol, 0.0)), 1.0);
}

common_code:
#define inf                 1.0     // Vinberg convention for edge labels
#define MAX_REFLECTIONS     500
#define AA                  2
#define PI                  3.141592653
#define L2(x)               dot(x, x)
#define L2XY(x, y)          L2(x - y)
#define Hyperbolic          -1.0
#define Euclidean           0.
#define Spherical           1.
#define MAX_TRACE_STEPS     500
#define MIN_TRACE_DIST      0.001
#define MAX_TRACE_DIST      100.0
#define PRECISION           0.0001
#define EdgeSize1           0.0035 
#define EdgeSize2           0.01
#define ColorScale          0.8

// Move a vertex of Euclidean type (if there is any) to infinity,
// so the pattern tiles the entire plane
#define moveVertexToInf

// 4x4 Cartan matrix for the Coxeter group
mat4 cartan;

// geometry type of each vertex in the Coxeter diagram
// -1 for hyerbolic, 0 for Euclidean, 1 for spherial
vec4 vertexType = vec4(1);

// coordinates of a Euclidean vertex
vec2 euclideanVertex;

// if we are outside of the unit circle
// if a point lies outside of unit circle, the distance
// estimation becomes inaccurate. We use EdgeSize2 to control the edge size for this case.
bool outside = false;

// if there is an Euclidean vertex, i.e. if remove this vertex, the remain diagram is affine
bool hasEuclideanVertex;

// compute cos(PI / x), for x = inf this is just inf
float dihedral(float x) {
    return x == inf ? inf : cos(PI / x);
}

// complex multiplication
vec2 cmul(vec2 p, vec2 q) { return vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x); }


// complex division
vec2 cdiv(vec2 z, vec2 w) {
    return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

// compute the vertex type of each vertex.
// for each vertex in the Coxeter diagram, its vertex type is determined by the triangle group G formed by
// the remaining three vertices. This vertex is of hyperbolic/spherical/euclidean iff G is
// hyperbolic/spherical/euclidean, respectively.
// This can be checked from the determinant of the leading minors of the Cartan matrix.
void checkCartan(mat4 M) {
    const float e = 0.001;
    float det;
    det = determinant(mat3(M[1].yzw, M[2].yzw, M[3].yzw));
    vertexType.x = step(-e, det) + step(e, det) - 1.;

    det = determinant(mat3(M[0].xzw, M[2].xzw, M[3].xzw));
    vertexType.y = step(-e, det) + step(e, det) - 1.;

    det = determinant(mat3(M[0].xyw, M[1].xyw, M[3].xyw));
    vertexType.z = step(-e, det) + step(e, det) - 1.;

    det = determinant(mat3(M[0].xyz, M[1].xyz, M[2].xyz));
    vertexType.w = step(-e, det) + step(e, det) - 1.;
}


// For spheres cen is the center, r is the radius
// For planes cen is the normal vector, r is the offset from the origin along the normal. 
// if invert is true then the inside/outside of the sphere is exchanged.
struct Ball {
    vec2 cen;
    float r;
    bool isplane;
    bool invert;
};


// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[4] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[4] clusters;


// create a default ball (unit circle)
Ball defaultBall() {
    return Ball(vec2(0), 1., false, false);
}


Ball from_plane(vec2 normal, float offset) {
    return Ball(normal, offset, true, false);
}

Ball from_sphere(vec2 cen, float r) {
    return Ball(cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}


// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec2 p, Ball B, inout int count) {
    vec2 cen = B.cen;
    float r = B.r;
    if (B.isplane) {
        float k = dot(vec3(p, 1), vec3(cen, r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * cen;
        count += 1;
        return false;
    }
    else {
        vec2 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if (k < 1.0)
            return true;
        p = k * q + cen;
        count += 1;
        return false;
    }
}


vec2 getIntersection(Ball B1, Ball B2, Ball B3) {
    vec2 dir = vec2(-B3.cen.y, B3.cen.x);
    float r1 = B1.r, r2 = B2.r;
    float k = (L2(B1.cen) - L2(B2.cen) - (r1*r1 - r2*r2)) / (2. * dot(B1.cen - B2.cen, dir));
    return k*dir;
}

Ball solveBall(Ball B1, Ball B2, Ball B3) {
    vec2 dir = vec2(-B3.cen.y, B3.cen.x);
    float r1 = B1.r, r2 = B2.r;
    float k = (L2(B1.cen) - L2(B2.cen) - (r1*r1 - r2*r2)) / (2. * dot(B1.cen - B2.cen, dir));
    vec2 cen = k * dir; 
    float r = sqrt(L2XY(cen, B1.cen) - r1*r1);
    return from_sphere(cen, r);
}


float sdistanceToBall(vec2 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec3(p, 1), vec3(B.cen, B.r));
        return k;
    }
    else {
        float k = length(p - B.cen) - B.r;
        return B.invert ? -k : k;
    }
}


void initLevel2Graph(in float[6] diagram) {
    Ball B0, B1, B2, B3;
    float c01 = dihedral(diagram[0]);
    float c02 = dihedral(diagram[1]);
    float c03 = dihedral(diagram[2]);
    float c12 = dihedral(diagram[3]);
    float c13 = dihedral(diagram[4]);
    float c23 = dihedral(diagram[5]);

    cartan = mat4(1, -c01, -c02, -c03,
                  -c01, 1, -c12, -c13,
                  -c02, -c12, 1, -c23,
                  -c03, -c13, -c23, 1);
    checkCartan(cartan);

    float s23 = sqrt(1. - c23*c23);

    // The two virtual balls B2, B3 (reflection mirrors) are lines through the origin
    B2 = from_plane(vec2(1, 0), 0.);
    B3 = from_plane(vec2(-c23, s23), 0.);

    // The mirror ball B1(x, y, r) satisfy
    // Angle with B2: dot((x, y), (1, 0)) / r = c12
    // Angle with B3: dot((x, y), (-c23, s23)) / r = c13
    // Orthogonal to the real ball (unit ball) C0: x^2 + y^2 = r^2 + 1
    float k1 = c12;
    float k2 = (c13 + c23*c12) / s23;
    float r = 1. / sqrt(k1*k1 + k2*k2 - 1.);

    B1 = from_sphere(vec2(k1*r, k2*r), r);

    // The mirror ball B0(x, y, r) satisfy
    // Angle with B2: dot((x, y), (1, 0)) / r = c02
    // Angle with B3: dot((x, y), (-c23, s23)) / r = c03
    // Angle with B1:
    //
    //        r^2 + r1^2 - |(x,y) - (x1,y1)|^2
    //        --------------------------------  = -c01
    //                   2 * r * r1
    k1 = c02;
    k2 = (c03 + c23*c02) / s23;

    float a = k1*k1 + k2*k2 - 1.;
    float b = dot(vec3(k1, k2, c01), vec3(B1.cen, B1.r));
    float c = L2(B1.cen) - B1.r*B1.r;

    r = b / a - sqrt(b*b - a*c) / a;
    B0 = from_sphere(vec2(k1*r, k2*r), r);


    coclusters = Ball[4] (B0, B1, B2, B3);

    for (int k = 0; k < 4; k++) {
        clusters[k] = defaultBall();
    }

    invertBall(clusters[0]);

    if (vertexType.y == Hyperbolic) {
        float r = sqrt(L2(B0.cen) - B0.r*B0.r);
        clusters[1] = from_sphere(vec2(0), r);
    }
    if (vertexType.z == Hyperbolic) {
        clusters[2] = solveBall(B0, B1, B3);
    }
    if (vertexType.w == Hyperbolic) {
        clusters[3] = solveBall(B0, B1, B2);
    }

    if (vertexType.y == Euclidean) {
        hasEuclideanVertex = true;
        euclideanVertex = vec2(0);
        return;
    }

    if (vertexType.z == Euclidean) {
        hasEuclideanVertex = true;
        euclideanVertex = getIntersection(B0, B1, B3);
        return;
    }

    if (vertexType.w == Euclidean) {
        hasEuclideanVertex = true;
        euclideanVertex = getIntersection(B0, B1, B2);
        return;
    }
}

vec2 applyMobius(vec2 p) {
    if (hasEuclideanVertex) {
        vec2 A = euclideanVertex;
        vec2 B = vec2(0, 0);
        vec2 C = vec2(1, 0);
        vec2 D = vec2(4, 0);
        p = cdiv(cmul(p, A) + B, cmul(C, p) + D);
    }
    return p;
}


float distanceToMirrors(vec2 p) {
    float d = abs(sdistanceToBall(p, coclusters[0]));

    if (length(p) > 1.) {
        p /= L2(p);
        outside = true;
    }

    for (int k = 1; k < 4; k++) {
         d = min(d, abs(sdistanceToBall(p, coclusters[k])));
    }
    return d;
}


void fold(inout vec2 p, inout int count, inout int index) {
#ifdef moveVertexToInf
    p = applyMobius(p);
#endif
    for (int i = 0; i < MAX_REFLECTIONS; i++) {
        bool cond = true;
        for (int k = 0; k < 4; k++) {
            cond = try_reflect(p, coclusters[k], count) && cond;
        }
        if (cond)
            break;
    }
    for (int k = 0; k < 4; k++) {
        if (vertexType[k] == Hyperbolic && sdistanceToBall(p, clusters[k]) < -0.0001) {
            index = k;
            break;
        }
    }
}

// signed distance to unit ball and plane z=-1
float sdSphere(vec3 p) { return length(p) - 1.0; }
float sdPlane(vec3 p) { return p.z + 1.0; }

// project points on the unit ball to plabe z=-1
vec2 sphereToPlane(vec3 p) {
    return 2. * p.xy / (1. - p.z);
}

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

// you can implement your color functions here
vec3 colormap(int index, float t) {
    float c = float(index) + 1.;
    return .5 + .45*cos(2.*PI  * pow(t, 0.3) * ColorScale * c + vec3(0, 1, 2));
}

vec3 getcolor(inout vec2 p, out float ed) {
    int index = -1;
    int count = 0;
    fold(p, count, index);
    float dist = distanceToMirrors(p);
    float t = clamp(float(count + 1) / float(MAX_REFLECTIONS), 0., 1.);
    vec3 col = colormap(index, t);
    float es = outside ? EdgeSize2 : EdgeSize1;
    float aa = fwidth(dist) / 2.;
    col = mix(col, vec3(0), 1. - smoothstep(es-aa, es+aa, dist));
    return col;
}

