/*
Use the mouse to rotate the camera and the arrow keys to move it.

Common: contains all of the settings and helper functions for texture lookups
Buffer A: contains the code for updating the camera position and yaw/pitch rotationa
Image: contains the raymarching code (for a more in-depth raymarching tutorial, visit Jamie Wong's amazing website: http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/)
*/

float mapScene(in vec3 p) {
    p = mod(p, 2.0) - 1.0; // Repeat the scene infinitely with a spacing of 2 units (the -1 is to center it)
    return length(p) - 0.25; // Sphere with a radius of 0.25 units
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + SMALL_STEP.xyy) - mapScene(p - SMALL_STEP.xyy),   // How much the distance changes in the x direction
                          mapScene(p + SMALL_STEP.yxy) - mapScene(p - SMALL_STEP.yxy),   // How much the distance changes in the y direction
                          mapScene(p + SMALL_STEP.yyx) - mapScene(p - SMALL_STEP.yyx))); // How much the distance changes in the z direction
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Center and normalize uv (with aspect ratio so there is no squashing)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Generate a ray for the pixel:
    vec3 rayOrigin = getViewPosition();
    mat3 camera = getCamera(getViewDirection());
    vec3 rayDirection = normalize(FOCAL_LENGTH * camera[2] + uv.x * camera[0] + uv.y * camera[1]);

    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    float distanceTraveled = 0.0;
    for (int iteration=0; iteration < MAX_ITERATIONS; iteration++) {
        vec3 position = rayOrigin + rayDirection * distanceTraveled;
        float sceneDistance = mapScene(position);

        // Is the ray close enough to be hitting?
        if (sceneDistance < MIN_HIT_DISTANCE) {
            vec3 normal = getNormal(position);
            vec3 light = normalize(vec3(-1.0, 1.0, 1.0));
            fragColor.rgb = vec3(max(0.0, dot(normal, light))); // Diffuse shading
            break;
        }

        // Has the ray gone too far?
        if (distanceTraveled > MAX_TRACE_DISTANCE) {
            break;
        }

        // Move forward maximum distance possible without missing anything:
        distanceTraveled += sceneDistance;
    }
}

